import React, { useState, useEffect, useRef } from "react";
import "./app.css";

// Import local images


function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLogin, setShowLogin] = useState(true);
  const [showRegister, setShowRegister] = useState(false);
  const [profile, setProfile] = useState(null);
  const [activeTab, setActiveTab] = useState("chat");
  const [journalEntries, setJournalEntries] = useState([]);
  const [therapists, setTherapists] = useState([]);
const [resources, setResources] = useState([]);
  const [goals, setGoals] = useState([]);
  const [loading, setLoading] = useState({
    journal: false,
    therapists: false,
    resources: false
  });

  const chatEndRef = useRef(null);
  const audioRef = useRef(null);

  // Load saved session
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    if (token && userData) {
      setUser(JSON.parse(userData));
      setIsLoggedIn(true);
      setShowLogin(false);
      loadInitialData();
    }
  }, []);

  // Auto scroll
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, isTyping]);

  const loadInitialData = async () => {
    setMessages([{
      text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
      sender: "bot"
    }]);
    
    // Load journal entries if user is logged in
    if (isLoggedIn) {
      await loadJournalEntries();
    }
  };

  // Add this function to load journal entries from the backend
  const loadJournalEntries = async () => {
    setLoading(prev => ({...prev, journal: true}));
    try {
      const token = localStorage.getItem('token');
      const response = await fetch("/api/journal", {
        method: "GET",
        headers: {
          "Authorization": `Bearer ${token}`
        }
      });
      
      const data = await response.json();
      if (data.success) {
        setJournalEntries(data.entries || []);
      }
    } catch (error) {
      console.error("Error loading journal entries:", error);
    } finally {
      setLoading(prev => ({...prev, journal: false}));
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const credentials = {
      email: formData.get('email'),
      password: formData.get('password')
    };

    try {
      const response = await fetch("/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(credentials)
      });

      const data = await response.json();
      
      if (data.success) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        setUser(data.user);
        setIsLoggedIn(true);
        setShowLogin(false);
        loadInitialData();
      } else {
        alert(data.error || "Login failed");
      }
    } catch (error) {
      console.error("Login error:", error);
      alert("Login failed. Please check if the server is running.");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const userData = {
      email: formData.get('email'),
      password: formData.get('password'),
      name: formData.get('name')
    };

    try {
      const response = await fetch("/api/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(userData)
      });

      const data = await response.json();
      
      if (data.success) {
        alert("Registration successful! Please login.");
        setShowRegister(false);
        setShowLogin(true);
        e.target.reset();
      } else {
        alert(data.error || "Registration failed. Please try again.");
      }
    } catch (error) {
      console.error("Registration error:", error);
      alert("Registration failed. Please check if the server is running.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setIsLoggedIn(false);
    setShowLogin(true);
    setMessages([]);
    setProfile(null);
  };

  const sendMessage = async () => {
    if (!input.trim() || !isLoggedIn) return;

    const userMsg = { text: input, sender: "user" };
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);

    try {
      const token = localStorage.getItem('token');
      const response = await fetch("/api/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({ message: input })
      });

      const data = await response.json();
      
      if (data.success) {
        const botMsg = { 
          text: data.response, 
          sender: "bot",
          mood: data.mood,
          moodEmoji: data.moodEmoji,
          phrase: data.phrase
        };
        setMessages(prev => [...prev, botMsg]);

        // Play audio if available
        if (data.audioUrl) {
          const audio = new Audio(`http://localhost:5000${data.audioUrl}`);
          audio.play().catch(e => console.log("Audio play error:", e));
        }
      } else {
        const botMsg = { 
          text: data.error || "Sorry, I'm having trouble responding.", 
          sender: "bot" 
        };
        setMessages(prev => [...prev, botMsg]);
      }
    } catch (error) {
      console.error("Chat error:", error);
      const botMsg = { 
        text: "Connection error. Please check if the server is running.", 
        sender: "bot" 
      };
      setMessages(prev => [...prev, botMsg]);
    }

    setIsTyping(false);
  };

  const addJournalEntry = async () => {
    const entryText = prompt("What's on your mind today?");
    if (!entryText) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch("/api/journal", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({ text: entryText })
      });

      const data = await response.json();
      if (data.success) {
        alert("Journal entry saved! +3 coins earned!");
        setUser(prev => ({ ...prev, coins: data.totalCoins }));
        // Reload journal entries to show the new one
        await loadJournalEntries();
      } else {
        alert(data.error || "Failed to save journal entry");
      }
    } catch (error) {
      console.error("Journal error:", error);
      alert("Failed to save journal entry. Please try again.");
    }
  };

  const loadTherapists = async () => {
    setLoading(prev => ({...prev, therapists: true}));
    try {
      const response = await fetch("/api/therapists");
      const data = await response.json();
      setTherapists(data.therapists || []);
    } catch (error) {
      console.error("Error loading therapists:", error);
    } finally {
      setLoading(prev => ({...prev, therapists: false}));
    }
  };

  const loadResources = async () => {
    setLoading(prev => ({...prev, resources: true}));
    try {
      const response = await fetch("/api/resources");
      const data = await response.json();
      setResources(data.resources || []);
    } catch (error) {
      console.error("Error loading resources:", error);
    } finally {
      setLoading(prev => ({...prev, resources: false}));
    }
  };

  const bookTherapist = async (therapistId) => {
    if (!window.confirm("Book this therapist? This will cost coins.")) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch("/api/therapists/book", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({ therapist_id: therapistId, mode: "chat" })
      });

      const data = await response.json();
      if (data.success) {
        alert(`Booking confirmed! ${data.message}`);
        setUser(prev => ({ ...prev, coins: data.remainingCoins }));
      } else {
        alert(data.error || "Booking failed");
      }
    } catch (error) {
      console.error("Booking error:", error);
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="app-container auth-container">
        <div className="auth-box">
          <h1>🌙 MoodMate</h1>
          <p>Your mental health companion</p>
          
          {showLogin && (
            <form onSubmit={handleLogin} className="auth-form">
              <h2>Login</h2>
              <input type="email" name="email" placeholder="Email" required />
              <input type="password" name="password" placeholder="Password" required />
              <button type="submit">Login</button>
              <p>
                Don't have an account?{" "}
                <span className="auth-link" onClick={() => setShowRegister(true)}>
                  Register
                </span>
              </p>
            </form>
          )}

          {showRegister && (
            <form onSubmit={handleRegister} className="auth-form">
              <h2>Register</h2>
              <input type="text" name="name" placeholder="Full Name" required />
              <input type="email" name="email" placeholder="Email" required />
              <input type="password" name="password" placeholder="Password (min 6 chars)" required />
              <button type="submit">Register</button>
              <p>
                Already have an account?{" "}
                <span className="auth-link" onClick={() => setShowRegister(false)}>
                  Login
                </span>
              </p>
            </form>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="app-container">
      {/* Header */}
      <header className="app-header">
        <div className="header-left">
          <h1>🌙 MoodMate</h1>
          <div className="user-stats">
            <span>🔥 {user?.streak || 0} day streak</span>
            <span>🪙 {user?.coins || 0} coins</span>
          </div>
        </div>
        <div className="header-right">
          <span>Welcome, {user?.name}</span>
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </div>
      </header>

      {/* Navigation */}
      <nav className="app-nav">
        <button 
          className={activeTab === "chat" ? "active" : ""} 
          onClick={() => setActiveTab("chat")}
        >
          💬 Chat
        </button>
        <button 
          className={activeTab === "journal" ? "active" : ""} 
          onClick={() => {
            setActiveTab("journal");
            loadJournalEntries();
          }}
        >
          📓 Journal
        </button>
        <button 
          className={activeTab === "therapy" ? "active" : ""} 
          onClick={() => {
            setActiveTab("therapy");
            loadTherapists();
            loadResources();
          }}
        >
          👨‍⚕ Therapy
        </button>
        <button 
          className={activeTab === "profile" ? "active" : ""} 
          onClick={() => setActiveTab("profile")}
        >
          👤 Profile
        </button>
      </nav>

      {/* Main Content */}
      <main className="app-main">
        {/* Chat Tab */}
        {activeTab === "chat" && (
          <div className="chat-tab">
            <div className="chat-box">
              {messages.map((msg, i) => (
                <div key={i} className={`chat-message ${msg.sender}`}>
                  {msg.sender === "bot" && (
                    <img
                      src="https://i.imgur.com/1XK5n.jpg"
                      alt="MoodMate"
                      className="avatar"
                      onError={(e) => {
                        e.target.src = 'https://via.placeholder.com/40x40/4A90E2/FFFFFF?text=MM';
                      }}
                    />
                  )}
                  {msg.sender === "user" && (
                    <img
                      src={`https://ui-avatars.com/api/?name=${user?.name || 'User'}&background=random`}
                      alt="You"
                      className="avatar"
                    />
                  )}
                  <div className="bubble">
                    {msg.text}
                    {msg.moodEmoji && (
                      <div className="mood-indicator">
                        {msg.moodEmoji} {msg.phrase}
                      </div>
                    )}
                  </div>
                </div>
              ))}

              {isTyping && (
                <div className="chat-message bot">
                  <img
                    src="https://i.imgur.com/1XK5n.jpg"
                    alt="MoodMate"
                    className="avatar"
                    onError={(e) => {
                      e.target.src = 'https://via.placeholder.com/40x40/4A90E2/FFFFFF?text=MM';
                    }}
                  />
                  <div className="bubble typing">...</div>
                </div>
              )}

              <div ref={chatEndRef}></div>
            </div>

            <div className="input-area">
              <input
                type="text"
                placeholder="How are you feeling today?"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
              />
              <button onClick={sendMessage}>Send</button>
            </div>
          </div>
        )}

        {/* Journal Tab */}
        {activeTab === "journal" && (
          <div className="journal-tab">
            <div className="journal-header">
              <h2>Your Journal</h2>
              <button onClick={addJournalEntry} className="add-entry-btn">
                + New Entry
              </button>
            </div>
            <div className="journal-entries">
              {loading.journal ? (
                <div className="loading">Loading journal entries...</div>
              ) : journalEntries.length === 0 ? (
                <div className="empty-state">
                  <p>No journal entries yet. Write your first entry!</p>
                  <button onClick={addJournalEntry}>Start Journaling</button>
                </div>
              ) : (
                journalEntries.map((entry, index) => (
                  <div key={index} className="journal-entry">
                    <div className="entry-date">{new Date(entry.date).toLocaleDateString()}</div>
                    <div className="entry-text">{entry.text}</div>
                    {entry.summary && (
                      <div className="entry-summary">
                        <strong>Summary:</strong> {entry.summary}
                      </div>
                    )}
                    {entry.mood && (
                      <div className="entry-mood">
                        <strong>Mood:</strong> {entry.mood}
                      </div>
                    )}
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {/* Therapy Tab */}
        {activeTab === "therapy" && (
          <div className="therapy-tab">
            <div className="resources-section">
              <h3>🆘 Crisis Resources</h3>
              <div className="resources-grid">
                {resources.filter(r => r.type === 'crisis').map((resource, index) => (
                  <div key={index} className="resource-card">
                    <h4>{resource.name}</h4>
                    <p>{resource.number}</p>
                    <small>{resource.available}</small>
                  </div>
                ))}
              </div>
            </div>

            <div className="therapists-section">
              <h3>👨‍⚕ Professional Therapists</h3>
              {loading.therapists ? (
                <div className="loading">Loading therapists...</div>
              ) : (
                <div className="therapists-grid">
                  {therapists.map((therapist) => (
                    <div key={therapist.id} className="therapist-card">
                      <div className="therapist-avatar">{therapist.avatar}</div>
                      <h4>{therapist.name}</h4>
                      <p>{therapist.specialization}</p>
                      <p>⭐ {therapist.rating} • {therapist.experience}</p>
                      <p>🪙 {therapist.price} coins/session</p>
                      <button 
                        onClick={() => bookTherapist(therapist.id)}
                        className="book-btn"
                        disabled={user?.coins < therapist.price}
                      >
                        {user?.coins >= therapist.price ? "Book Session" : "Not Enough Coins"}
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}

        {/* Profile Tab */}
        {activeTab === "profile" && user && (
          <div className="profile-tab">
            <div className="profile-card">
              <h2>Your Profile</h2>
              <div className="profile-info">
                <p><strong>Name:</strong> {user.name}</p>
                <p><strong>Email:</strong> {user.email}</p>
                <p><strong>Coins:</strong> 🪙 {user.coins}</p>
                <p><strong>Streak:</strong> 🔥 {user.streak} days</p>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Audio element for playing responses */}
      <audio ref={audioRef} style={{ display: 'none' }} />
    </div>
  );
}

export default App;
