// App.jsx - Complete Fixed Version
import React, { useState, useEffect, useRef } from "react";
import "./app.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLogin, setShowLogin] = useState(true);
  const [showRegister, setShowRegister] = useState(false);
  const [activeTab, setActiveTab] = useState("chat");
  const [journalEntries, setJournalEntries] = useState([]);
  const [therapists, setTherapists] = useState([]);
  const [resources, setResources] = useState([]);
  const [moodWall, setMoodWall] = useState([]);
  const [moodTribes, setMoodTribes] = useState([]);
  const [shopItems, setShopItems] = useState([]);
  const [currentMood, setCurrentMood] = useState("neutral");
  const [typingIndicator, setTypingIndicator] = useState("");

  const chatEndRef = useRef(null);
  const audioRef = useRef(null);

  // Mood-based theme colors
  const moodThemes = {
    happy: { primary: "#FFD700", secondary: "#FF6B6B", bg: "linear-gradient(135deg, #FFD700, #FF6B6B)" },
    sad: { primary: "#5DADE2", secondary: "#3498DB", bg: "linear-gradient(135deg, #5DADE2, #3498DB)" },
    angry: { primary: "#E74C3C", secondary: "#C0392B", bg: "linear-gradient(135deg, #E74C3C, #C0392B)" },
    anxious: { primary: "#F39C12", secondary: "#D35400", bg: "linear-gradient(135deg, #F39C12, #D35400)" },
    calm: { primary: "#48C9B0", secondary: "#1ABC9C", bg: "linear-gradient(135deg, #48C9B0, #1ABC9C)" },
    neutral: { primary: "#9B59B6", secondary: "#8E44AD", bg: "linear-gradient(135deg, #9B59B6, #8E44AD)" }
  };

  // Load saved session
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    if (token && userData) {
      setUser(JSON.parse(userData));
      setIsLoggedIn(true);
      setShowLogin(false);
      loadInitialData();
    }
  }, []);

  // Auto scroll and apply mood theme
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
    applyMoodTheme();
  }, [messages, currentMood]);

  // Apply mood-based theme
  const applyMoodTheme = () => {
    document.documentElement.style.setProperty('--primary-color', moodThemes[currentMood].primary);
    document.documentElement.style.setProperty('--secondary-color', moodThemes[currentMood].secondary);
    document.documentElement.style.setProperty('--bg-gradient', moodThemes[currentMood].bg);
  };

  const loadInitialData = async () => {
    setMessages([{
      text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
      sender: "bot"
    }]);
    
    // Load other data if user is logged in
    if (isLoggedIn) {
      await loadJournalEntries();
      await loadMoodWall();
      await loadMoodTribes();
      await loadShopItems();
    }
  };

  const loadJournalEntries = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch("/api/journal", {
        method: "GET",
        headers: {
          "Authorization": `Bearer ${token}`
        }
      });
      
      const data = await response.json();
      if (data.success) {
        setJournalEntries(data.entries || []);
      }
    } catch (error) {
      console.error("Error loading journal entries:", error);
    }
  };

  const loadMoodWall = async () => {
    try {
      const response = await fetch("/api/moodwall");
      const data = await response.json();
      if (data.success) {
        setMoodWall(data.posts || []);
      }
    } catch (error) {
      console.error("Error loading mood wall:", error);
    }
  };

  const loadMoodTribes = async () => {
    try {
      const response = await fetch("/api/moodtribes");
      const data = await response.json();
      if (data.success) {
        setMoodTribes(data.tribes || []);
      }
    } catch (error) {
      console.error("Error loading mood tribes:", error);
    }
  };

  const loadShopItems = async () => {
    try {
      const response = await fetch("/api/shop");
      const data = await response.json();
      if (data.success) {
        setShopItems(data.items || []);
      }
    } catch (error) {
      console.error("Error loading shop items:", error);
    }
  };

  // Simulate typing animation
  const simulateTyping = async () => {
    const typingPhrases = ["Thinking", "Thinking.", "Thinking..", "Thinking..."];
    for (let i = 0; i < 8; i++) {
      setTypingIndicator(typingPhrases[i % 4]);
      await new Promise(resolve => setTimeout(resolve, 300));
    }
    setTypingIndicator("");
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const credentials = {
      email: formData.get('email'),
      password: formData.get('password')
    };

    try {
      const response = await fetch("/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(credentials)
      });

      const data = await response.json();
      
      if (data.success) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        setUser(data.user);
        setIsLoggedIn(true);
        setShowLogin(false);
        loadInitialData();
      } else {
        alert(data.error || "Login failed");
      }
    } catch (error) {
      console.error("Login error:", error);
      alert("Login failed. Please check if the server is running.");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const userData = {
      email: formData.get('email'),
      password: formData.get('password'),
      name: formData.get('name')
    };

    try {
      const response = await fetch("/api/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(userData)
      });

      const data = await response.json();
      
      if (data.success) {
        alert("Registration successful! Please login.");
        setShowRegister(false);
        setShowLogin(true);
        e.target.reset();
      } else {
        alert(data.error || "Registration failed. Please try again.");
      }
    } catch (error) {
      console.error("Registration error:", error);
      alert("Registration failed. Please check if the server is running.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setIsLoggedIn(false);
    setShowLogin(true);
    setMessages([]);
  };

  const sendMessage = async () => {
    if (!input.trim() || !isLoggedIn) return;

    const userMsg = { text: input, sender: "user" };
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);
    
    // Simulate typing animation
    simulateTyping();

    try {
      const token = localStorage.getItem('token');
      const response = await fetch("/api/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({ message: input })
      });

      const data = await response.json();
      
      if (data.success) {
        const botMsg = { 
          text: data.response, 
          sender: "bot",
          mood: data.mood,
          moodEmoji: data.moodEmoji,
          phrase: data.phrase
        };
        setMessages(prev => [...prev, botMsg]);
        setCurrentMood(data.mood || "neutral");

        // Play audio if available
        if (data.audioUrl) {
          const audio = new Audio(`http://localhost:5000${data.audioUrl}`);
          audio.play().catch(e => console.log("Audio play error:", e));
        }
        
        // Random surprise reward (10% chance)
        if (Math.random() < 0.1) {
          const surpriseCoins = Math.floor(Math.random() * 10) + 5;
          setUser(prev => ({ ...prev, coins: prev.coins + surpriseCoins }));
          setMessages(prev => [...prev, {
            text: `🎉 Surprise! You've earned ${surpriseCoins} bonus coins for engaging with your mental health!`,
            sender: "bot",
            isReward: true
          }]);
        }
      } else {
        // Fallback responses if API fails
        const fallbackResponses = [
          "I hear you. Would you like to talk more about that?",
          "Thank you for sharing. How does that make you feel?",
          "I'm here to listen. Tell me more about what's on your mind.",
          "That sounds challenging. How can I support you right now?",
          "Your feelings are completely valid. Would you like to explore this further?"
        ];
        
        const randomResponse = fallbackResponses[Math.floor(Math.random() * fallbackResponses.length)];
        const botMsg = { 
          text: randomResponse, 
          sender: "bot"
        };
        setMessages(prev => [...prev, botMsg]);
      }
    } catch (error) {
      console.error("Chat error:", error);
      const botMsg = { 
        text: "I'm having trouble connecting right now. Please check your internet connection.", 
        sender: "bot" 
      };
      setMessages(prev => [...prev, botMsg]);
    }

    setIsTyping(false);
    setTypingIndicator("");
  };

  const addJournalEntry = async () => {
    const entryText = prompt("What's on your mind today?");
    if (!entryText) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch("/api/journal", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({ text: entryText })
      });

      const data = await response.json();
      if (data.success) {
        alert("Journal entry saved! +3 coins earned!");
        setUser(prev => ({ ...prev, coins: data.totalCoins }));
        await loadJournalEntries();
      } else {
        alert(data.error || "Failed to save journal entry");
      }
    } catch (error) {
      console.error("Journal error:", error);
      alert("Failed to save journal entry. Please try again.");
    }
  };

  const addMoodWallPost = async () => {
    const postText = prompt("Share your feelings with the community (anonymous):");
    if (!postText) return;

    try {
      const response = await fetch("/api/moodwall", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({ text: postText })
      });

      const data = await response.json();
      if (data.success) {
        alert("Posted to mood wall!");
        await loadMoodWall();
      }
    } catch (error) {
      console.error("Mood wall error:", error);
    }
  };

  const playBreathingExercise = () => {
    setMessages(prev => [...prev, {
      text: "Let's do a quick breathing exercise. Breathe in for 4 seconds, hold for 4 seconds, exhale for 6 seconds. Repeat 5 times.",
      sender: "bot",
      isExercise: true
    }]);
  };

  const buyItem = (itemId, price) => {
    if (user.coins < price) {
      alert("Not enough coins!");
      return;
    }
    
    if (window.confirm(`Buy this item for ${price} coins?`)) {
      setUser(prev => ({ ...prev, coins: prev.coins - price }));
      alert("Item purchased!");
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="app-container auth-container">
        <div className="auth-box glass">
          <h1>🌙 MoodMate</h1>
          <p>Your mental health companion</p>
          
          {showLogin && (
            <form onSubmit={handleLogin} className="auth-form">
              <h2>Login</h2>
              <input type="email" name="email" placeholder="Email" required />
              <input type="password" name="password" placeholder="Password" required />
              <button type="submit" className="glow-btn">Login</button>
              <p>
                Don't have an account?{" "}
                <span className="auth-link" onClick={() => setShowRegister(true)}>
                  Register
                </span>
              </p>
            </form>
          )}

          {showRegister && (
            <form onSubmit={handleRegister} className="auth-form">
              <h2>Register</h2>
              <input type="text" name="name" placeholder="Full Name" required />
              <input type="email" name="email" placeholder="Email" required />
              <input type="password" name="password" placeholder="Password (min 6 chars)" required />
              <button type="submit" className="glow-btn">Register</button>
              <p>
                Already have an account?{" "}
                <span className="auth-link" onClick={() => setShowRegister(false)}>
                  Login
                </span>
              </p>
            </form>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="app-container">
      <header className="app-header glass">
        <div className="header-left">
          <h1>🌙 MoodMate</h1>
          <div className="user-stats">
            <span>🔥 {user?.streak || 0} day streak</span>
            <span>🪙 {user?.coins || 0} coins</span>
          </div>
        </div>
        <div className="header-right">
          <span>Welcome, {user?.name}</span>
          <button onClick={handleLogout} className="logout-btn bounce">Logout</button>
        </div>
      </header>

      <nav className="app-nav glass">
        <button 
          className={activeTab === "chat" ? "active" : ""} 
          onClick={() => setActiveTab("chat")}
        >
          💬 Chat
        </button>
        <button 
          className={activeTab === "journal" ? "active" : ""} 
          onClick={() => setActiveTab("journal")}
        >
          📓 Journal
        </button>
        <button 
          className={activeTab === "therapy" ? "active" : ""} 
          onClick={() => setActiveTab("therapy")}
        >
          👨‍⚕ Therapy
        </button>
        <button 
          className={activeTab === "community" ? "active" : ""} 
          onClick={() => {
            setActiveTab("community");
            loadMoodWall();
            loadMoodTribes();
          }}
        >
          🌍 Community
        </button>
        <button 
          className={activeTab === "shop" ? "active" : ""} 
          onClick={() => {
            setActiveTab("shop");
            loadShopItems();
          }}
        >
          🎮 Rewards
        </button>
      </nav>

      <main className="app-main">
        {activeTab === "chat" && (
          <div className="chat-tab">
            <div className="chat-box">
              {messages.map((msg, i) => (
                <div key={i} className={`chat-message ${msg.sender} ${msg.isReward ? 'reward' : ''} ${msg.isExercise ? 'exercise' : ''}`}>
                  {msg.sender === "bot" && (
                    <img
                      src="https://i.imgur.com/1XK5n.jpg"
                      alt="MoodMate"
                      className="avatar"
                      onError={(e) => {
                        e.target.src = 'https://via.placeholder.com/40x40/4A90E2/FFFFFF?text=MM';
                      }}
                    />
                  )}
                  {msg.sender === "user" && (
                    <img
                      src={`https://ui-avatars.com/api/?name=${user?.name || 'User'}&background=random`}
                      alt="You"
                      className="avatar"
                    />
                  )}
                  <div className="bubble">
                    {msg.text}
                    {msg.moodEmoji && (
                      <div className="mood-indicator">
                        {msg.moodEmoji} {msg.phrase}
                      </div>
                    )}
                  </div>
                </div>
              ))}

              {isTyping && (
                <div className="chat-message bot">
                  <img
                    src="https://i.imgur.com/1XK5n.jpg"
                    alt="MoodMate"
                    className="avatar"
                    onError={(e) => {
                      e.target.src = 'https://via.placeholder.com/40x40/4A90E2/FFFFFF?text=MM';
                    }}
                  />
                  <div className="bubble typing">
                    {typingIndicator || "..."}
                  </div>
                </div>
              )}

              <div ref={chatEndRef}></div>
            </div>

            <div className="input-area">
              <input
                type="text"
                placeholder="How are you feeling today?"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
              />
              <button onClick={sendMessage} className="glow-btn">Send</button>
              <button onClick={playBreathingExercise} className="exercise-btn" title="Breathing Exercise">🧘</button>
            </div>
          </div>
        )}

        {activeTab === "community" && (
          <div className="community-tab">
            <div className="mood-wall-section">
              <h3>🌍 Community Mood Wall</h3>
              <button onClick={addMoodWallPost} className="glow-btn">+ Share Your Feelings</button>
              <div className="mood-wall">
                {moodWall.map((post, index) => (
                  <div key={index} className="mood-post glass">
                    <p>{post.text}</p>
                    <div className="post-reactions">
                      <button>💖</button>
                      <button>🤗</button>
                      <button>👍</button>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            <div className="tribes-section">
              <h3>🎯 Mood Tribes</h3>
              <div className="tribes-grid">
                {moodTribes.map((tribe, index) => (
                  <div key={index} className="tribe-card glass">
                    <h4>{tribe.name}</h4>
                    <p>{tribe.description}</p>
                    <button className="glow-btn">Join Tribe</button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {activeTab === "shop" && (
          <div className="shop-tab">
            <h3>🎮 Reward Shop</h3>
            <div className="shop-items">
              {shopItems.map((item, index) => (
                <div key={index} className="shop-item glass">
                  <h4>{item.name}</h4>
                  <p>{item.description}</p>
                  <p className="price">🪙 {item.price} coins</p>
                  <button 
                    onClick={() => buyItem(item.id, item.price)}
                    className={user.coins >= item.price ? "glow-btn" : "disabled-btn"}
                  >
                    {user.coins >= item.price ? "Purchase" : "Not Enough Coins"}
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
