/**
 * MoodMate - Complete React Frontend
 * ==================================
 * * A comprehensive mental health companion app that integrates with the MoodMate Flask backend.
 * Features: AI chat, mood tracking, community wall, daily challenges, gamification, 
 * wellness exercises, analytics, and more.
 */

const { useState, useEffect, useRef, useCallback, useMemo, createContext, useContext } = React;

// ================================================================
// GLOBAL STATE MANAGEMENT
// ================================================================

const AppContext = createContext();

const useAppContext = () => {
    const context = useContext(AppContext);
    if (!context) {
        throw new Error('useAppContext must be used within AppProvider');
    }
    return context;
};

// ================================================================
// CUSTOM HOOKS
// ================================================================

const useAPI = () => {
    const makeRequest = useCallback(async (endpoint, options = {}) => {
        try {
            const response = await fetch(`/api${endpoint}`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include', // Important for session-based auth
                ...options
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || `HTTP ${response.status}`);
            }

            return data;
        } catch (error) {
            console.error('API Error:', error);
            throw error;
        }
    }, []);

    return { makeRequest };
};

const useLocalStorage = (key, defaultValue) => {
    const [value, setValue] = useState(() => {
        try {
            const item = window.localStorage.getItem(key);
            return item ? JSON.parse(item) : defaultValue;
        } catch (error) {
            return defaultValue;
        }
    });

    const setStoredValue = useCallback((newValue) => {
        try {
            setValue(newValue);
            window.localStorage.setItem(key, JSON.stringify(newValue));
        } catch (error) {
            console.error('LocalStorage error:', error);
        }
    }, [key]);

    return [value, setStoredValue];
};

const useTypingEffect = (text, speed = 30) => {
    const [displayedText, setDisplayedText] = useState('');
    const [isTyping, setIsTyping] = useState(false);

    useEffect(() => {
        if (!text) {
            setDisplayedText('');
            return;
        }

        setIsTyping(true);
        setDisplayedText('');

        let index = 0;
        const timer = setInterval(() => {
            if (index < text.length) {
                setDisplayedText(text.slice(0, index + 1));
                index++;
            } else {
                setIsTyping(false);
                clearInterval(timer);
            }
        }, speed);

        return () => {
            clearInterval(timer);
            setIsTyping(false);
        };
    }, [text, speed]);

    return { displayedText, isTyping };
};

const useAutoScroll = (dependency) => {
    const scrollRef = useRef(null);

    useEffect(() => {
        if (scrollRef.current) {
            const element = scrollRef.current;
            element.scrollTop = element.scrollHeight;
        }
    }, [dependency]);

    return scrollRef;
};

// ================================================================
// UTILITY FUNCTIONS
// ================================================================

const getMoodEmoji = (mood) => {
    const emojiMap = {
        happy: '😊', sad: '😢', angry: '😠', anxious: '😰',
        excited: '🤩', calm: '😌', confused: '😕', motivated: '💪',
        lonely: '😔', grateful: '🙏', neutral: '😐'
    };
    return emojiMap[mood] || '😐';
};

const formatTimeAgo = (timestamp) => {
    const now = new Date();
    const past = new Date(timestamp);
    const diff = Math.floor((now - past) / 1000);

    if (diff < 60) return 'Just now';
    if (diff < 3600) return `${Math.floor(diff / 60)}m ago`;
    if (diff < 86400) return `${Math.floor(diff / 3600)}h ago`;
    return `${Math.floor(diff / 86400)}d ago`;
};

const getStreakEmoji = (streak) => {
    if (streak >= 365) return '👑';
    if (streak >= 100) return '🔥';
    if (streak >= 30) return '⭐';
    if (streak >= 7) return '🌟';
    return '✨';
};

const getBadgeDetails = (badgeKey) => {
    const badges = {
        first_chat: { name: 'First Hello', emoji: '👋', description: 'Started first conversation' },
        streak_7: { name: 'Week Warrior', emoji: '🔥', description: '7 day streak' },
        streak_30: { name: 'Month Master', emoji: '🏆', description: '30 day streak' },
        chatterbox: { name: 'Chatterbox', emoji: '💬', description: '100 messages sent' },
        mood_tracker: { name: 'Mood Master', emoji: '😌', description: '50 mood logs' }
    };
    return badges[badgeKey] || { name: badgeKey, emoji: '🏅', description: 'Achievement unlocked!' };
};

// ================================================================
// NOTIFICATION COMPONENT
// ================================================================

const Notification = ({ notification, onClose }) => {
    useEffect(() => {
        const timer = setTimeout(() => {
            onClose(notification.id);
        }, 5000);

        return () => clearTimeout(timer);
    }, [notification.id, onClose]);

    return (
        <div className={`notification ${notification.type} animate-slide-in`} onClick={() => onClose(notification.id)}>
            <div className="notification-content">
                <span className="notification-emoji">{notification.emoji}</span>
                <div>
                    <div className="notification-title">{notification.title}</div>
                    <div className="notification-message">{notification.message}</div>
                </div>
            </div>
            <button className="notification-close">×</button>
        </div>
    );
};

// ================================================================
// AUTHENTICATION COMPONENTS
// ================================================================

const LoginForm = ({ onLogin, onSwitchToRegister }) => {
    const [formData, setFormData] = useState({ username: '', password: '' });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const { makeRequest } = useAPI();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const response = await makeRequest('/login', {
                method: 'POST',
                body: JSON.stringify(formData)
            });

            onLogin(response.user);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="auth-form animate-fade-in">
            <div className="auth-header">
                <h2>Welcome Back to MoodMate! 💜</h2>
                <p>Your AI companion is waiting for you</p>
            </div>

            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Username</label>
                    <input
                        type="text"
                        value={formData.username}
                        onChange={(e) => setFormData({...formData, username: e.target.value})}
                        placeholder="Enter your username"
                        required
                        disabled={loading}
                    />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input
                        type="password"
                        value={formData.password}
                        onChange={(e) => setFormData({...formData, password: e.target.value})}
                        placeholder="Enter your password"
                        required
                        disabled={loading}
                    />
                </div>

                {error && <div className="error-message">{error}</div>}

                <button type="submit" className="btn btn-primary" disabled={loading}>
                    {loading ? 'Signing in...' : 'Sign In'}
                </button>

                <div className="auth-switch">
                    <p>New to MoodMate? 
                        <button type="button" onClick={onSwitchToRegister} className="link-button">
                            Create Account
                        </button>
                    </p>
                </div>
            </form>
        </div>
    );
};

const RegisterForm = ({ onRegister, onSwitchToLogin }) => {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        confirmPassword: ''
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const { makeRequest } = useAPI();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        if (formData.password !== formData.confirmPassword) {
            setError('Passwords do not match');
            setLoading(false);
            return;
        }

        try {
            const response = await makeRequest('/register', {
                method: 'POST',
                body: JSON.stringify({
                    username: formData.username,
                    email: formData.email,
                    password: formData.password
                })
            });

            onRegister(response.user);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="auth-form animate-fade-in">
            <div className="auth-header">
                <h2>Join MoodMate! 🌟</h2>
                <p>Start your mental wellness journey today</p>
            </div>

            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Username</label>
                    <input
                        type="text"
                        value={formData.username}
                        onChange={(e) => setFormData({...formData, username: e.target.value})}
                        placeholder="Choose a username"
                        required
                        disabled={loading}
                    />
                </div>

                <div className="form-group">
                    <label>Email</label>
                    <input
                        type="email"
                        value={formData.email}
                        onChange={(e) => setFormData({...formData, email: e.target.value})}
                        placeholder="your.email@example.com"
                        required
                        disabled={loading}
                    />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input
                        type="password"
                        value={formData.password}
                        onChange={(e) => setFormData({...formData, password: e.target.value})}
                        placeholder="Create a secure password"
                        required
                        disabled={loading}
                    />
                </div>

                <div className="form-group">
                    <label>Confirm Password</label>
                    <input
                        type="password"
                        value={formData.confirmPassword}
                        onChange={(e) => setFormData({...formData, confirmPassword: e.target.value})}
                        placeholder="Confirm your password"
                        required
                        disabled={loading}
                    />
                </div>

                {error && <div className="error-message">{error}</div>}

                <button type="submit" className="btn btn-primary" disabled={loading}>
                    {loading ? 'Creating Account...' : 'Create Account'}
                </button>

                <div className="auth-switch">
                    <p>Already have an account? 
                        <button type="button" onClick={onSwitchToLogin} className="link-button">
                            Sign In
                        </button>
                    </p>
                </div>
            </form>
        </div>
    );
};

// ================================================================
// CHAT COMPONENTS
// ================================================================

const ChatMessage = ({ message, isUser, onSuggestionClick }) => {
    const { displayedText, isTyping } = useTypingEffect(
        !isUser && message.ai_response ? message.ai_response : '', 
        30
    );

    const content = isUser 
        ? message.user_message || message.message
        : displayedText || message.response || message.ai_response;

    return (
        <div className={`chat-message ${isUser ? 'user' : 'assistant'} animate-slide-up`}>
            <div className="message-avatar">
                {isUser ? (
                    <div className="user-avatar">👤</div>
                ) : (
                    <div className="ai-avatar">🤖</div>
                )}
            </div>

            <div className="message-content">
                <div className="message-bubble">
                    <div className="message-text">
                        {content}
                        {!isUser && isTyping && <span className="typing-cursor">|</span>}
                    </div>

                    {!isUser && message.mood && (
                        <div className="mood-indicator">
                            <span className="mood-emoji">{getMoodEmoji(message.mood)}</span>
                            <span className="mood-text">Detected: {message.mood}</span>
                        </div>
                    )}

                    {!isUser && message.points_earned && (
                        <div className="points-earned">
                            +{message.points_earned} points! ✨
                        </div>
                    )}

                    <div className="message-time">
                        {formatTimeAgo(message.timestamp)}
                    </div>
                </div>

                {!isUser && message.suggestion && (
                    <div className="suggestion-card" onClick={() => onSuggestionClick(message.suggestion)}>
                        <div className="suggestion-header">💡 Try this:</div>
                        <div className="suggestion-text">{message.suggestion}</div>
                    </div>
                )}

                {!isUser && message.music_recommendation && (
                    <div className="music-recommendation">
                        <div className="music-header">🎵 Music for you:</div>
                        <div className="music-text">{message.music_recommendation}</div>
                    </div>
                )}

                {!isUser && message.new_badges && message.new_badges.length > 0 && (
                    <div className="badge-notification">
                        {message.new_badges.map((badge, index) => {
                            const badgeInfo = getBadgeDetails(badge);
                            return (
                                <div key={index} className="new-badge">
                                    <span className="badge-emoji">{badgeInfo.emoji}</span>
                                    <span className="badge-name">New Badge: {badgeInfo.name}!</span>
                                </div>
                            );
                        })}
                    </div>
                )}
            </div>
        </div>
    );
};

const ChatInput = ({ onSendMessage, loading }) => {
    const [message, setMessage] = useState('');
    const textareaRef = useRef(null);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (message.trim() && !loading) {
            onSendMessage(message.trim());
            setMessage('');
            if (textareaRef.current) {
                textareaRef.current.style.height = 'auto';
            }
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSubmit(e);
        }
    };

    const adjustHeight = (e) => {
        const textarea = e.target;
        textarea.style.height = 'auto';
        textarea.style.height = Math.min(textarea.scrollHeight, 120) + 'px';
    };

    return (
        <div className="chat-input-container">
            <form onSubmit={handleSubmit} className="chat-input-form">
                <div className="input-wrapper">
                    <textarea
                        ref={textareaRef}
                        value={message}
                        onChange={(e) => {
                            setMessage(e.target.value);
                            adjustHeight(e);
                        }}
                        onKeyPress={handleKeyPress}
                        placeholder="Share what's on your mind..."
                        className="chat-textarea"
                        disabled={loading}
                        rows={1}
                        maxLength={2000}
                    />

                    <button
                        type="submit"
                        className="send-button"
                        disabled={!message.trim() || loading}
                    >
                        {loading ? '...' : '➤'}
                    </button>
                </div>

                <div className="input-hints">
                    <span>Press Enter to send, Shift+Enter for new line</span>
                    <span className="char-counter">{message.length}/2000</span>
                </div>
            </form>
        </div>
    );
};

const ChatInterface = ({ user, onUpdateUser, onShowNotification }) => {
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(false);
    const [initialLoading, setInitialLoading] = useState(true);
    const { makeRequest } = useAPI();
    const messagesEndRef = useAutoScroll(messages);

    // Load chat history on component mount
    useEffect(() => {
        loadChatHistory();
    }, []);

    const loadChatHistory = async () => {
        try {
            const response = await makeRequest('/chat/history?limit=50');
            setMessages(response.history || []);
        } catch (error) {
            console.error('Failed to load chat history:', error);
        } finally {
            setInitialLoading(false);
        }
    };

    const handleSendMessage = async (messageText) => {
        if (!messageText.trim()) return;

        // Add user message immediately
        const userMessage = {
            id: Date.now(),
            user_message: messageText,
            timestamp: new Date().toISOString()
        };

        setMessages(prev => [...prev, userMessage]);
        setLoading(true);

        try {
            const response = await makeRequest('/chat', {
                method: 'POST',
                body: JSON.stringify({ message: messageText })
            });

            // Add AI response
            const aiMessage = {
                id: Date.now() + 1,
                response: response.response,
                mood: response.mood,
                sentiment: response.sentiment,
                points_earned: response.points_earned,
                suggestion: response.suggestion,
                music_recommendation: response.music_recommendation,
                new_badges: response.new_badges,
                timestamp: new Date().toISOString()
            };

            setMessages(prev => [...prev, aiMessage]);

            // Update user stats
            if (response.points_earned > 0) {
                onUpdateUser({
                    ...user,
                    mood_points: (user.mood_points || 0) + response.points_earned
                });
            }

            // Show notifications for new badges
            if (response.new_badges && response.new_badges.length > 0) {
                response.new_badges.forEach(badge => {
                    const badgeInfo = getBadgeDetails(badge);
                    onShowNotification({
                        type: 'success',
                        title: 'New Badge Earned!',
                        message: badgeInfo.name,
                        emoji: badgeInfo.emoji
                    });
                });
            }

        } catch (error) {
            console.error('Chat error:', error);
            const errorMessage = {
                id: Date.now() + 2,
                response: "I'm having trouble responding right now. Please try again in a moment.",
                timestamp: new Date().toISOString(),
                is_error: true
            };
            setMessages(prev => [...prev, errorMessage]);
        } finally {
            setLoading(false);
        }
    };

    const handleSuggestionClick = (suggestion) => {
        onShowNotification({
            type: 'info',
            title: 'Great Suggestion!',
            message: 'Try incorporating this into your day',
            emoji: '💡'
        });
    };

    if (initialLoading) {
        return (
            <div className="loading-container">
                <div className="spinner"></div>
                <p>Loading your conversation...</p>
            </div>
        );
    }

    return (
        <div className="chat-interface">
            <div className="chat-header">
                <div className="chat-title">
                    <h2>💬 Chat with MoodMate</h2>
                    <p>Your AI mental health companion</p>
                </div>

                <div className="user-stats-mini">
                    <div className="stat-item">
                        <span className="stat-emoji">🪙</span>
                        <span className="stat-value">{user.mood_points || 0}</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-emoji">{getStreakEmoji(user.current_streak || 0)}</span>
                        <span className="stat-value">{user.current_streak || 0}</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-emoji">⭐</span>
                        <span className="stat-value">Lv.{user.level || 1}</span>
                    </div>
                </div>
            </div>

            <div className="chat-messages" ref={messagesEndRef}>
                {messages.length === 0 ? (
                    <div className="welcome-message animate-fade-in">
                        <div className="welcome-avatar">🤖</div>
                        <div className="welcome-content">
                            <h3>Namaste {user.username}! 🙏</h3>
                            <p>Main MoodMate hun, tumhara AI companion! I'm here to listen, support, and chat with you in natural Hinglish.</p>
                            <p>How are you feeling today? Kya chal raha hai aaj? Share anything on your mind! 💜</p>
                        </div>
                    </div>
                ) : (
                    messages.map((message, index) => (
                        <ChatMessage
                            key={message.id || index}
                            message={message}
                            isUser={!!(message.user_message || message.message)}
                            onSuggestionClick={handleSuggestionClick}
                        />
                    ))
                )}

                {loading && (
                    <div className="ai-typing animate-fade-in">
                        <div className="typing-indicator">
                            <div className="typing-dots">
                                <span></span>
                                <span></span>
                                <span></span>
                            </div>
                            <span>MoodMate is typing...</span>
                        </div>
                    </div>
                )}
            </div>

            <ChatInput onSendMessage={handleSendMessage} loading={loading} />
        </div>
    );
};

// ================================================================
// MOOD TRACKING COMPONENTS
// ================================================================

const MoodLogger = ({ user, onShowNotification }) => {
    const [selectedMood, setSelectedMood] = useState('');
    const [intensity, setIntensity] = useState(5);
    const [triggers, setTriggers] = useState('');
    const [activities, setActivities] = useState('');
    const [loading, setLoading] = useState(false);
    const { makeRequest } = useAPI();

    const moods = [
        { key: 'happy', label: 'Happy', emoji: '😊', color: '#FFD93D' },
        { key: 'sad', label: 'Sad', emoji: '😢', color: '#6C9BD1' },
        { key: 'anxious', label: 'Anxious', emoji: '😰', color: '#FF6B9D' },
        { key: 'calm', label: 'Calm', emoji: '😌', color: '#4ECDC4' },
        { key: 'excited', label: 'Excited', emoji: '🤩', color: '#FF8C42' },
        { key: 'angry', label: 'Angry', emoji: '😠', color: '#FF4757' },
        { key: 'confused', label: 'Confused', emoji: '😕', color: '#A4B0BE' },
        { key: 'grateful', label: 'Grateful', emoji: '🙏', color: '#2ED573' }
    ];

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!selectedMood) return;

        setLoading(true);

        try {
            const response = await makeRequest('/mood/log', {
                method: 'POST',
                body: JSON.stringify({
                    mood: selectedMood,
                    intensity: intensity,
                    triggers: triggers,
                    activities: activities
                })
            });

            if (response.success) {
                onShowNotification({
                    type: 'success',
                    title: 'Mood Logged!',
                    message: `+${response.points_earned} points earned`,
                    emoji: '📝'
                });

                // Reset form
                setSelectedMood('');
                setIntensity(5);
                setTriggers('');
                setActivities('');
            }
        } catch (error) {
            console.error('Failed to log mood:', error);
            onShowNotification({
                type: 'error',
                title: 'Error',
                message: 'Failed to log mood. Please try again.',
                emoji: '❌'
            });
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="mood-logger animate-fade-in">
            <div className="mood-logger-header">
                <h3>📝 Log Your Mood</h3>
                <p>How are you feeling right now?</p>
            </div>

            <form onSubmit={handleSubmit}>
                <div className="mood-selection">
                    <div className="mood-grid">
                        {moods.map(mood => (
                            <button
                                key={mood.key}
                                type="button"
                                className={`mood-option ${selectedMood === mood.key ? 'selected' : ''}`}
                                onClick={() => setSelectedMood(mood.key)}
                                style={{ borderColor: mood.color }}
                            >
                                <span className="mood-emoji">{mood.emoji}</span>
                                <span className="mood-label">{mood.label}</span>
                            </button>
                        ))}
                    </div>
                </div>

                {selectedMood && (
                    <div className="mood-details animate-slide-down">
                        <div className="form-group">
                            <label>Intensity (1-10)</label>
                            <div className="intensity-slider">
                                <input
                                    type="range"
                                    min="1"
                                    max="10"
                                    value={intensity}
                                    onChange={(e) => setIntensity(parseInt(e.target.value))}
                                    className="slider"
                                />
                                <div className="intensity-value">{intensity}/10</div>
                            </div>
                        </div>

                        <div className="form-group">
                            <label>What triggered this mood? (Optional)</label>
                            <textarea
                                value={triggers}
                                onChange={(e) => setTriggers(e.target.value)}
                                placeholder="e.g., work stress, good news, social interaction..."
                                rows="2"
                            />
                        </div>

                        <div className="form-group">
                            <label>What activities helped? (Optional)</label>
                            <textarea
                                value={activities}
                                onChange={(e) => setActivities(e.target.value)}
                                placeholder="e.g., listening to music, talking to friends, meditation..."
                                rows="2"
                            />
                        </div>

                        <button type="submit" className="btn btn-primary" disabled={loading}>
                            {loading ? 'Logging...' : 'Log Mood'}
                        </button>
                    </div>
                )}
            </form>
        </div>
    );
};

// ================================================================
// COMMUNITY WALL COMPONENT
// ================================================================

const CommunityWall = ({ onShowNotification }) => {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [newPost, setNewPost] = useState({ mood: '', message: '' });
    const [posting, setPosting] = useState(false);
    const { makeRequest } = useAPI();

    const moods = ['happy', 'sad', 'anxious', 'excited', 'calm', 'grateful', 'motivated'];

    useEffect(() => {
        loadCommunityPosts();
    }, []);

    const loadCommunityPosts = async () => {
        try {
            const response = await makeRequest('/community');
            setPosts(response.posts || []);
        } catch (error) {
            console.error('Failed to load community posts:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSubmitPost = async (e) => {
        e.preventDefault();
        if (!newPost.mood || !newPost.message.trim()) return;

        setPosting(true);

        try {
            const response = await makeRequest('/community', {
                method: 'POST',
                body: JSON.stringify(newPost)
            });

            if (response.success) {
                setPosts(prev => [response.post, ...prev]);
                setNewPost({ mood: '', message: '' });
                onShowNotification({
                    type: 'success',
                    title: 'Posted!',
                    message: 'Your anonymous post has been shared',
                    emoji: '📝'
                });
            }
        } catch (error) {
            console.error('Failed to create post:', error);
        } finally {
            setPosting(false);
        }
    };

    const handleReaction = async (postId, emoji) => {
        try {
            const response = await makeRequest(`/community/${postId}/react`, {
                method: 'POST',
                body: JSON.stringify({ emoji })
            });

            if (response.success) {
                setPosts(prev => prev.map(post => 
                    post.id === postId 
                        ? { ...post, reactions: response.reactions }
                        : post
                ));
            }
        } catch (error) {
            console.error('Failed to add reaction:', error);
        }
    };

    return (
        <div className="community-wall">
            <div className="community-header">
                <h2>🌍 Community Wall</h2>
                <p>Share your feelings anonymously with the MoodMate community</p>
            </div>

            <div className="post-composer">
                <h3>Share Anonymously</h3>
                <form onSubmit={handleSubmitPost}>
                    <div className="form-group">
                        <label>How are you feeling?</label>
                        <div className="mood-selector">
                            {moods.map(mood => (
                                <button
                                    key={mood}
                                    type="button"
                                    className={`mood-chip ${newPost.mood === mood ? 'selected' : ''}`}
                                    onClick={() => setNewPost({...newPost, mood})}
                                >
                                    {getMoodEmoji(mood)} {mood}
                                </button>
                            ))}
                        </div>
                    </div>

                    <div className="form-group">
                        <textarea
                            value={newPost.message}
                            onChange={(e) => setNewPost({...newPost, message: e.target.value})}
                            placeholder="Share what's on your mind... Your post will be anonymous."
                            maxLength="500"
                            rows="3"
                            required
                        />
                        <div className="char-counter">{newPost.message.length}/500</div>
                    </div>

                    <button 
                        type="submit" 
                        className="btn btn-primary" 
                        disabled={!newPost.mood || !newPost.message.trim() || posting}
                    >
                        {posting ? 'Posting...' : 'Share Anonymously'}
                    </button>
                </form>
            </div>

            <div className="community-posts">
                {loading ? (
                    <div className="loading-container">
                        <div className="spinner"></div>
                        <p>Loading community posts...</p>
                    </div>
                ) : (
                    posts.map(post => (
                        <div key={post.id} className="community-post animate-fade-in" 
                             style={{ borderLeft: `4px solid ${post.color_theme}` }}>
                            <div className="post-header">
                                <div className="post-mood">
                                    {getMoodEmoji(post.mood)} {post.mood}
                                </div>
                                <div className="post-time">{post.time_ago || formatTimeAgo(post.timestamp)}</div>
                            </div>

                            <div className="post-content">
                                {post.message}
                            </div>

                            <div className="post-reactions">
                                {['❤️', '🤗', '💪', '🙏', '✨'].map(emoji => (
                                    <button
                                        key={emoji}
                                        className="reaction-btn"
                                        onClick={() => handleReaction(post.id, emoji)}
                                    >
                                        {emoji} {post.reactions?.[emoji] || 0}
                                    </button>
                                ))}
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

// ================================================================
// DAILY CHALLENGE COMPONENT
