const DailyChallenge = ({ user, onShowNotification }) => {
    const [challenge, setChallenge] = useState(null);
    const [loading, setLoading] = useState(true);
    const [completing, setCompleting] = useState(false);
    const { makeRequest } = useAPI();

    useEffect(() => {
        loadDailyChallenge();
    }, []);

    const loadDailyChallenge = async () => {
        try {
            const response = await makeRequest('/challenges/daily');
            setChallenge(response.challenge);
        } catch (error) {
            console.error('Failed to load daily challenge:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleCompleteChallenge = async () => {
        if (!challenge || challenge.completed) return;

        setCompleting(true);

        try {
            const response = await makeRequest(`/challenges/${challenge.id}/complete`, {
                method: 'POST'
            });

            if (response.success) {
                setChallenge(prev => ({ ...prev, completed: true }));
                onShowNotification({
                    type: 'success',
                    title: 'Challenge Completed!',
                    message: `+${response.points_earned} points earned`,
                    emoji: '🎉'
                });

                if (response.new_badges && response.new_badges.length > 0) {
                    response.new_badges.forEach(badge => {
                        const badgeInfo = getBadgeDetails(badge);
                        onShowNotification({
                            type: 'success',
                            title: 'New Badge!',
                            message: badgeInfo.name,
                            emoji: badgeInfo.emoji
                        });
                    });
                }
            }
        } catch (error) {
            console.error('Failed to complete challenge:', error);
        } finally {
            setCompleting(false);
        }
    };

    if (loading) {
        return (
            <div className="loading-container">
                <div className="spinner"></div>
                <p>Loading daily challenge...</p>
            </div>
        );
    }

    if (!challenge) {
        return (
            <div className="daily-challenge">
                <h3>🎯 Daily Challenge</h3>
                <p>No challenge available today. Check back tomorrow!</p>
            </div>
        );
    }

    return (
        <div className="daily-challenge animate-fade-in">
            <div className="challenge-header">
                <h3>🎯 Today's Challenge</h3>
                <div className="challenge-points">+{challenge.points} points</div>
            </div>

            <div className={`challenge-card ${challenge.completed ? 'completed' : ''}`}>
                <div className="challenge-type">{challenge.type}</div>
                <div className="challenge-text">{challenge.text}</div>

                {challenge.completed ? (
                    <div className="challenge-completed">
                        ✅ Completed! Great job!
                    </div>
                ) : (
                    <button
                        className="btn btn-primary challenge-btn"
                        onClick={handleCompleteChallenge}
                        disabled={completing}
                    >
                        {completing ? 'Marking Complete...' : 'Mark as Complete'}
                    </button>
                )}
            </div>
        </div>
    );
};

// ================================================================
// WELLNESS FEATURES COMPONENT
// ================================================================

const WellnessFeatures = ({ onShowNotification }) => {
    const [activeFeature, setActiveFeature] = useState(null);
    const [content, setContent] = useState(null);
    const [loading, setLoading] = useState(false);
    const { makeRequest } = useAPI();

    const features = [
        { key: 'breathing', title: '🌬️ Breathing Exercise', description: 'Guided breathing exercises' },
        { key: 'mindfulness', title: '🧘 Mindfulness', description: 'Mindfulness and meditation' },
        { key: 'quotes', title: '💭 Daily Quotes', description: 'Inspirational quotes' },
        { key: 'affirmations', title: '✨ Affirmations', description: 'Positive affirmations' },
        { key: 'music', title: '🎵 Music Therapy', description: 'Mood-based music recommendations' },
        { key: 'emergency', title: '🆘 Emergency Resources', description: 'Crisis support contacts' }
    ];

    const handleFeatureClick = async (feature) => {
        setActiveFeature(feature);
        setLoading(true);
        setContent(null);

        try {
            let response;
            switch (feature.key) {
                case 'breathing':
                    response = await makeRequest('/breathing-exercise');
                    break;
                case 'mindfulness':
                    response = await makeRequest('/mindfulness/exercise');
                    break;
                case 'quotes':
                    response = await makeRequest('/quotes/random');
                    break;
                case 'affirmations':
                    response = await makeRequest('/affirmations');
                    break;
                case 'music':
                    response = await makeRequest('/music/recommend', {
                        method: 'POST',
                        body: JSON.stringify({ mood: 'happy' })
                    });
                    break;
                case 'emergency':
                    response = await makeRequest('/emergency/resources');
                    break;
                default:
                    response = { message: 'Feature coming soon!' };
            }
            setContent(response);
        } catch (error) {
            console.error('Failed to load feature content:', error);
            setContent({ error: 'Failed to load content. Please try again.' });
        } finally {
            setLoading(false);
        }
    };

    const renderContent = () => {
        if (loading) return <div className="spinner"></div>;
        if (!content) return null;
        if (content.error) return <div className="error-message">{content.error}</div>;

        switch (activeFeature.key) {
            case 'breathing':
                return (
                    <div className="breathing-exercise">
                        <h4>{content.exercise?.name}</h4>
                        <p>{content.exercise?.instructions}</p>
                        <div className="exercise-duration">Duration: {content.exercise?.duration}s</div>
                    </div>
                );

            case 'mindfulness':
                return (
                    <div className="mindfulness-exercise">
                        <h4>{content.exercise?.name}</h4>
                        <ol>
                            {content.exercise?.instructions?.map((step, index) => (
                                <li key={index}>{step}</li>
                            ))}
                        </ol>
                        <div className="exercise-duration">Duration: {Math.floor(content.exercise?.duration / 60)} minutes</div>
                    </div>
                );

            case 'quotes':
                return (
                    <div className="daily-quote">
                        <blockquote>"{content.quote}"</blockquote>
                    </div>
                );

            case 'affirmations':
                return (
                    <div className="affirmations">
                        {content.affirmations?.map((affirmation, index) => (
                            <div key={index} className="affirmation">
                                ✨ {affirmation}
                            </div>
                        ))}
                        <div className="daily-reminder">{content.daily_reminder}</div>
                    </div>
                );

            case 'music':
                return (
                    <div className="music-recommendations">
                        <h4>🎵 {content.playlist_name}</h4>
                        {content.recommendations?.map((rec, index) => (
                            <div key={index} className="music-rec">
                                <div className="song-title">{rec.title}</div>
                                <div className="song-reason">{rec.reason}</div>
                            </div>
                        ))}
                    </div>
                );

            case 'emergency':
                return (
                    <div className="emergency-resources">
                        <div className="emergency-warning">
                            If you're in crisis, please reach out for help immediately:
                        </div>
                        {content.resources?.map((resource, index) => (
                            <div key={index} className="emergency-contact">
                                <div className="contact-name">{resource.name}</div>
                                <div className="contact-number">{resource.number}</div>
                                <div className="contact-description">{resource.description}</div>
                            </div>
                        ))}
                    </div>
                );

            default:
                return <div>Feature content not available</div>;
        }
    };

    return (
        <div className="wellness-features">
            <div className="wellness-header">
                <h2>🌿 Wellness Center</h2>
                <p>Tools and resources for your mental well-being</p>
            </div>

            <div className="features-grid">
                {features.map(feature => (
                    <div 
                        key={feature.key} 
                        className={`feature-card ${activeFeature?.key === feature.key ? 'active' : ''}`}
                        onClick={() => handleFeatureClick(feature)}
                    >
                        <h3>{feature.title}</h3>
                        <p>{feature.description}</p>
                    </div>
                ))}
            </div>

            {activeFeature && (
                <div className="feature-content animate-slide-down">
                    <div className="feature-content-header">
                        <h3>{activeFeature.title}</h3>
                        <button className="close-btn" onClick={() => setActiveFeature(null)}>×</button>
                    </div>
                    <div className="feature-content-body">
                        {renderContent()}
                    </div>
                </div>
            )}
        </div>
    );
};

