    if (loading) {
        return (
            <div className="loading-container">
                <div className="spinner"></div>
                <p>Loading your profile...</p>
            </div>
        );
    }

    return (
        <div className="user-profile">
            <div className="profile-header">
                <div className="profile-avatar" style={{ backgroundColor: user.avatar_color || '#8B5CF6' }}>
                    {user.username?.[0]?.toUpperCase()}
                </div>
                <div className="profile-info">
                    <h2>{user.username}</h2>
                    <p>{user.email}</p>
                    <div className="profile-stats">
                        <div className="stat">
                            <span className="stat-emoji">⭐</span>
                            <span>Level {user.level || 1}</span>
                        </div>
                        <div className="stat">
                            <span className="stat-emoji">{getStreakEmoji(user.current_streak || 0)}</span>
                            <span>{user.current_streak || 0} day streak</span>
                        </div>
                        <div className="stat">
                            <span className="stat-emoji">🪙</span>
                            <span>{user.mood_points || 0} points</span>
                        </div>
