                        <div className="mood-distribution">
                            <h4>Most Common Moods</h4>
                            <div className="mood-bars">
                                {moodAnalytics.mood_distribution.slice(0, 5).map(([mood, count, avgIntensity]) => (
                                    <div key={mood} className="mood-bar">
                                        <div className="mood-info">
                                            <span className="mood-emoji">{getMoodEmoji(mood)}</span>
                                            <span className="mood-name">{mood}</span>
                                            <span className="mood-count">{count} times</span>
                                        </div>
                                        <div className="bar-container">
                                            <div 
                                                className="bar-fill" 
                                                style={{ 
                                                    width: `${(count / moodAnalytics.mood_distribution[0][1]) * 100}%` 
                                                }}
                                            ></div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}
                </div>
            )}

            {userStats?.recent_activities && userStats.recent_activities.length > 0 && (
                <div className="recent-activities">
                    <h3>📝 Recent Activity</h3>
                    <div className="activity-list">
                        {userStats.recent_activities.slice(0, 5).map((activity, index) => (
                            <div key={index} className="activity-item">
                                <div className="activity-mood">
                                    {getMoodEmoji(activity[1])} {activity[1]}
                                </div>
                                <div className="activity-message">{activity[0].substring(0, 100)}...</div>
                                <div className="activity-time">{formatTimeAgo(activity[2])}</div>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

// ================================================================
// MAIN APP COMPONENT
// ================================================================

const App = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [authMode, setAuthMode] = useState('login');
    const [currentTab, setCurrentTab] = useState('chat');
    const [notifications, setNotifications] = useState([]);
    const [theme, setTheme] = useLocalStorage('moodmate-theme', 'dark');
    const { makeRequest } = useAPI();

    // Initialize app and check authentication
    useEffect(() => {
        checkAuthStatus();
    }, []);

    // Apply theme to document
    useEffect(() => {
        document.documentElement.setAttribute('data-theme', theme);
    }, [theme]);

    const checkAuthStatus = async () => {
        try {
            const response = await makeRequest('/user/stats');
            if (response.user) {
                setUser(response.user);
            }
        } catch (error) {
            console.log('Not authenticated');
        } finally {
            setLoading(false);
        }
    };

    const handleLogin = (userData) => {
        setUser(userData);
        showNotification({
            type: 'success',
            title: 'Welcome back!',
            message: `Namaste ${userData.username}! 🙏`,
            emoji: '👋'
        });
    };

    const handleRegister = (userData) => {
        setUser(userData);
        showNotification({
            type: 'success',
            title: 'Welcome to MoodMate!',
            message: 'Your mental wellness journey starts now',
            emoji: '🎉'
        });
    };

    const handleLogout = async () => {
        try {
            await makeRequest('/logout', { method: 'POST' });
        } finally {
            setUser(null);
            setCurrentTab('chat');
            showNotification({
                type: 'info',
                title: 'Logged out',
                message: 'Take care, see you soon! 💜',
                emoji: '👋'
            });
        }
    };

    const showNotification = (notification) => {
        const id = Date.now();
        setNotifications(prev => [...prev, { ...notification, id }]);
    };

    const removeNotification = (id) => {
        setNotifications(prev => prev.filter(n => n.id !== id));
    };

    const updateUser = (updatedUser) => {
        setUser(updatedUser);
    };

    const contextValue = {
        user,
        setUser,
        currentTab,
        setCurrentTab,
        theme,
        setTheme,
        showNotification
    };

    if (loading) {
        return (
            <div className="app-loading">
                <div className="loading-content">
                    <div className="app-logo">🧠</div>
                    <h1>MoodMate</h1>
                    <div className="spinner large"></div>
                    <p>Loading your AI mental health companion...</p>
                </div>
            </div>
        );
    }

    return (
        <AppContext.Provider value={contextValue}>
            <div className={`app theme-${theme}`}>
                {!user ? (
                    <div className="auth-container">
                        <div className="auth-background"></div>
                        <div className="auth-content">
                            <div className="auth-logo">
                                <div className="logo-icon">🧠</div>
                                <h1>MoodMate</h1>
                                <p>Your AI Mental Health Companion</p>
                                <div className="tagline">सच में, तुम्हारा दोस्त!</div>
                            </div>

                            {authMode === 'login' ? (
                                <LoginForm
                                    onLogin={handleLogin}
                                    onSwitchToRegister={() => setAuthMode('register')}
                                />
                            ) : (
                                <RegisterForm
                                    onRegister={handleRegister}
                                    onSwitchToLogin={() => setAuthMode('login')}
                                />
                            )}
                        </div>
                    </div>
                ) : (
                    <div className="app-main">
                        <div className="app-header">
                            <div className="header-left">
                                <div className="app-logo">
                                    <span className="logo-icon">🧠</span>
                                    <span className="logo-text">MoodMate</span>
                                </div>
                            </div>

                            <div className="header-center">
                                <nav className="main-nav">
                                    <button
                                        className={`nav-tab ${currentTab === 'chat' ? 'active' : ''}`}
                                        onClick={() => setCurrentTab('chat')}
                                    >
                                        💬 Chat
                                    </button>
                                    <button
                                        className={`nav-tab ${currentTab === 'mood' ? 'active' : ''}`}
                                        onClick={() => setCurrentTab('mood')}
                                    >
                                        😊 Mood
                                    </button>
                                    <button
                                        className={`nav-tab ${currentTab === 'community' ? 'active' : ''}`}
                                        onClick={() => setCurrentTab('community')}
                                    >
                                        🌍 Community
                                    </button>
                                    <button
                                        className={`nav-tab ${currentTab === 'wellness' ? 'active' : ''}`}
                                        onClick={() => setCurrentTab('wellness')}
                                    >
                                        🌿 Wellness
                                    </button>
                                    <button
                                        className={`nav-tab ${currentTab === 'profile' ? 'active' : ''}`}
                                        onClick={() => setCurrentTab('profile')}
                                    >
                                        👤 Profile
                                    </button>
                                </nav>
                            </div>

                            <div className="header-right">
                                <div className="user-menu">
                                    <div className="user-info">
                                        <span className="user-name">{user.username}</span>
                                        <div className="user-avatar" style={{ backgroundColor: user.avatar_color }}>
                                            {user.username?.[0]?.toUpperCase()}
                                        </div>
                                    </div>

                                    <div className="user-actions">
                                        <button
                                            className="theme-toggle"
                                            onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')}
                                            title="Toggle theme"
                                        >
                                            {theme === 'dark' ? '☀️' : '🌙'}
                                        </button>

                                        <button
                                            className="logout-button"
                                            onClick={handleLogout}
                                            title="Logout"
                                        >
                                            🚪
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div className="app-content">
                            <div className="main-content">
                                {currentTab === 'chat' && (
                                    <ChatInterface 
                                        user={user} 
                                        onUpdateUser={updateUser}
                                        onShowNotification={showNotification}
                                    />
                                )}

                                {currentTab === 'mood' && (
                                    <div className="mood-tab">
                                        <MoodLogger 
                                            user={user} 
                                            onShowNotification={showNotification}
                                        />
                                    </div>
                                )}

                                {currentTab === 'community' && (
                                    <CommunityWall onShowNotification={showNotification} />
                                )}

                                {currentTab === 'wellness' && (
                                    <WellnessFeatures onShowNotification={showNotification} />
                                )}

                                {currentTab === 'profile' && (
                                    <UserProfile 
                                        user={user} 
                                        onShowNotification={showNotification}
                                    />
                                )}
                            </div>

                            <div className="sidebar">
                                <DailyChallenge 
                                    user={user} 
                                    onShowNotification={showNotification}
                                />
                            </div>
                        </div>
                    </div>
                )}

                <div className="notifications-container">
                    {notifications.map(notification => (
                        <Notification
                            key={notification.id}
                            notification={notification}
                            onClose={removeNotification}
                        />
                    ))}
                </div>
            </div>
        </AppContext.Provider>
    );
};

// Render the app
ReactDOM.render(<App />, document.getElementById('root'));
