import React from 'react';

// This data would typically be fetched or passed down from a central config
const sampleAchievements = [
  {id: 1, name: "First Steps", description: "Complete your first journal entry", earned: true, icon: "📝", coins: 10},
  {id: 2, name: "7-Day Warrior", description: "Maintain a 7-day streak", earned: false, icon: "🔥", coins: 25},
  {id: 3, name: "Community Builder", description: "Join your first tribe", earned: false, icon: "👥", coins: 15},
  {id: 4, name: "Meditation Master", description: "Complete 10 meditation sessions", earned: false, icon: "🧘", coins: 20},
  {id: 5, name: "Therapy Explorer", description: "Book your first therapy session", earned: false, icon: "👨‍⚕️", coins: 25},
  {id: 6, name: "Game Champion", description: "Play all mini-games", earned: false, icon: "🎮", coins: 30}
];

const sampleShopItems = [
    {id: 1, name: "Sunset", description: "Warm orange and purple theme", category: "theme"},
    {id: 2, name: "Ocean", description: "Calming blue theme", category: "theme"},
    {id: 11, name: "Astronaut", description: "Space-themed avatar", category: "avatar"},
    {id: 12, name: "Nature Lover", description: "Eco-friendly avatar", category: "avatar"},
];

function Profile({ user, onUpdateUser, onUpgradeClick }) {
  
  const ownedThemes = sampleShopItems.filter(item => 
    item.category === 'theme' && user.purchasedItems?.includes(item.id)
  );

  const ownedAvatars = sampleShopItems.filter(item => 
    item.category === 'avatar' && user.purchasedItems?.includes(item.id)
  );

  const applyTheme = (themeName) => {
    onUpdateUser({ currentTheme: themeName });
    alert(`Theme set to ${themeName}!`);
  };

  const applyAvatar = (avatarName) => {
    onUpdateUser({ avatar: avatarName });
    alert(`Avatar set to ${avatarName}!`);
  };

  return (
    <div className="tab-content profile-tab">
      <div className="profile-grid">
        {/* Left Column */}
        <div className="profile-column-left">
          <div className="profile-card main-profile">
            <div className="profile-avatar-large">
              <div className="avatar-image">
                {/* Fixed optional chaining for avatar/username */}
                {user?.avatar?.charAt(0)?.toUpperCase() || user?.username?.charAt(0)?.toUpperCase() || "U"}
              </div>
            </div>
            <div className="profile-main-info">
              <h2>{user?.username || "User"}</h2>
              <p>Member since {new Date().toLocaleDateString()}</p>
              <div className={`premium-status ${user?.is_premium ? 'premium' : 'basic'}`}>
                {user?.is_premium ? '🌟 Premium Member' : 'Basic Member'}
              </div>
            </div>
          </div>

          <div className="profile-card">
            <h3>🔥 Weekly Streak</h3>
            <div className="weekly-streak">
              <div className="streak-bar">
                <div className="streak-progress" style={{width: `${Math.min(100, ((user?.streak || 0) / 7) * 100)}%`}}></div>
              </div>
              <div className="streak-days">
                {['S', 'M', 'T', 'W', 'T', 'F', 'S'].map((day, i) => {
                  const dayActive = i < ((user?.streak || 0) % 7);
                  return (
                    <div key={i} className={`streak-day ${dayActive ? 'active' : ''}`}>
                      <span>{day}</span>
                      {dayActive && <div className="streak-dot"></div>}
                    </div>
                  );
                })}
              </div>
            </div>
            <div className="streak-motivation">
              <p>You've maintained your streak for {user?.streak || 0} days! Keep the momentum going.</p>
            </div>
          </div>

          <div className="profile-card">
            <h3>🎨 Customization</h3>
            <h4>Themes</h4>
            <div className="customization-grid">
              {ownedThemes.map(theme => (
                <button key={theme.id} onClick={() => applyTheme(theme.name.toLowerCase())} className={`theme-option ${user?.currentTheme === theme.name.toLowerCase() ? 'active' : ''}`}>
                  {theme.name}
                </button>
              ))}
            </div>
            <h4 style={{marginTop: '1rem'}}>Avatars</h4>
            <div className="customization-grid">
              {ownedAvatars.map(avatar => (
                <button key={avatar.id} onClick={() => applyAvatar(avatar.name)} className={`avatar-option ${user?.avatar === avatar.name ? 'active' : ''}`}>
                  {avatar.name}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Right Column */}
        <div className="profile-column-right">
          <div className="profile-card">
            <h3>🏅 Achievements</h3>
            <div className="achievements-grid">
              {sampleAchievements.map(ach => (
                <div key={ach.id} className={`achievement ${ach.earned ? 'earned' : 'locked'}`}>
                  <span className="achievement-icon">{ach.icon}</span>
                  <div className="achievement-info">
                    <h4>{ach.name}</h4>
                    <p>{ach.description}</p>
                    {ach.earned && <span className="achievement-coins">+{ach.coins} coins</span>}
                  </div>
                  <span className="achievement-status">{ach.earned ? '✓' : '🔒'}</span>
                </div>
              ))}
            </div>
          </div>
           {!user?.is_premium && (
            <div className="profile-card upgrade-card">
              <h3>Go Premium</h3>
              <p>Unlock all features including games, AI coach, and sleep stories!</p>
              <button className="upgrade-btn-profile" onClick={onUpgradeClick}>Upgrade Now</button>
            </div>
           )}
        </div>
      </div>
    </div>
  );
}

export default Profile;
