import React, { useState, useEffect, useRef } from 'react';
import './Chat.css';

const Chat = ({ user }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const chatEndRef = useRef(null);
  const [activeTab, setActiveTab] = useState('chat');

  // Set the initial welcome message
  useEffect(() => {
    if (user && user.username) {
      setMessages([{
        id: Date.now(),
        text: `Hello ${user.username}! I'm MoodMate. How can I help you today?`,
        sender: 'bot'
      }]);
    }
  }, [user]);

  // Auto-scroll chat
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { id: Date.now(), text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput("");
    setIsTyping(true);

    try {
      // Simulate API call delay
      setTimeout(() => {
        const responses = [
          "I understand how that might make you feel.",
          "Thank you for sharing that with me.",
          "Would you like to talk more about it?",
          "I'm here to listen whenever you need.",
          "It's completely normal to feel that way."
        ];
        
        const randomResponse = responses[Math.floor(Math.random() * responses.length)];
        const botMessage = { id: Date.now() + 1, text: randomResponse, sender: 'bot' };
        setMessages(prev => [...prev, botMessage]);
        setIsTyping(false);
      }, 1000);
    } catch (error) {
      console.error("Chat error:", error);
      const errorMsg = { 
        id: Date.now() + 1, 
        text: "Sorry, I'm having trouble responding right now.", 
        sender: 'bot' 
      };
      setMessages(prev => [...prev, errorMsg]);
      setIsTyping(false);
    }
  };

  return (
    <div className="moodmate-app">
      {/* Header */}
      <header className="app-header">
        <div className="header-content">
          <h1 className="app-title">MoodMate</h1>
          <div className="user-info">
            <span className="username">{user?.username || 'User'}</span>
            <div className="user-avatar">
              {user?.username ? user.username.charAt(0).toUpperCase() : 'U'}
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="app-body">
        {/* Sidebar */}
        <aside className="sidebar">
          <nav className="sidebar-nav">
            <button 
              className={`nav-item ${activeTab === 'chat' ? 'active' : ''}`}
              onClick={() => setActiveTab('chat')}
            >
              <i className="fas fa-comment"></i>
              <span>Chat</span>
            </button>
            <button 
              className={`nav-item ${activeTab === 'journal' ? 'active' : ''}`}
              onClick={() => setActiveTab('journal')}
            >
              <i className="fas fa-book"></i>
              <span>Journal</span>
            </button>
            <button 
              className={`nav-item ${activeTab === 'therapy' ? 'active' : ''}`}
              onClick={() => setActiveTab('therapy')}
            >
              <i className="fas fa-heart"></i>
              <span>Therapy</span>
            </button>
            <button 
              className={`nav-item ${activeTab === 'community' ? 'active' : ''}`}
              onClick={() => setActiveTab('community')}
            >
              <i className="fas fa-users"></i>
              <span>Community</span>
            </button>
            <button 
              className={`nav-item ${activeTab === 'shop' ? 'active' : ''}`}
              onClick={() => setActiveTab('shop')}
            >
              <i className="fas fa-shopping-cart"></i>
              <span>Shop</span>
            </button>
            <button 
              className={`nav-item ${activeTab === 'profile' ? 'active' : ''}`}
              onClick={() => setActiveTab('profile')}
            >
              <i className="fas fa-user"></i>
              <span>Profile</span>
            </button>
          </nav>
          
          <div className="premium-section">
            <button className="premium-btn">
              <i className="fas fa-crown"></i>
              <span>Go Premium</span>
            </button>
          </div>
        </aside>

        {/* Chat Area */}
        <main className="chat-main">
          <div className="chat-container">
            <div className="chat-messages">
              {messages.map((msg) => (
                <div key={msg.id} className={`message ${msg.sender}`}>
                  <div className="message-bubble">
                    <p>{msg.text}</p>
                  </div>
                </div>
              ))}
              {isTyping && (
                <div className="message bot typing">
                  <div className="message-bubble">
                    <div className="typing-indicator">
                      <span></span>
                      <span></span>
                      <span></span>
                    </div>
                  </div>
                </div>
              )}
              <div ref={chatEndRef} className="scroll-anchor"></div>
            </div>

            <div className="chat-input-area">
              <div className="input-container">
                <input
                  type="text"
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
                  placeholder="Type your message here..."
                  className="message-input"
                />
                <button 
                  onClick={sendMessage} 
                  disabled={isTyping || !input.trim()}
                  className="send-btn"
                >
                  <i className="fas fa-paper-plane"></i>
                </button>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Chat;
