import React, { useState } from "react";
import "./login.css";

const API_URL = "http://127.0.0.1:5000"; // Flask backend

function AppWrapper() {
  const [isSignup, setIsSignup] = useState(false);
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    phone: "",
    loginId: "", // for login (email or phone)
    password: "",
    confirmPassword: ""
  });
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // --- Login ---
  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    setMessage("");

    if (!formData.loginId || !formData.password) {
      setError("Enter email/phone and password!");
      return;
    }

    try {
      console.log("Sending login data:", formData.loginId, formData.password);
      const res = await fetch(`${API_URL}/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          loginId: formData.loginId,
          password: formData.password,
        }),
      });

      const data = await res.json();
      console.log("Login response:", data);

      if (data.success) setMessage(data.message);
      else setError(data.message);
    } catch (err) {
      setError("‚ö†Ô∏è Server error: " + err.message);
      console.error("Login fetch error:", err);
    }
  };

  // --- Signup ---
  const handleSignup = async (e) => {
    e.preventDefault();
    setError("");
    setMessage("");

    if (!formData.username || !formData.email || !formData.phone || !formData.password) {
      setError("All fields are required!");
      return;
    }
    if (formData.password !== formData.confirmPassword) {
      setError("Passwords do not match!");
      return;
    }

    try {
      console.log("Sending signup data:", formData);
      const res = await fetch(`${API_URL}/signup`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          username: formData.username,
          email: formData.email,
          phone: formData.phone,
          password: formData.password,
        }),
      });

      const data = await res.json();
      console.log("Signup response:", data);

      if (data.success) {
        setMessage(data.message);
        setIsSignup(false);
      } else setError(data.message);
    } catch (err) {
      setError("‚ö†Ô∏è Server error: " + err.message);
      console.error("Signup fetch error:", err);
    }
  };

  const handleSubmit = isSignup ? handleSignup : handleLogin;

  return (
    <div className="auth-container">
      <div className="auth-box">
        <h1>üåô MoodMate</h1>
        <p>Your mental health companion</p>

        <form onSubmit={handleSubmit} className="auth-form">
          {isSignup && (
            <>
              <input
                type="text"
                name="username"
                placeholder="Username"
                value={formData.username}
                onChange={handleChange}
              />
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
              />
              <input
                type="text"
                name="phone"
                placeholder="Phone Number"
                value={formData.phone}
                onChange={handleChange}
              />
            </>
          )}

          {!isSignup && (
            <input
              type="text"
              name="loginId"
              placeholder="Email or Phone Number"
              value={formData.loginId}
              onChange={handleChange}
            />
          )}

          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
          />

          {isSignup && (
            <input
              type="password"
              name="confirmPassword"
              placeholder="Confirm Password"
              value={formData.confirmPassword}
              onChange={handleChange}
            />
          )}

          {error && <div className="error-message">{error}</div>}
          {message && <div className="success-message">{message}</div>}

          <button type="submit">{isSignup ? "Sign Up" : "Login"}</button>
        </form>

        {!isSignup && (
          <button
            className="forgot-btn"
            onClick={() => alert("üîë Password reset link sent to your email!")}
          >
            Forgot Password?
          </button>
        )}

        <p className="toggle-text">
          {isSignup ? (
            <>
              Already have an account?{" "}
              <span onClick={() => setIsSignup(false)}>Login</span>
            </>
          ) : (
            <>
              New here? <span onClick={() => setIsSignup(true)}>Sign Up</span>
            </>
          )}
        </p>
      </div>
    </div>
  );
}

export default AppWrapper;
