import React, { useEffect, useState } from "react";

/**
 * Billion-Dollar Coach Modal (frontend-only)
 *
 * Props:
 *  - user: { username?, loginId?, is_premium? }   // user object
 *  - onClose: () => void
 *  - onUpgradeClick: () => void
 *
 * Behavior:
 *  - If user is NOT premium: shows a premium upsell with benefits, urgency, CTA.
 *  - If user IS premium: generates (client-side) a structured "coach session":
 *      - Mindset Reset (1 line)
 *      - Action Plan (3 specific steps)
 *      - Accountability (1-line call to action)
 *
 * This is rule-based and deterministic — no external API calls required.
 */

function CoachModal({ user = {}, onClose = () => {}, onUpgradeClick = () => {} }) {
  const isPremium = Boolean(user?.is_premium);

  // Local state for premium coaching session
  const [coachContent, setCoachContent] = useState(null);
  const [tone, setTone] = useState("balanced"); // "tough", "compassionate", "balanced"
  const [loading, setLoading] = useState(false);
  const [seed, setSeed] = useState(() => Math.floor(Math.random() * 1000000));

  // Utility: small deterministic pseudo-random based on seed
  const prng = (n) => {
    // simple mulberry32-like generator
    let t = seed + n;
    t += 0x6D2B79F5;
    t = Math.imul(t ^ (t >>> 15), t | 1);
    t ^= t + Math.imul(t ^ (t >>> 7), t | 61);
    return ((t ^ (t >>> 14)) >>> 0) / 4294967296;
  };

  // Short helper lookups (can expand)
  const mindsetPrompts = {
    tough: [
      "Stop negotiating with your future — do the thing you said you'd do.",
      "Comfort is a trap. Discomfort builds muscle.",
      "The market pays for results, not good intentions."
    ],
    compassionate: [
      "Small consistent steps beat big bursts — be gentle and steady.",
      "You’re allowed to be imperfect; progress is the goal, not perfection.",
      "Rest is a part of growth — recharge to return stronger."
    ],
    balanced: [
      "Discipline creates freedom — build the scaffolding today.",
      "Progress compounds — do one meaningful thing right now.",
      "Momentum loves motion — one small win starts the streak."
    ]
  };

  const actionTemplates = [
    // Templates accept username and return a 3-step array
    (u) => [
      `Block 90 minutes in your calendar for deep focus (no phone).`,
      `Identify ONE non-negotiable task and finish it before lunch.`,
      `End the day with a 5-minute review: what worked, what to tweak.`
    ],
    (u) => [
      `Start with a 7-minute focused routine: breathe, plan, prioritize.`,
      `Work in 45-minute sprints; after each sprint, stand and reset for 2 minutes.`,
      `Send a short end-of-day update to your accountability buddy (or yourself).`
    ],
    (u) => [
      `Do the hardest task first — embrace the friction for 60 minutes.`,
      `Remove one distraction for the day (notifications off / app blocks).`,
      `Reward yourself with 15 minutes of quality rest after finishing the main task.`
    ]
  ];

  const accountabilityLines = {
    tough: [
      "Report back tonight with ✅ or be honest and accept the consequence.",
      "No excuses. Do it. Then tell me you did.",
      "If you skip, write down why—then show me you fixed it tomorrow."
    ],
    compassionate: [
      "Share one win tonight — small wins matter deeply.",
      "If you fall short, reflect and try again tomorrow with kindness.",
      "Tell me one thing you learned today and one step for tomorrow."
    ],
    balanced: [
      "Check in tonight with a short status: done / partial / missed.",
      "Keep the streak alive — even 1 small win counts.",
      "Log one improvement and one gratitude before bed."
    ]
  };

  // Build a coach session (client-side, rule-based)
  function buildCoachSession(username = "Friend", toneMode = "balanced") {
    // generate deterministic picks using seed
    const t = toneMode;
    const mindsetList = mindsetPrompts[t];
    const mindset = mindsetList[Math.floor(prng(1) * mindsetList.length)];

    const actionTpl = actionTemplates[Math.floor(prng(2) * actionTemplates.length)];
    const actions = actionTpl(username);

    const accountabilityList = accountabilityLines[t];
    const accountability = accountabilityList[Math.floor(prng(3) * accountabilityList.length)];

    // Add small personalization
    const userPrefix = username ? `${username.split(" ")[0]}, ` : "";

    return {
      header: "💎 Your Billion-Dollar Coach",
      greeting: `${userPrefix}Today’s focus: Discipline & High-ROI Habits`,
      mindset,
      actions,
      accountability,
      footer: `Limited seats remain — premium members get weekly 1:1 recaps.`
    };
  }

  // Generate session when modal opens for premium users
  useEffect(() => {
    
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isPremium, tone, seed]);

  // Handle "regenerate" (new seed)
  const handleRegenerate = () => {
    setSeed(Math.floor(Math.random() * 1000000));
  };

  // Small CTA for non-premium users: show urgency with a pseudo countdown
  const [countdown, setCountdown] = useState(24 * 60 * 60); // seconds until offer expires (example)

  useEffect(() => {
    if (isPremium) return;
    const iv = setInterval(() => setCountdown((c) => Math.max(0, c - 1)), 1000);
    return () => clearInterval(iv);
  }, [isPremium]);

  const formatCountdown = (s) => {
    const h = String(Math.floor(s / 3600)).padStart(2, "0");
    const m = String(Math.floor((s % 3600) / 60)).padStart(2, "0");
    const sec = String(s % 60).padStart(2, "0");
    return `${h}:${m}:${sec}`;
  };

  // Render: Premium vs Upsell
  return (
    <div
      className="coach-modal-overlay"
      role="dialog"
      aria-modal="true"
      onClick={onClose}
      style={styles.overlay}
    >
      <div
        className="coach-modal-card"
        onClick={(e) => e.stopPropagation()}
        style={styles.card}
      >
        <button aria-label="Close" onClick={onClose} style={styles.closeBtn}>
          ×
        </button>

        {!isPremium ? (
          <>
            <div style={styles.header}>
              <div style={styles.badge}>💎</div>
              <div>
                <h2 style={styles.title}>Billion-Dollar Coach — Premium</h2>
                <p style={styles.subtitle}>Elite coaching that actually moves the needle.</p>
              </div>
            </div>

            <div style={styles.content}>
              <ul style={styles.benefits}>
                <li>• Personalized daily action plans</li>
                <li>• Weekly performance recaps & a private accountability loop</li>
                <li>• Exclusive mini-courses (productivity, focus, resilience)</li>
                <li>• Priority coaching support & invites to limited cohorts</li>
              </ul>

              <div style={styles.urgency}>
                <div style={styles.urgencyText}>
                  <strong>Limited beta slots:</strong> <span style={{ color: "#ffdd57" }}>100 seats</span> left
                </div>
                <div style={styles.countdown}>Claim within: <strong>{formatCountdown(countdown)}</strong></div>
              </div>

              <div style={styles.priceRow}>
                <div>
                  <div style={{ fontSize: 14, color: "#888" }}>Special Founders Price</div>
                  <div style={{ fontSize: 22, fontWeight: 700 }}>$100 / month</div>
                </div>
                <button style={styles.upgradeBtn} onClick={onUpgradeClick}>
                  🔓 Unlock Elite Access
                </button>
              </div>

              <div style={{ marginTop: 14, color: "#666", fontSize: 13 }}>
                Not ready? Try a <button style={styles.linkBtn} onClick={() => { setSeed(Math.floor(Math.random()*1000000)); setTimeout(() => window.alert("Limited demo: try premium features!"), 200); }}>free demo snippet</button>.
              </div>
            </div>
          </>
        ) : (
          <>
            <div style={styles.header}>
              <div style={styles.badgeGold}>💎</div>
              <div>
                <h2 style={styles.titleGold}>Your Billion-Dollar Coach</h2>
                <p style={styles.subtitle}>High-impact daily plan — short, ruthless, effective.</p>
              </div>
            </div>

            <div style={styles.content}>
              <div style={{ display: "flex", gap: 8, marginBottom: 8 }}>
                <label style={styles.toneLabel}>
                  Tone:
                  <select
                    value={tone}
                    onChange={(e) => setTone(e.target.value)}
                    style={styles.select}
                  >
                    <option value="tough">Tough Mentor</option>
                    <option value="balanced">Balanced Pro</option>
                    <option value="compassionate">Supportive Guide</option>
                  </select>
                </label>

                <button style={styles.smallBtn} onClick={handleRegenerate} aria-label="Regenerate">
                  🔁 Regenerate
                </button>
              </div>

              {loading && (
                <div style={styles.loadingBox}>
                  <div className="pulse" style={styles.pulse} />
                  <div>Preparing your daily breakthrough...</div>
                </div>
              )}

              {!loading && coachContent && (
                <article style={styles.coachCard}>
                  <h3 style={{ marginTop: 0 }}>{coachContent.header}</h3>
                  <p style={{ marginTop: 4, color: "#333" }}>{coachContent.greeting}</p>

                  <section style={styles.section}>
                    <h4 style={styles.sectionTitle}>Mindset Reset</h4>
                    <p style={styles.sectionText}>{coachContent.mindset}</p>
                  </section>

                  <section style={styles.section}>
                    <h4 style={styles.sectionTitle}>Action Plan (Today)</h4>
                    <ol style={styles.actionList}>
                      {coachContent.actions.map((a, i) => (
                        <li key={i} style={styles.actionItem}>{a}</li>
                      ))}
                    </ol>
                  </section>

                  <section style={styles.section}>
                    <h4 style={styles.sectionTitle}>Accountability</h4>
                    <p style={styles.sectionText}>{coachContent.accountability}</p>
                  </section>

                  <div style={styles.footerRow}>
                    <button
                      style={styles.confirmBtn}
                      onClick={() => {
                        // simple local "commit" action - in prod this would call backend
                        window.alert("✅ Plan saved. Check your daily coaching log.");
                      }}
                    >
                      ✅ I'm in — Save Plan
                    </button>

                    <button
                      style={styles.smallBtn}
                      onClick={() => {
                        // mimic "message coach" UX
                        window.alert("Coach: I'll check in tonight — message me 'done' when finished.");
                      }}
                    >
                      ✉️ Message Coach
                    </button>
                  </div>
                </article>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

/* ---------- Inline styles (you can move these to CSS) ---------- */
const styles = {
  overlay: {
    position: "fixed",
    inset: 0,
    background: "rgba(6,10,15,0.6)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    zIndex: 9999,
    padding: 20
  },
  card: {
    width: "min(920px, 96%)",
    maxHeight: "90vh",
    overflowY: "auto",
    background: "#fff",
    borderRadius: 14,
    boxShadow: "0 20px 50px rgba(2,6,23,0.6)",
    padding: 22,
    position: "relative",
    border: "1px solid rgba(255, 221, 87, 0.18)"
  },
  closeBtn: {
    position: "absolute",
    right: 10,
    top: 8,
    border: "none",
    background: "transparent",
    fontSize: 22,
    cursor: "pointer",
    color: "#333"
  },
  header: { display: "flex", gap: 12, alignItems: "center", marginBottom: 8 },
  badge: { width: 56, height: 56, borderRadius: 12, background: "#111", display: "flex", alignItems: "center", justifyContent: "center", color: "#fff", fontSize: 24 },
  badgeGold: { width: 56, height: 56, borderRadius: 12, background: "linear-gradient(135deg,#ffd54a,#f9a825)", display: "flex", alignItems: "center", justifyContent: "center", color: "#222", fontSize: 24 },
  title: { margin: 0, fontSize: 22 },
  titleGold: { margin: 0, fontSize: 22, color: "#5b3a00" },
  subtitle: { margin: 0, color: "#666" },
  content: { marginTop: 12 },
  benefits: { paddingLeft: 18, color: "#333", marginTop: 10 },
  urgency: { marginTop: 14, padding: 12, borderRadius: 10, background: "linear-gradient(90deg,#fff6e5,#fff)", border: "1px solid rgba(255,170,0,0.06)" },
  urgencyText: { fontWeight: 600 },
  countdown: { marginTop: 6, color: "#444" },
  priceRow: { display: "flex", justifyContent: "space-between", alignItems: "center", marginTop: 12 },
  upgradeBtn: { background: "#0f1724", color: "#fff", padding: "10px 18px", borderRadius: 10, border: "none", cursor: "pointer", fontWeight: 700 },
  linkBtn: { background: "transparent", border: "none", color: "#0f62ff", cursor: "pointer", textDecoration: "underline" },
  headerGold: {},
  select: { marginLeft: 8, padding: 6, borderRadius: 6 },
  smallBtn: { padding: "8px 12px", borderRadius: 8, border: "1px solid #ddd", background: "#fff", cursor: "pointer" },
  loadingBox: { display: "flex", alignItems: "center", gap: 12, padding: 12, borderRadius: 10, background: "#fafafa" },
  pulse: { width: 12, height: 12, borderRadius: 6, background: "#ffd54a", animation: "pulse 1s infinite" },
  coachCard: { background: "linear-gradient(180deg,#fff,#fffef7)", padding: 16, borderRadius: 12, border: "1px solid rgba(0,0,0,0.04)" },
  section: { marginTop: 12 },
  sectionTitle: { margin: 0, fontSize: 14, color: "#444" },
  sectionText: { marginTop: 6, color: "#222" },
  actionList: { marginTop: 8, paddingLeft: 18 },
  actionItem: { marginBottom: 8 },
  footerRow: { marginTop: 14, display: "flex", gap: 10, alignItems: "center" },
  confirmBtn: { background: "#0f1724", color: "#fff", padding: "10px 14px", borderRadius: 10, border: "none", cursor: "pointer", fontWeight: 700 },
  toneLabel: { display: "flex", alignItems: "center", gap: 8, fontSize: 13 },
  coachFooter: { marginTop: 12, color: "#666" }
};

// small keyframes (place into your global CSS if desired)
const styleSheet = `
@keyframes pulse {
  0% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.3); opacity: 0.6; }
  100% { transform: scale(1); opacity: 1; }
}
`;
// Append the keyframes once
if (typeof document !== "undefined" && !document.getElementById("__coach_modal_keyframes")) {
  const el = document.createElement("style");
  el.id = "__coach_modal_keyframes";
  el.innerHTML = styleSheet;
  document.head.appendChild(el);
}

export default CoachModal;
