import React, { useState, useEffect } from 'react';
import './Therapy.css';

const allTherapists = [
    {id: 1, name: "Dr. Arnav Singh", specialization: "Clinical Psychology", price: 799, rating: 5.0, experience: "8 years", avatar: "👨‍⚕️", languages: ["Hindi", "English"], modes: ["Video", "Chat"], description: "Specialized in anxiety and depression treatment using evidence-based CBT techniques.", availableSlots: ["2023-12-15 10:00", "2023-12-15 14:00", "2023-12-16 11:00", "2023-12-17 15:00"]},
    {id: 2, name: "Aaryan Kumar", specialization: "Counseling Psychology", price: 599, rating: 4.8, experience: "5 years", avatar: "👨‍⚕️", languages: ["Hindi", "English"], modes: ["Voice", "Chat"], description: "Focuses on youth mental health, career stress, and building healthy relationships.", availableSlots: ["2023-12-15 09:00", "2023-12-16 10:00", "2023-12-16 16:00", "2023-12-17 14:00"]},
    {id: 3, name: "Dr. Ankur Verma", specialization: "Psychiatry", price: 999, rating: 4.9, experience: "12 years", avatar: "👨‍⚕️", languages: ["English"], modes: ["Video"], description: "MD in Psychiatry with expertise in medication management for complex cases.", availableSlots: ["2023-12-15 11:00", "2023-12-16 09:00", "2023-12-17 10:00", "2023-12-17 16:00"]}
];

function Therapy({ user, onUpdateUser }) {
    const [searchTerm, setSearchTerm] = useState('');
    const [languageFilter, setLanguageFilter] = useState('All');
    const [modeFilter, setModeFilter] = useState('All');
    const [filteredTherapists, setFilteredTherapists] = useState(allTherapists.slice(0, 3));
    const [selectedTherapist, setSelectedTherapist] = useState(null);
    const [showBookingForm, setShowBookingForm] = useState(false);
    const [bookingData, setBookingData] = useState({
        name: '',
        phone: '',
        age: '',
        gender: '',
        reason: '',
        preferredTime: ''
    });

    useEffect(() => {
        let therapists = allTherapists;

        if (searchTerm) {
            therapists = therapists.filter(t => 
                t.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
                t.specialization.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        if (languageFilter !== 'All') {
            therapists = therapists.filter(t => t.languages.includes(languageFilter));
        }

        if (modeFilter !== 'All') {
            therapists = therapists.filter(t => t.modes.includes(modeFilter));
        }

        setFilteredTherapists(therapists.slice(0, 3));
    }, [searchTerm, languageFilter, modeFilter]);

    const handleViewDetails = (therapist) => {
        setSelectedTherapist(therapist);
        setShowBookingForm(true);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setBookingData({
            ...bookingData,
            [name]: value
        });
    };

    const handleBookingSubmit = (e) => {
        e.preventDefault();
        alert(`Booking confirmed with ${selectedTherapist.name} on ${bookingData.preferredTime}`);
        setShowBookingForm(false);
        setBookingData({
            name: '',
            phone: '',
            age: '',
            gender: '',
            reason: '',
            preferredTime: ''
        });
    };

    return (
        <div className="therapy-hub">
            {!showBookingForm ? (
                <>
                    <div className="therapy-header">
                        <h2>Find Your Therapist</h2>
                        <p>Connect with licensed professionals to support your mental wellness journey.</p>
                    </div>

                    <div className="filter-bar">
                        <input 
                            type="text" 
                            placeholder="Search by name or specialty..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                        <select value={languageFilter} onChange={(e) => setLanguageFilter(e.target.value)}>
                            <option value="All">All Languages</option>
                            <option value="English">English</option>
                            <option value="Hindi">Hindi</option>
                            <option value="Gujarati">Gujarati</option>
                        </select>
                        <select value={modeFilter} onChange={(e) => setModeFilter(e.target.value)}>
                            <option value="All">All Session Modes</option>
                            <option value="Video">Video</option>
                            <option value="Voice">Voice</option>
                            <option value="Chat">Chat</option>
                        </select>
                    </div>

                    <div className="therapists-grid">
                        {filteredTherapists.length > 0 ? (
                            filteredTherapists.map(therapist => (
                                <div key={therapist.id} className="therapist-card">
                                    <div className="therapist-card-header">
                                        <div className="therapist-avatar">{therapist.avatar}</div>
                                        <div className="therapist-header-info">
                                            <h4>{therapist.name}</h4>
                                            <p>{therapist.specialization}</p>
                                        </div>
                                    </div>
                                    <div className="therapist-card-body">
                                        <div className="therapist-stats">
                                            <span>⭐ {therapist.rating.toFixed(1)}</span>
                                            <span>{therapist.experience}</span>
                                            <span>₹{therapist.price}/session</span>
                                        </div>
                                        <div className="therapist-tags">
                                            {therapist.languages.map(lang => <span key={lang}>{lang}</span>)}
                                        </div>
                                        <p className="therapist-bio">{therapist.description}</p>
                                    </div>
                                    <div className="therapist-card-footer">
                                        <button className="info-btn" onClick={() => handleViewDetails(therapist)}>View Details</button>
                                        <button className="book-btn" onClick={() => handleViewDetails(therapist)}>Book Now</button>
                                    </div>
                                </div>
                            ))
                        ) : (
                            <p className="no-results">No therapists found matching your criteria.</p>
                        )}
                    </div>
                </>
            ) : (
                <div className="booking-form-container">
                    <div className="booking-header">
                        <h2>Book Session with {selectedTherapist.name}</h2>
                        <p>Please fill out the form below to complete your booking</p>
                    </div>
                    
                    <form className="booking-form" onSubmit={handleBookingSubmit}>
                        <div className="form-row">
                            <div className="form-group">
                                <label htmlFor="name">Full Name</label>
                                <input
                                    type="text"
                                    id="name"
                                    name="name"
                                    value={bookingData.name}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="phone">Phone Number</label>
                                <input
                                    type="tel"
                                    id="phone"
                                    name="phone"
                                    value={bookingData.phone}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                        </div>
                        
                        <div className="form-row">
                            <div className="form-group">
                                <label htmlFor="age">Age</label>
                                <input
                                    type="number"
                                    id="age"
                                    name="age"
                                    value={bookingData.age}
                                    onChange={handleInputChange}
                                    required
                                    min="1"
                                    max="120"
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="gender">Gender</label>
                                <select
                                    id="gender"
                                    name="gender"
                                    value={bookingData.gender}
                                    onChange={handleInputChange}
                                    required
                                >
                                    <option value="">Select Gender</option>
                                    <option value="male">Male</option>
                                    <option value="female">Female</option>
                                    <option value="other">Other</option>
                                    <option value="prefer-not-to-say">Prefer not to say</option>
                                </select>
                            </div>
                        </div>
                        
                        <div className="form-group">
                            <label htmlFor="reason">What would you like to discuss?</label>
                            <textarea
                                id="reason"
                                name="reason"
                                value={bookingData.reason}
                                onChange={handleInputChange}
                                required
                                rows="4"
                                placeholder="Please share what's bringing you to therapy today..."
                            ></textarea>
                        </div>
                        
                        <div className="form-group">
                            <label htmlFor="preferredTime">Preferred Session Time</label>
                            <select
                                id="preferredTime"
                                name="preferredTime"
                                value={bookingData.preferredTime}
                                onChange={handleInputChange}
                                required
                            >
                                <option value="">Select a time slot</option>
                                {selectedTherapist.availableSlots.map((slot, index) => (
                                    <option key={index} value={slot}>
                                        {new Date(slot).toLocaleString()}
                                    </option>
                                ))}
                            </select>
                        </div>
                        
                        <div className="form-actions">
                            <button 
                                type="button" 
                                className="cancel-btn"
                                onClick={() => setShowBookingForm(false)}
                            >
                                Cancel
                            </button>
                            <button type="submit" className="submit-btn">
                                Confirm Booking
                            </button>
                        </div>
                    </form>
                </div>
            )}
        </div>
    );
}

export default Therapy;
