import React, { useState, useEffect, useRef } from 'react';
import './Chat.css'; // This now points to your new dedicated CSS

function Chat({ user }) {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const [isTyping, setIsTyping] = useState(false);
    const chatEndRef = useRef(null);

    useEffect(() => {
        if (user && user.username) {
            setMessages([{
                id: 1,
                text: `Hey there! MoodMate here. How are you doing today? I'm always here to listen if you'd like to talk about anything that's on your mind.`,
                sender: 'bot',
                timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
            }]);
        }
    }, [user]);

    useEffect(() => {
        chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    const sendMessage = async () => {
        // ... your sendMessage function remains the same ...
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                {messages.length === 0 && (
                    <div className="chat-placeholder">
                        <div className="placeholder-icon">ðŸ’¬</div>
                        <h2>Start a conversation</h2>
                        <p>Your mental wellness assistant is ready to listen.</p>
                    </div>
                )}
                
                {messages.map((msg) => (
                    <div key={msg.id} className={`chat-message ${msg.sender}`}>
                        <div className="bubble">
                            {msg.text}
                            <div className="message-timestamp">{msg.timestamp}</div>
                        </div>
                    </div>
                ))}
                {isTyping && <p>MoodMate is typing...</p>}
                <div ref={chatEndRef} />
            </div>
            <div className="input-area">
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                    placeholder="Type your message..."
                />
                <button onClick={sendMessage} disabled={isTyping} className="send-btn">
                    âž¤
                </button>
            </div>
        </div>
    );
}

export default Chat;
