import React, { useState } from 'react';

const Auth = ({ onLogin, onSignup, onForgotPassword, onSocialLogin, error, message }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [email, setEmail] = useState('');
  const [formData, setFormData] = useState({
    name: '',
    password: '',
    confirmPassword: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isLogin) {
      onLogin({ email, password: formData.password });
    } else {
      if (formData.password !== formData.confirmPassword) {
        alert("Passwords don't match!");
        return;
      }
      onSignup({ name: formData.name, email, password: formData.password });
    }
  };

  const handleForgotPasswordSubmit = (e) => {
    e.preventDefault();
    onForgotPassword(email);
  };

  if (showForgotPassword) {
    return (
      <div className="auth-container">
        <div className="auth-form">
          <h2>Reset Password</h2>
          <p>Enter your email to receive reset instructions</p>
          
          {error && <div className="error-message">{error}</div>}
          {message && <div className="success-message">{message}</div>}
          
          <form onSubmit={handleForgotPasswordSubmit}>
            <div className="form-group">
              <input
                type="email"
                placeholder="Email Address"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            
            <button type="submit" className="auth-btn">
              Send Reset Instructions
            </button>
          </form>
          
          <div className="auth-links">
            <button onClick={() => setShowForgotPassword(false)} className="link-btn">
              Back to {isLogin ? 'login' : 'signup'}
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="auth-container">
      <div className="auth-form">
        <h2>{isLogin ? 'Login' : 'Sign Up'}</h2>
        
        {error && <div className="error-message">{error}</div>}
        {message && <div className="success-message">{message}</div>}
        
        <div className="social-login">
          <button onClick={() => onSocialLogin('google')} className="social-btn google">
            Continue with Google
          </button>
          <button onClick={() => onSocialLogin('facebook')} className="social-btn facebook">
            Continue with Facebook
          </button>
        </div>
        
        <div className="divider">
          <span>or</span>
        </div>
        
        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <div className="form-group">
              <input
                type="text"
                name="name"
                placeholder="Full Name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
          )}
          
          <div className="form-group">
            <input
              type="email"
              placeholder="Email Address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          
          <div className="form-group">
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          
          {!isLogin && (
            <div className="form-group">
              <input
                type="password"
                name="confirmPassword"
                placeholder="Confirm Password"
                value={formData.confirmPassword}
                onChange={handleChange}
                required
              />
            </div>
          )}
          
          <button type="submit" className="auth-btn">
            {isLogin ? 'Login' : 'Sign Up'}
          </button>
        </form>
        
        <div className="auth-links">
          {isLogin ? (
            <>
              <button onClick={() => setShowForgotPassword(true)} className="link-btn">
                Forgot password?
              </button>
              <p>
                Don't have an account?{' '}
                <span onClick={() => setIsLogin(false)} className="link-btn">
                  Sign Up
                </span>
              </p>
            </>
          ) : (
            <p>
              Already have an account?{' '}
              <span onClick={() => setIsLogin(true)} className="link-btn">
                Login
              </span>
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Auth;
