import React, { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";
import "./App.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLogin, setShowLogin] = useState(true);
  const [showRegister, setShowRegister] = useState(false);
  const [activeTab, setActiveTab] = useState("chat");
  const [therapists, setTherapists] = useState([]);
  const [resources, setResources] = useState([]);
  const [storeItems, setStoreItems] = useState([]);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [showCrisis, setShowCrisis] = useState(false);

  const chatEndRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    if (token && userData) {
      setUser(JSON.parse(userData));
      setIsLoggedIn(true);
      setShowLogin(false);
      loadInitialData();
    }
  }, []);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  useEffect(() => {
    document.body.classList.toggle("dark-mode", isDarkMode);
  }, [isDarkMode]);

  const loadInitialData = () => {
    setMessages([{
      text: "Hello! I'm MoodMate. How are you feeling today?",
      sender: "bot"
    }]);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const credentials = {
      email: formData.get('email'),
      password: formData.get('password')
    };

    try {
      const response = await fetch("http://localhost:5000/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(credentials)
      });
      const data = await response.json();
      if (data.success) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        setUser(data.user);
        setIsLoggedIn(true);
        setShowLogin(false);
        loadInitialData();
      } else {
        alert(data.error || "Login failed");
      }
    } catch (error) {
      alert("Login failed. Check server.");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const userData = {
      email: formData.get('email'),
      password: formData.get('password'),
      name: formData.get('name')
    };

    try {
      const response = await fetch("http://localhost:5000/api/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(userData)
      });
      const data = await response.json();
      if (data.success) {
        alert("Registered! Login now.");
        setShowRegister(false);
        setShowLogin(true);
      } else {
        alert(data.error || "Registration failed");
      }
    } catch (error) {
      alert("Registration failed.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setIsLoggedIn(false);
    setShowLogin(true);
    setMessages([]);
  };

  const sendMessage = async () => {
    if (!input.trim() || !isLoggedIn) return;

    const userMsg = { text: input, sender: "user" };
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);

    try {
      const response = await fetch("http://localhost:5000/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: input })
      });
      const data = await response.json();
      if (data.success) {
        const botMsg = { text: data.response, sender: "bot" };
        setMessages(prev => [...prev, botMsg]);
        if (data.mood === "crisis") {
          setShowCrisis(true);
        }
      } else {
        setMessages(prev => [...prev, { text: data.error, sender: "bot" }]);
      }
    } catch (error) {
      setMessages(prev => [...prev, { text: "Connection error.", sender: "bot" }]);
    }
    setIsTyping(false);
  };

  const addJournalEntry = async () => {
    const entryText = prompt("What's on your mind?");
    if (!entryText) return;

    try {
      const response = await fetch("http://localhost:5000/api/journal", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text: entryText, email: user.email })
      });
      const data = await response.json();
      if (data.success) {
        setUser(prev => ({ ...prev, coins: data.totalCoins }));
        alert("Entry saved! +3 coins.");
      }
    } catch (error) {
      alert("Journal error.");
    }
  };

  const loadTherapists = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/therapists");
      const data = await response.json();
      setTherapists(data.therapists || []);
    } catch (error) {
      console.error("Therapists load error");
    }
  };

  const loadResources = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/resources");
      const data = await response.json();
      setResources(data.resources || []);
    } catch (error) {
      console.error("Resources load error");
    }
  };

  const loadStore = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/store");
      const data = await response.json();
      setStoreItems(data.items || []);
    } catch (error) {
      console.error("Store load error");
    }
  };

  const bookTherapist = async (therapistId) => {
    if (!window.confirm("Book?")) return;

    try {
      const response = await fetch("http://localhost:5000/api/therapists/book", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ therapist_id: therapistId, email: user.email })
      });
      const data = await response.json();
      if (data.success) {
        setUser(prev => ({ ...prev, coins: data.remainingCoins }));
        alert(data.message);
      } else {
        alert(data.error);
      }
    } catch (error) {
      alert("Booking error.");
    }
  };

  const buyItem = async (itemId) => {
    if (!window.confirm("Buy?")) return;

    try {
      const response = await fetch("http://localhost:5000/api/store/buy", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ item_id: itemId, email: user.email })
      });
      const data = await response.json();
      if (data.success) {
        setUser(prev => ({ ...prev, coins: data.remainingCoins, inventory: data.inventory }));
        alert("Purchased!");
      } else {
        alert(data.error);
      }
    } catch (error) {
      alert("Purchase error.");
    }
  };

  if (!isLoggedIn) {
    return (
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 1 }} className="app-container auth-container">
        <div className="auth-box">
          <h1>üåô MoodMate</h1>
          <p>Your mental health companion</p>
          
          {showLogin && (
            <form onSubmit={handleLogin} className="auth-form">
              <h2>Login</h2>
              <input type="email" name="email" placeholder="Email" required />
              <input type="password" name="password" placeholder="Password" required />
              <button type="submit">Login</button>
              <p>Don't have an account? <span className="auth-link" onClick={() => setShowRegister(true)}>Register</span></p>
            </form>
          )}

          {showRegister && (
            <form onSubmit={handleRegister} className="auth-form">
              <h2>Register</h2>
              <input type="text" name="name" placeholder="Name" required />
              <input type="email" name="email" placeholder="Email" required />
              <input type="password" name="password" placeholder="Password" required />
              <button type="submit">Register</button>
              <p>Already have an account? <span className="auth-link" onClick={() => setShowRegister(false)}>Login</span></p>
            </form>
          )}
        </div>
      </motion.div>
    );
  }

  return (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 1 }} className="app-container">
      <header className="app-header">
        <div className="header-left">
          <h1>üåô MoodMate</h1>
          <div className="user-stats">
            <span>üî• {user?.streak || 0} streak</span>
            <span>ü™ô {user?.coins || 0} coins</span>
          </div>
        </div>
        <div className="header-right">
          <span>Welcome, {user?.name}</span>
          <button onClick={() => setIsDarkMode(!isDarkMode)} className="dark-mode-toggle">{isDarkMode ? "‚òÄ" : "üåô"}</button>
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </div>
      </header>

      <nav className="app-nav">
        <button className={activeTab === "chat" ? "active" : ""} onClick={() => setActiveTab("chat")}>üí¨ Chat</button>
        <button className={activeTab === "journal" ? "active" : ""} onClick={() => setActiveTab("journal")}>üìì Journal</button>
        <button className={activeTab === "therapy" ? "active" : ""} onClick={() => { setActiveTab("therapy"); loadTherapists(); loadResources(); }}>üë®‚Äç‚öï Therapy</button>
        <button className={activeTab === "store" ? "active" : ""} onClick={() => { setActiveTab("store"); loadStore(); }}>üõí Store</button>
        <button className={activeTab === "relax" ? "active" : ""} onClick={() => setActiveTab("relax")}>üßò Relax</button>
        <button className={activeTab === "profile" ? "active" : ""} onClick={() => setActiveTab("profile")}>üë§ Profile</button>
      </nav>

      <motion.main initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ duration: 0.5 }} className="app-main">
        {activeTab === "chat" && (
          <div className="chat-tab">
            <div className="chat-box">
              {messages.map((msg, i) => (
                <motion.p key={i} initial={{ opacity: 0 }} animate={{ opacity: 1 }} className={msg.sender}>{msg.text}</motion.p>
              ))}
              {isTyping && <p className="typing">...</p>}
              <div ref={chatEndRef}></div>
            </div>
            <div className="input-area">
              <input value={input} onChange={(e) => setInput(e.target.value)} onKeyDown={(e) => e.key === "Enter" && sendMessage()} placeholder="Type..." />
              <button onClick={sendMessage}>Send</button>
            </div>
          </div>
        )}

        {activeTab === "journal" && (
          <div className="journal-tab">
            <button onClick={addJournalEntry}>+ New Entry</button>
          </div>
        )}

        {activeTab === "therapy" && (
          <div className="therapy-tab">
            <h3>Crisis Resources</h3>
            {resources.map((r, i) => <div key={i} className="resource-card">{r.name}: {r.number}</div>)}
            <h3>Therapists</h3>
            {therapists.map((t) => (
              <div key={t.id} className="therapist-card">
                <h4>{t.name}</h4>
                <p>{t.specialization} - ü™ô {t.price}</p>
                {t.number && <p>Call: {t.number}</p>}
                <button onClick={() => bookTherapist(t.id)}>Book</button>
              </div>
            ))}
          </div>
        )}

        {activeTab === "store" && (
          <div className="store-tab">
            <h3>Store</h3>
            {storeItems.map((item) => (
              <div key={item.id} className="store-item">
                <h4>{item.name} - ü™ô {item.price}</h4>
                <button onClick={() => buyItem(item.id)}>Buy</button>
              </div>
            ))}
          </div>
        )}

        {activeTab === "relax" && (
          <div className="relax-tab">
            <h3>Breathing Exercise</h3>
            <div className="breathing-circle"></div>
            <p>Inhale... Hold... Exhale... Repeat for relaxation.</p>
          </div>
        )}

        {activeTab === "profile" && (
          <div className="profile-tab">
            <h2>Profile</h2>
            <p>Name: {user.name}</p>
            <p>Email: {user.email}</p>
            <p>Coins: {user.coins}</p>
            <p>Streak: {user.streak} days</p>
            <h3>Inventory</h3>
            {user.inventory.map((item, i) => <p key={i}>{item}</p>)}
          </div>
        )}
      </motion.main>

      {showCrisis && (
        <div className="crisis-modal">
          <h2>Crisis Alert</h2>
          <p>We're here for you. Call a helpline:</p>
          <ul>
            <li>Crisis Text Line: Text HOME to 741741</li>
            <li>Samaritans: 116 123</li>
          </ul>
          <button onClick={() => setShowCrisis(false)}>Close</button>
        </div>
      )}
    </motion.div>
  );
}

export default App;
