  const simulateTyping = async () => {

    const typingPhrases = ["Thinking", "Thinking.", "Thinking..", "Thinking..."];

    for (let i = 0; i < 6; i++) {

      setTypingIndicator(typingPhrases[i % 4]);

      await new Promise(resolve => setTimeout(resolve, 300));

    }

    setTypingIndicator("");

  };



  const sendMessage = async () => {

    if (!input.trim()) return;



    const userMsg = {

      id: Date.now(),

      text: input,

      sender: "user",

      timestamp: new Date().toISOString()

    };

    setMessages(prev => [...prev, userMsg]);

    setInput("");

    setIsTyping(true);

   

    const emergencyKeywords = ['suicide', 'kill myself', 'end it all', 'want to die', 'self harm', 'hurt myself'];

    const hasEmergency = emergencyKeywords.some(keyword =>

      input.toLowerCase().includes(keyword)

    );

   

    if (hasEmergency) {

      setShowEmergencyModal(true);

      setIsTyping(false);

      return;

    }

   

    simulateTyping();



    try {

      const response = await fetch(`${API_BASE_URL}/api/chat`, {

        method: 'POST',

        headers: { 'Content-Type': 'application/json' },

        body: JSON.stringify({ message: input })

      });



      const data = await response.json();



      if (!response.ok) {

        if (data.error === "gemini_unavailable") {

            alert("AI service unavailable, please try again later.");

            setIsTyping(false);

            return;

        }

        throw new Error(data.error || "API error");

      }

     

      const botMsg = {

        id: Date.now(),

        text: data.reply,

        sender: "bot",

        mood: data.mood,

        moodEmoji: data.moodEmoji,

        phrase: data.phrase,

        audioUrl: data.audioUrl,

        timestamp: new Date().toISOString()

      };

      setMessages(prev => [...prev, botMsg]);



      if (data.audioUrl && audioEnabled) {

        const audio = new Audio(`${API_BASE_URL}${data.audioUrl}`);

        audio.play();

      }



      if (data.coinsEarned > 0) {

        const newCoins = (user?.coins || 0) + data.coinsEarned;

        const updatedUser = {...user, coins: newCoins, streak: data.streak};

        setUser(updatedUser);

      }



    } catch (error) {

      console.log("Chat error:", error);

      alert("An unexpected error occurred. Please try again.");

    }



    setIsTyping(false);

    setTypingIndicator("");

  };



  const detectMoodFromInput = (text) => {

    const textLower = text.toLowerCase();

    if (textLower.includes('happy') || textLower.includes('good') || textLower.includes('great') || textLower.includes('excited')) return 'happy';

    if (textLower.includes('sad') || textLower.includes('depressed') || textLower.includes('unhappy') || textLower.includes('cry')) return 'sad';

    if (textLower.includes('anxious') || textLower.includes('nervous') || textLower.includes('worried') || textLower.includes('stress')) return 'anxious';

    if (textLower.includes('angry') || textLower.includes('mad') || textLower.includes('frustrated') || textLower.includes('annoyed')) return 'angry';

    return 'neutral';

  };



