                  <div key={`achievement-${achievement.id}`} className={`achievement ${achievement.earned ? 'earned' : 'locked'}`}>

                    <span className="achievement-icon">{achievement.icon}</span>

                    <div className="achievement-info">

                      <h4>{achievement.name}</h4>

                      <p>{achievement.description}</p>

                    </div>

                    <span className="achievement-status">

                      {achievement.earned ? '✓' : '🔒'}

                    </span>

                  </div>

                ))}

              </div>

            </div>



            <div className="profile-section">

              <h3>👥 My Tribes</h3>

              {userTribes.length === 0 ? (

                <p>You haven't joined any tribes yet. Visit the Community tab to join one!</p>

              ) : (

                <div className="user-tribes">

                  {userTribes.map(tribe => (

                    <div key={`user-tribe-${tribe.id}`} className="user-tribe">

                      <span>{tribe.name}</span>

                      <button onClick={() => setActiveTribe(tribe)}>

                        Visit

                      </button>

                    </div>

                  ))}

                </div>

              )}

            </div>



            <div className="profile-section">

              <h3>⚙️ Settings</h3>

              <div className="settings-grid">

                <div className="setting-item">

                  <span>Voice Responses</span>

                  <label className="switch">

                    <input type="checkbox" checked={audioEnabled} onChange={toggleAudio} />

                    <span className="slider"></span>

                  </label>

                </div>

                <div className="setting-item">

                  <span>Background Music</span>

                  <label className="switch">

                    <input type="checkbox" checked={musicEnabled} onChange={toggleMusic} />

                    <span className="slider"></span>

                  </label>

                </div>

                <div className="setting-item">

                  <span>AI Provider</span>

                  <select value={aiProvider} onChange={(e) => switchAiProvider(e.target.value)}>

                    <option value="gemini">Gemini AI</option>

                    <option value="fallback">Fallback Mode</option>

                  </select>

                </div>

              </div>

            </div>

          </div>

        )}

      </main>

    </div>

  );

}



export default App;
