import React, { useState, useEffect, useRef } from "react";
import "./app.css";

// Base URL for API calls
const API_BASE_URL = 'http://127.0.0.1:5000';

function App({ user, onLogout, chatHistory = [], currentChat, onNewChat, onLoadChat, onDeleteChat, onUpdateChat }) {
  const [messages, setMessages] = useState(currentChat?.messages || []);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [activeTab, setActiveTab] = useState("chat");
  const [journalEntries, setJournalEntries] = useState([]);
  const [therapists, setTherapists] = useState([]);
  const [resources, setResources] = useState([]);
  const [moodTribes, setMoodTribes] = useState([]);
  const [shopItems, setShopItems] = useState([]);
  const [userTribes, setUserTribes] = useState([]);
  const [typingIndicator, setTypingIndicator] = useState("");
  const [showEmergencyModal, setShowEmergencyModal] = useState(false);
  const [achievements, setAchievements] = useState([]);
  const [activeTribe, setActiveTribe] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [showLoginBonus, setShowLoginBonus] = useState(false);
  const [showTherapistForm, setShowTherapistForm] = useState(false);
  const [bookingForm, setBookingForm] = useState({
    name: "",
    age: "",
    gender: "",
    contact: "",
    problem: "",
    preferredTime: ""
  });
  const [activeGame, setActiveGame] = useState(null);
  const [bubbleGame, setBubbleGame] = useState({ bubbles: [], score: 0 });
  const [coloringImage, setColoringImage] = useState(null);
  const [stressBalloon, setStressBalloon] = useState({ inflated: false, progress: 0 });
  const [gratitudeWheel, setGratitudeWheel] = useState({ spinning: false, result: null });
  const [selectedColor, setSelectedColor] = useState("#FF6B6B");
  const [showTherapistInfo, setShowTherapistInfo] = useState(null);
  const [exerciseTimer, setExerciseTimer] = useState(0);
  const [exerciseDuration, setExerciseDuration] = useState(0);
  const [showJournalAnalysis, setShowJournalAnalysis] = useState(false);
  const [showMoodTracker, setShowMoodTracker] = useState(false);
  const [breathingActive, setBreathingActive] = useState(false);
  const [breathingPhase, setBreathingPhase] = useState("inhale");
  const [breathingProgress, setBreathingProgress] = useState(0);
  const [breathingPhaseDuration, setBreathingPhaseDuration] = useState(4000); // Default to 4 seconds
  const [showMeditationGuide, setShowMeditationGuide] = useState(false);
  const [activeMeditation, setActiveMeditation] = useState(null);
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [selectedTherapist, setSelectedTherapist] = useState(null);
  const [showGamesModal, setShowGamesModal] = useState(false);
  const [showAICoach, setShowAICoach] = useState(false);
  const [aiCoachPlan, setAiCoachPlan] = useState([]);
  const [showSleepCompanion, setShowSleepCompanion] = useState(false);
  const [activeChallenge, setActiveChallenge] = useState(null);
  const [showChallengeModal, setShowChallengeModal] = useState(false);
  const [audioEnabled, setAudioEnabled] = useState(true);
  const [musicEnabled, setMusicEnabled] = useState(true);
  const [tribeChatMessages, setTribeChatMessages] = useState({});
  const [tribeInput, setTribeInput] = useState("");
  const [showMoodReport, setShowMoodReport] = useState(false);
  const [lastMessageMood, setLastMessageMood] = useState(null);
  const [aiProvider, setAiProvider] = useState("gemini");


  const chatEndRef = useRef(null);
  const breathingTimerRef = useRef(null);
  const gameIntervalRef = useRef(null);
  const tribeChatEndRef = useRef(null);
  const exerciseTimerRef = useRef(null);
  const inputRef = useRef(null);

  // Sample data (can be replaced with API calls)
  const sampleTherapists = [
    { id: 1, name: "Dr. Arnav Singh", specialization: "Clinical Psychology", price: 7999, rating: 5.0, experience: "8 years", avatar: "ðŸ‘¨â€âš•ï¸", languages: ["Hindi", "English"], modes: ["Video", "Chat"], description: "Specialized in anxiety and depression treatment", education: "PhD in Clinical Psychology, University of Delhi", approach: "Cognitive Behavioral Therapy (CBT)", bio: "Dr. Arnav has helped hundreds of patients overcome anxiety and depression through evidence-based techniques. He believes in a holistic approach to mental health." },
    { id: 2, name: "Aaryan Kumar", specialization: "Counseling Psychology", price: 599, rating: 4.5, experience: "5 years", avatar: "ðŸ‘¨â€âš•ï¸", languages: ["Hindi", "English"], modes: ["Voice", "Chat"], description: "Focus on youth mental health and career stress", education: "Masters in Counseling Psychology, TISS Mumbai", approach: "Person-Centered Therapy", bio: "Rajiv specializes in helping young adults navigate career stress and life transitions. He creates a safe, non-judgmental space for his clients." },
    { id: 3, name: "Ankur Verma", specialization: "Psychiatry", price: 999, rating: 4.8, experience: "12 years", avatar: "ðŸ‘¨â€âš•ï¸", languages: ["English"], modes: ["Video"], description: "MD in Psychiatry with medication management expertise", education: "MD Psychiatry, AIIMS Delhi", approach: "Integrated Pharmacotherapy and Psychotherapy", bio: "Dr. Singh is a board-certified psychiatrist with extensive experience in medication management and treatment-resistant depression." },
    { id: 4, name: "Aanchal", specialization: "Art Therapy", price: 499, rating: 4.6, experience: "4 years", avatar: "ðŸŽ¨", languages: ["Hindi"], modes: ["Video", "Chat"], description: "Creative approaches to healing through art therapy", education: "Masters in Art Therapy, IATE Mumbai", approach: "Expressive Arts Therapy", bio: "Neha uses creative processes to help clients explore emotions, reduce anxiety, and improve self-esteem. No artistic skill required!" },
  ];
  const sampleResources = [
    { type: "crisis", name: "National Suicide Prevention Lifeline", number: "1-800-273-8255", available: "24/7" },
    { type: "crisis", name: "Crisis Call Line", number: "+91-9999666555", available: "24/7" },
    { type: "crisis", name: "iCall India Helpline", number: "+91-9152987821", available: "Mon-Sat, 10AM-8PM" },
  ];
  const sampleMoodTribes = [
    { id: 1, name: "Positivity Tribe", description: "For those seeking positivity and joy", member_count: 245, isMember: false },
    { id: 2, name: "Calm Collective", description: "Find your inner peace together", member_count: 189, isMember: false },
    { id: 3, name: "Anxiety Allies", description: "Support for those dealing with anxiety", member_count: 312, isMember: false },
  ];
  const sampleShopItems = [
    { id: 1, name: "Sunset Theme", description: "Warm orange and purple theme", price: 50, category: "theme", permanent: true },
    { id: 2, name: "Ocean Theme", description: "Calming blue theme", price: 50, category: "theme", permanent: true },
    { id: 3, name: "Forest Theme", description: "Green nature-inspired theme", price: 50, category: "theme", permanent: true },
    { id: 6, name: "Exclusive Avatar", description: "Special avatar frame", price: 100, category: "cosmetic", permanent: true },
    { id: 7, name: "Meditation Pack", description: "20 guided meditation sessions", price: 75, category: "content", permanent: true, content: "Includes:\n- 5 breathing exercises\n- 5 mindfulness meditations\n- 5 sleep meditations\n- 5 stress relief sessions" },
    { id: 8, name: "Second Chance Token", description: "Save your streak if you miss a day", price: 30, category: "utility", permanent: false },
    { id: 9, name: "Sleep Stories Pack", description: "10 bedtime stories for better sleep", price: 60, category: "content", permanent: true, content: "Includes:\n- Beach Sunset (12 min)\n- Mountain Cabin (15 min)\n- Starry Night (10 min)\n- Forest Walk (18 min)\n- And 6 more calming stories" },
    { id: 11, name: "Astronaut Avatar", description: "Space-themed avatar", price: 120, category: "avatar", permanent: true },
  ];
  const sampleAchievements = [
    { id: 1, name: "First Steps", description: "Complete your first journal entry", earned: false, icon: "ðŸ“", coins: 10 },
    { id: 2, name: "7-Day Warrior", description: "Maintain a 7-day streak", earned: false, icon: "ðŸ”¥", coins: 25 },
    { id: 3, name: "Community Builder", description: "Join your first tribe", earned: false, icon: "ðŸ‘¥", coins: 15 },
    { id: 5, name: "Therapy Explorer", description: "Book your first therapy session", earned: false, icon: "ðŸ‘¨â€âš•ï¸", coins: 25 },
  ];
  const meditationExercises = [
    { id: 1, title: "4-7-8 Breathing", duration: "5 min", description: "Inhale for 4, hold for 7, exhale for 8", steps: ["Sit comfortably", "Inhale through nose for 4 seconds", "Hold breath for 7 seconds", "Exhale through mouth for 8 seconds", "Repeat 5 times"], image: "ðŸŒ¬ï¸", breathingTimes: [4, 7, 8] },
    { id: 2, title: "Box Breathing", duration: "4 min", description: "4x4 square breathing technique", steps: ["Inhale for 4 seconds", "Hold for 4 seconds", "Exhale for 4 seconds", "Hold for 4 seconds", "Repeat 5-10 times"], image: "ðŸ§Š", breathingTimes: [4, 4, 4, 4] },
  ];
  const moodGames = [
    { id: 1, title: "Bubble Pop", description: "Click/tap on bubbles to pop them", icon: "ðŸ«§" },
    { id: 2, title: "Mindful Coloring", description: "Digital coloring book with patterns", icon: "ðŸŽ¨" },
    { id: 3, title: "Breathing Guide", description: "Interactive visual breathing guide", icon: "ðŸŒ¬ï¸" },
    { id: 4, title: "Stress Balloon", description: "Deflate a balloon to release stress", icon: "ðŸŽˆ" },
    { id: 5, title: "Gratitude Wheel", description: "Spin for random gratitude prompts", icon: "ðŸŽ¡" }
  ];
  const moodChallenges = [
    { id: 1, title: "7 Days of Gratitude", duration: "7 days", description: "Write one thing you're grateful for each day", reward: "Gratitude Badge + 30 coins", progress: 0 },
    { id: 2, title: "Mindful Mornings", duration: "5 days", description: "Start each day with 5 minutes of meditation", reward: "Morning Person Badge + 20 coins", progress: 0 },
  ];
  const sleepStories = [
    { id: 1, title: "Beach Sunset", duration: "12 min", description: "Visualize a peaceful beach at sunset" },
    { id: 2, title: "Mountain Cabin", duration: "15 min", description: "Imagine a cozy cabin in the mountains" },
  ];
  const aiCoachPlans = [
    { time: "Morning", activity: "Affirmation", description: "Start your day with a positive affirmation", duration: "2 min", icon: "ðŸŒž" },
    { time: "Midday", activity: "Check-in", description: "How are you feeling right now?", duration: "3 min", icon: "ðŸ’¬" },
    { time: "Evening", activity: "Reflection", description: "Reflect on your day and things you're grateful for", duration: "5 min", icon: "ðŸŒ™" },
  ];
  const coloringImages = [
    { id: 1, name: "Mandala", complexity: "Medium", colors: ["#FF6B6B", "#4ECDC4", "#FFE66D", "#1A535C", "#F7FFF7"] },
  ];
  const gratitudePrompts = [
    "What made you smile today?",
    "Who are you grateful for in your life?",
    "What about nature are you grateful for?",
  ];
  const motivationalQuotes = [
    "Believe you can and you're halfway there.",
    "The only way to do great work is to love what you do.",
    "The future belongs to those who believe in the beauty of their dreams.",
    "It does not matter how slowly you go as long as you do not stop.",
  ];

  const [userData, setUserData] = useState(
    user || {
      id: 1,
      name: "Demo User",
      coins: 100,
      streak: 0,
      mentalFitness: 85,
      purchasedItems: [],
      ownedThemes: ["default"],
      currentTheme: "default",
      avatar: "default",
      achievements: [],
      loginStreak: 0,
      lastLogin: new Date().toISOString().split('T')[0]
    }
  );

  useEffect(() => {
    setMessages(currentChat?.messages || []);
  }, [currentChat]);

  useEffect(() => {
    if (currentChat && messages.length > 0) {
      const updatedChat = {
        ...currentChat,
        messages: messages,
        preview: messages.length > 0 ? messages[messages.length - 1].text.substring(0, 50) + "..." : "New chat",
        timestamp: new Date().toISOString()
      };
      
      if (currentChat.title === "New Chat" && messages.length === 1 && messages[0].sender === "user") {
        updatedChat.title = messages[0].text.substring(0, 30) + (messages[0].text.length > 30 ? "..." : "");
      }
      
      onUpdateChat && onUpdateChat(updatedChat);
    }
  }, [messages, currentChat, onUpdateChat]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  useEffect(() => {
    if (tribeChatEndRef.current && activeTribe) {
      tribeChatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [tribeChatMessages, activeTribe]);

  useEffect(() => {
    return () => {
      if (breathingTimerRef.current) {
        clearInterval(breathingTimerRef.current);
      }
      if (gameIntervalRef.current) {
        clearInterval(gameIntervalRef.current);
      }
      if (exerciseTimerRef.current) {
        clearInterval(exerciseTimerRef.current);
      }
    };
  }, []);

  useEffect(() => {
    loadInitialData();
    checkDailyLoginBonus();
  }, []);

  const checkDailyLoginBonus = () => {
    const today = new Date().toISOString().split('T')[0];
    if (userData.lastLogin !== today) {
      const streak = (userData.loginStreak || 0) + 1;
      const coinsEarned = Math.min(streak, 7) * 5;
      setUserData(prev => ({
        ...prev,
        loginStreak: streak,
        coins: (prev.coins || 0) + coinsEarned,
        lastLogin: today
      }));
      setShowLoginBonus(true);
      setTimeout(() => setShowLoginBonus(false), 3000);
    }
  };

  const loadInitialData = () => {
    if (messages.length === 0) {
      setMessages([{
        id: Date.now(),
        text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
        sender: "bot",
        timestamp: new Date().toISOString()
      }]);
    }
    setTherapists(sampleTherapists);
    setResources(sampleResources);
    const userTribeIds = userTribes.map(t => t.id);
    const updatedTribes = sampleMoodTribes.map(tribe => ({
      ...tribe,
      isMember: userTribeIds.includes(tribe.id)
    }));
    setMoodTribes(updatedTribes);
    setShopItems(sampleShopItems);
    const sampleEntries = [
      { id: 1, text: "Today was a good day. I felt productive and happy.", summary: "Positive day with productivity", mood: "happy", date: new Date().toISOString().split('T')[0], shared: false },
      { id: 2, text: "Feeling a bit anxious about the upcoming presentation. Need to practice more.", summary: "Anxious about presentation", mood: "anxious", date: "2023-10-24", shared: true },
    ];
    setJournalEntries(sampleEntries);
    const initialTribeChats = {};
    sampleMoodTribes.forEach(tribe => {
      initialTribeChats[tribe.id] = [
        { id: 1, message: "Hello everyone! How are you all feeling today?", user_name: "Anonymous", timestamp: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString() },
      ];
    });
    setTribeChatMessages(initialTribeChats);
    setAchievements(sampleAchievements);
    setAiCoachPlan(aiCoachPlans);
  };

  const simulateTyping = async () => {
    const typingPhrases = ["Thinking", "Thinking.", "Thinking..", "Thinking..."];
    for (let i = 0; i < 6; i++) {
      setTypingIndicator(typingPhrases[i % 4]);
      await new Promise(resolve => setTimeout(resolve, 300));
    }
    setTypingIndicator("");
  };

  const getMoodFromText = (text) => {
    const lowerText = text.toLowerCase();
    if (lowerText.includes('happy') || lowerText.includes('good') || lowerText.includes('great') || lowerText.includes('joy') || lowerText.includes('calm') || lowerText.includes('peaceful')) {
      return { mood: 'happy', emoji: 'ðŸ˜Š' };
    }
    if (lowerText.includes('anxious') || lowerText.includes('nervous') || lowerText.includes('stressed') || lowerText.includes('worried') || lowerText.includes('depressed')) {
      return { mood: 'anxious', emoji: 'ðŸ˜°' };
    }
    if (lowerText.includes('sad') || lowerText.includes('unhappy') || lowerText.includes('low')) {
      return { mood: 'sad', emoji: 'ðŸ˜”' };
    }
    return { mood: 'neutral', emoji: 'ðŸ˜' };
  };

  const sendMessage = async () => {
    if (!input.trim()) return;

    let chat = currentChat;
    if (!chat && onNewChat) {
      chat = onNewChat();
    }

    const userMsg = {
      id: Date.now(),
      text: input,
      sender: "user",
      timestamp: new Date().toISOString()
    };
    
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);
    
    const emergencyKeywords = ['suicide', 'kill myself', 'end it all', 'want to die', 'self harm', 'hurt myself'];
    const hasEmergency = emergencyKeywords.some(keyword => userMsg.text.toLowerCase().includes(keyword));
    
    if (hasEmergency) {
      setShowEmergencyModal(true);
      setIsTyping(false);
      return;
    }
    
    await simulateTyping();

    try {
      const response = await fetch(`${API_BASE_URL}/api/chat`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: userMsg.text, session_id: userData.id || 'default_session' })
      });

      const data = await response.json();

      if (!response.ok) {
        if (data.error === "gemini_unavailable") {
            alert("AI service unavailable, please try again later.");
            setIsTyping(false);
            return;
        }
        throw new Error(data.error || "API error");
      }
      
      const botMsg = {
        id: Date.now(),
        text: data.reply,
        sender: "bot",
        mood: data.mood,
        moodEmoji: data.moodEmoji,
        phrase: data.phrase,
        audioUrl: data.audioUrl,
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, botMsg]);

      if (data.audioUrl && audioEnabled) {
        const audio = new Audio(`${API_BASE_URL}${data.audioUrl}`);
        audio.play().catch(e => console.error("Audio playback failed:", e));
      }

    } catch (error) {
      console.error("Chat error:", error);
      const botMsg = {
        id: Date.now(),
        text: "I'm here to listen. How does that make you feel?",
        sender: "bot",
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, botMsg]);
    }
    setIsTyping(false);
  };

  const startBreathingExercise = (exerciseId = 1) => {
    setShowMeditationGuide(false);
    setBreathingActive(true);
    const exercise = meditationExercises.find(e => e.id === exerciseId) || meditationExercises[0];
    setActiveMeditation(exercise);
    
    const durationMinutes = parseInt(exercise.duration);
    setExerciseDuration(durationMinutes * 60);
    setExerciseTimer(durationMinutes * 60);
    const breathingCycle = exercise.breathingTimes;
    let cycleIndex = 0;
    
    const animateBreathing = () => {
      setBreathingPhase(cycleIndex % 2 === 0 ? "inhale" : "exhale");
      setBreathingPhaseDuration(breathingCycle[cycleIndex] * 1000);
      setBreathingProgress(0);
      
      let progressInterval = setInterval(() => {
        setBreathingProgress(prev => {
          if (prev >= 100) {
            clearInterval(progressInterval);
            cycleIndex = (cycleIndex + 1) % breathingCycle.length;
            if(breathingActive) animateBreathing();
            return 0;
          }
          return prev + 1;
        });
      }, (breathingCycle[cycleIndex] * 1000) / 100);
    };

    animateBreathing();
    
    exerciseTimerRef.current = setInterval(() => {
      setExerciseTimer(prev => {
        if (prev <= 1) {
          clearInterval(exerciseTimerRef.current);
          stopBreathingExercise();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    
    setMessages(prev => [...prev, {
      id: Date.now(),
      text: `Let's do the ${exercise.title} exercise. I'll guide you...`,
      sender: "bot",
      isExercise: true,
      timestamp: new Date().toISOString()
    }]);
  };

  const stopBreathingExercise = () => {
    setBreathingActive(false);
    if (breathingTimerRef.current) {
      clearInterval(breathingTimerRef.current);
    }
    if (exerciseTimerRef.current) {
      clearInterval(exerciseTimerRef.current);
    }
    setMessages(prev => [...prev, {
      id: Date.now(),
      text: "âœ… Exercise complete! How do you feel now?",
      sender: "bot",
      timestamp: new Date().toISOString()
    }]);
  };

  const addJournalEntry = () => {
    const entryText = prompt("What's on your mind today?");
    if (!entryText) return;

    const moodResult = getMoodFromText(entryText);
    const newEntry = {
      id: Date.now(),
      text: entryText,
      summary: "Personal reflection",
      mood: moodResult.mood,
      date: new Date().toISOString().split('T')[0],
      shared: false
    };
    
    setJournalEntries(prev => [newEntry, ...prev]);
    setLastMessageMood(newEntry.mood);
    alert("Journal entry saved!");
  };

  const shareJournalEntry = (entryId) => {
    setJournalEntries(journalEntries.map(e => e.id === entryId ? { ...e, shared: true } : e));
    alert("Journal entry shared to your tribes!");
  };

  const joinTribe = (tribeId) => {
    const tribe = moodTribes.find(t => t.id === tribeId);
    if (tribe) {
      const isAlreadyMember = userTribes.some(t => t.id === tribeId);
      if (isAlreadyMember) return;

      const updatedTribes = moodTribes.map(t =>
        t.id === tribeId ? { ...t, isMember: true, member_count: t.member_count + 1 } : t
      );
      setMoodTribes(updatedTribes);
      setUserTribes(prev => [...prev, tribe]);
      
      const communityAchievement = achievements.find(a => a.id === 3);
      if (communityAchievement && !communityAchievement.earned) {
        setAchievements(prev => prev.map(a => a.id === 3 ? { ...a, earned: true } : a));
        alert("ðŸ† Community Builder achievement unlocked!");
      }
      
      alert(`ðŸŽ‰ You've joined the ${tribe.name}!`);
    }
  };

  const sendTribeMessage = (tribeId) => {
    if (!tribeInput.trim()) return;
    const newMessage = {
      id: Date.now(),
      message: tribeInput,
      user_name: "You",
      timestamp: new Date().toISOString()
    };
    
    setTribeChatMessages(prev => ({
      ...prev,
      [tribeId]: [...(prev[tribeId] || []), newMessage]
    }));
    setTribeInput("");
  };

  const buyItem = (item) => {
    if (userData.coins < item.price) {
      alert("Not enough coins!");
      return;
    }
    
    if (window.confirm(`Buy ${item.name} for ${item.price} coins?`)) {
      setUserData(prev => ({
        ...prev,
        coins: (prev.coins || 0) - item.price,
        purchasedItems: [...(prev.purchasedItems || []), item.id],
        ownedThemes: item.category === "theme" ? [...(prev.ownedThemes || []), item.name.toLowerCase().replace(' theme', '')] : prev.ownedThemes,
      }));
      alert(`Purchased ${item.name}!`);
    }
  };

