        <button className={activeTab === "therapy" ? "active" : ""} onClick={() => setActiveTab("therapy")}>ðŸ‘¨â€âš•ï¸ Therapy</button>
        <button className={activeTab === "community" ? "active" : ""} onClick={() => setActiveTab("community")}>ðŸŒ Community</button>
