                    <div key={challenge.id} className="challenge-card">
                      <h4>{challenge.title}</h4>
                      <p className="challenge-duration">{challenge.duration}</p>
                      <p>{challenge.description}</p>
                      <div className="challenge-reward">{challenge.reward}</div>
