                              >
                                {userData.currentTheme === item.name.toLowerCase().replace(' theme', '') ? "Applied" : "Apply"}
                              </button>
                            )}
                            {item.category === "avatar" && (
                              <button
                                onClick={() => applyAvatar(item.name)}
                                className={userData.avatar === item.name ? "applied-btn" : "apply-btn"}
                              >
                                {userData.avatar === item.name ? "Applied" : "Apply"}
                              </button>
                            )}
                            {(item.category !== "theme" && item.category !== "avatar") && (
                              <button className="owned-btn">Owned</button>
                            )}
                          </>
                        ) : (
