}

const TribeCommunity = ({ activeTribe, setActiveTribe, tribeChatMessages, tribeInput, setTribeInput, sendTribeMessage, tribeChatEndRef }) => {
  if (!activeTribe) return null;
  const tribeChat = tribeChatMessages[activeTribe.id] || [];
  return (
    <div className="tribe-community">
      <div className="tribe-header">
        <button onClick={() => setActiveTribe(null)} className="back-btn">â† Back to Communities</button>
        <h2>{activeTibe.name} Community</h2>
      </div>
      <div className="tribe-content">
        <div className="tribe-chat">
          <div className="tribe-messages">
            {tribeChat.map(msg => (
              <div key={msg.id} className="tribe-message">
                <div className="message-avatar">{msg.user_name.charAt(0)}</div>
                <div className="message-content">
                  <span className="message-sender">{msg.user_name}</span>
                  <p>{msg.message}</p>
                </div>
              </div>
            ))}
            <div ref={tribeChatEndRef}></div>
          </div>
          <div className="tribe-input">
            <input type="text" placeholder="Share something with the community..." value={tribeInput} onChange={e => setTribeInput(e.target.value)} onKeyDown={e => e.key === "Enter" && sendTribeMessage(activeTribe.id)} />
            <button onClick={() => sendTribeMessage(activeTribe.id)}>Send</button>
          </div>
        </div>
      </div>
    </div>
  );
};

const BubbleGame = ({ bubbleGame, popBubble, exitGame }) => (
  <div className="game-container">
    <div className="game-header"><h2>Bubble Pop</h2><button onClick={exitGame} className="exit-game-btn">Ã—</button></div>
    <div className="game-score">Score: {bubbleGame.score}</div>
    <div className="bubble-game-area">
      {bubbleGame.bubbles.map(bubble => !bubble.popped && (
        <div key={bubble.id} className="bubble" style={{ left: `${bubble.x}%`, top: `${bubble.y}%`, width: `${bubble.size}px`, height: `${bubble.size}px` }} onClick={() => popBubble(bubble.id)}></div>
      ))}
    </div>
    <div className="game-instructions">Click on the bubbles to pop them!</div>
  </div>
);

const ColoringGame = ({ coloringImage, selectedColor, setSelectedColor, exitGame }) => (
  <div className="game-container">
    <div className="game-header"><h2>Mindful Coloring</h2><button onClick={exitGame} className="exit-game-btn">Ã—</button></div>
    <div className="coloring-game-area">
      {coloringImage && (
        <>
          <div className="coloring-canvas"><div className="coloring-pattern"></div></div>
          <div className="color-palette">
            {coloringImage.colors.map(color => (
              <div key={color} className={`color-option ${selectedColor === color ? 'selected' : ''}`} style={{ backgroundColor: color }} onClick={() => setSelectedColor(color)}></div>
            ))}
          </div>
          <div className="coloring-instructions"><p>Choose a color and click to fill areas</p></div>
        </>
      )}
    </div>
  </div>
);

const StressBalloonGame = ({ stressBalloon, inflateBalloon, releaseBalloon, exitGame }) => (
  <div className="game-container">
    <div className="game-header"><h2>Stress Balloon</h2><button onClick={exitGame} className="exit-game-btn">Ã—</button></div>
    <div className="balloon-game-area">
      <div className="balloon" style={{ transform: `scale(${1 + stressBalloon.progress / 100})`, opacity: 1 - (stressBalloon.progress / 200) }} onMouseDown={inflateBalloon} onMouseUp={releaseBalloon}></div>
      <div className="balloon-instructions">{stressBalloon.progress < 100 ? (<p>Press and hold to inflate the balloon</p>) : (<p>Release to pop the balloon and release stress</p>)}</div>
    </div>
  </div>
);

const GratitudeWheelGame = ({ gratitudeWheel, startGratitudeWheel, exitGame }) => (
  <div className="game-container">
    <div className="game-header"><h2>Gratitude Wheel</h2><button onClick={exitGame} className="exit-game-btn">Ã—</button></div>
    <div className="wheel-game-area">
      <div className={`gratitude-wheel ${gratitudeWheel.spinning ? 'spinning' : ''}`}></div>
      {gratitudeWheel.result && (<div className="wheel-result"><h3>Today's Prompt:</h3><p>{gratitudeWheel.result}</p></div>)}
      <button onClick={startGratitudeWheel} disabled={gratitudeWheel.spinning} className="spin-btn">{gratitudeWheel.spinning ? 'Spinning...' : 'Spin Wheel'}</button>
    </div>
  </div>
);

const MeditationGuideModal = ({ showMeditationGuide, setShowMeditationGuide, startBreathingExercise, meditationExercises }) => {
  if (!showMeditationGuide) return null;
  return (
    <div className="modal-overlay">
      <div className="modal-content large">
        <div className="modal-header">
          <h2>ðŸ§˜ Meditation & Breathing Exercises</h2>
          <button onClick={() => setShowMeditationGuide(false)} className="close-btn">Ã—</button>
        </div>
        <div className="meditation-grid">
          {meditationExercises.map(exercise => (
            <div key={exercise.id} className="meditation-card">
              <h3>{exercise.title}</h3>
              <p className="duration">{exercise.duration}</p>
              <p className="description">{exercise.description}</p>
              <button onClick={() => { setShowMeditationGuide(false); startBreathingExercise(exercise.id); }} className="start-btn">Start Exercise</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

const ChallengeModal = ({ showChallengeModal, setShowChallengeModal, activeChallenge, updateChallengeProgress }) => {
  if (!showChallengeModal || !activeChallenge) return null;
  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>ðŸ† {activeChallenge.title}</h2>
          <button onClick={() => setShowChallengeModal(false)} className="close-btn">Ã—</button>
        </div>
        <div className="challenge-details">
          <p className="challenge-description">{activeChallenge.description}</p>
          <div className="challenge-progress">
            <h4>Progress: {activeChallenge.progress}%</h4>
            <div className="progress-bar">
              <div className="progress-fill" style={{ width: `${activeChallenge.progress}%` }}></div>
            </div>
          </div>
          <button onClick={updateChallengeProgress} className="btn-primary">
            {activeChallenge.progress > 0 ? "Update Progress" : "Start Challenge"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default App;
