import React, { useState, useEffect, useRef } from 'react';
import QuickActionsMenu from "./QuickActions"; // Make sure to import it

const API_BASE_URL = 'http://localhost:5000';

function Chat({ user }) {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const [isTyping, setIsTyping] = useState(false);
    const chatEndRef = useRef(null);

    useEffect(() => {
        if (user && user.username) {
            setMessages([{
                id: Date.now(),
                text: `Hello ${user.username}! I'm MoodMate. How can I help you today?`,
                sender: 'bot'
            }]);
        }
    }, [user]);

    useEffect(() => {
        chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    const sendMessage = async () => {
        // ... your sendMessage function remains the same
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                {/* FIX: This placeholder now disappears after the conversation starts */}
                {messages.length <= 1 && (
                    <div className="chat-placeholder">
                        <div className="placeholder-icon">ðŸŒ™</div>
                        <h2>MoodMate</h2>
                        <p>Your AI wellness assistant, online and ready to listen.</p>
                    </div>
                )}
                
                {messages.map((msg) => (
                    <div key={msg.id} className={`chat-message ${msg.sender}`}>
                        <div className="bubble">{msg.text}</div>
                    </div>
                ))}
                {isTyping && (
                    <div className="chat-message bot typing">
                        <div className="bubble"><span>.</span><span>.</span><span>.</span></div>
                    </div>
                )}
                <div ref={chatEndRef} />
            </div>

            {/* FIX: Quick Actions are now here, part of the chat interface */}
            <QuickActionsMenu 
                // Pass any necessary props for opening modals
            />

            <div className="input-area">
                <button className="icon-btn">ðŸ˜ƒ</button>
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                    placeholder="Type your message here..."
                />
                <button className="icon-btn">ðŸŽ¤</button>
                <button onClick={sendMessage} disabled={isTyping} className="send-btn">
                    {isTyping ? '...' : 'âž¤'}
                </button>
            </div>
        </div>
    );
}

export default Chat;
