import React, { useState, useEffect, useRef } from "react";
import "./app.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLogin, setShowLogin] = useState(true);
  const [showRegister, setShowRegister] = useState(false);
  const [activeTab, setActiveTab] = useState("chat");
  const [therapists, setTherapists] = useState([]);
  const [resources, setResources] = useState([]);
  const [storeItems, setStoreItems] = useState([]);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [showCrisis, setShowCrisis] = useState(false);

  const chatEndRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    if (token && userData) {
      setUser(JSON.parse(userData));
      setIsLoggedIn(true);
      setShowLogin(false);
      loadInitialData();
    }
  }, []);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  useEffect(() => {
    document.body.classList.toggle("dark-mode", isDarkMode);
  }, [isDarkMode]);

  const loadInitialData = () => {
    setMessages([{
      text: "Hello! I'm MoodMate. How are you feeling today?",
      sender: "bot"
    }]);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const credentials = { email: formData.get('email'), password: formData.get('password') };
    try {
      const response = await fetch("http://localhost:5000/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(credentials)
      });
      const data = await response.json();
      if (data.success) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        setUser(data.user);
        setIsLoggedIn(true);
        setShowLogin(false);
        loadInitialData();
      } else alert(data.error || "Login failed");
    } catch { alert("Login failed. Check server."); }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const userData = { name: formData.get('name'), email: formData.get('email'), password: formData.get('password') };
    try {
      const response = await fetch("http://localhost:5000/api/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(userData)
      });
      const data = await response.json();
      if (data.success) {
        alert("Registered! Login now.");
        setShowRegister(false);
        setShowLogin(true);
      } else alert(data.error || "Registration failed");
    } catch { alert("Registration failed."); }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setIsLoggedIn(false);
    setShowLogin(true);
    setMessages([]);
  };

  const sendMessage = async () => {
    if (!input.trim() || !isLoggedIn) return;
    const userMsg = { text: input, sender: "user" };
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);
    try {
      const response = await fetch("http://localhost:5000/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: input, email: user.email })
      });
      const data = await response.json();
      if (data.success) setMessages(prev => [...prev, { text: data.response, sender: "bot" }]);
      else setMessages(prev => [...prev, { text: data.error, sender: "bot" }]);
    } catch { setMessages(prev => [...prev, { text: "Connection error.", sender: "bot" }]); }
    setIsTyping(false);
  };

  const addJournalEntry = async () => {
    const entryText = prompt("What's on your mind?");
    if (!entryText) return;
    try {
      const response = await fetch("http://localhost:5000/api/journal", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text: entryText, email: user.email })
      });
      const data = await response.json();
      if (data.success) {
        setUser(prev => ({ ...prev, coins: data.totalCoins }));
        alert("Entry saved! +3 coins.");
      }
    } catch { alert("Journal error."); }
  };

  const loadTherapists = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/therapists");
      const data = await response.json();
      setTherapists(data.therapists || []);
      const resResp = await fetch("http://localhost:5000/api/resources");
      const resData = await resResp.json();
      setResources(resData.resources || []);
    } catch (error) { console.error(error); }
  };

  const loadStore = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/store");
      const data = await response.json();
      setStoreItems(data.items || []);
    } catch (error) { console.error(error); }
  };

  const bookTherapist = async (therapistId) => {
    if (!window.confirm("Book?")) return;
    try {
      const response = await fetch("http://localhost:5000/api/therapists/book", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ therapist_id: therapistId, email: user.email })
      });
      const data = await response.json();
      if (data.success) setUser(prev => ({ ...prev, coins: data.remainingCoins }));
      alert(data.message || data.error);
    } catch { alert("Booking error."); }
  };

  const buyItem = async (itemId) => {
    if (!window.confirm("Buy?")) return;
    try {
      const response = await fetch("http://localhost:5000/api/store/buy", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ item_id: itemId, email: user.email })
      });
      const data = await response.json();
      if (data.success) setUser(prev => ({ ...prev, coins: data.remainingCoins, inventory: data.inventory }));
      alert(data.success ? "Purchased!" : data.error);
    } catch { alert("Purchase error."); }
  };

  if (!isLoggedIn) {
    return (
      <div className="app-container auth-container">
        <div className="auth-box">
          <h1>🌙 MoodMate</h1>
          {showLogin && <form onSubmit={handleLogin}><input name="email" type="email" placeholder="Email" required /><input name="password" type="password" placeholder="Password" required /><button>Login</button><p>Don't have an account? <span onClick={() => { setShowLogin(false); setShowRegister(true); }}>Register</span></p></form>}
          {showRegister && <form onSubmit={handleRegister}><input name="name" placeholder="Name" required /><input name="email" type="email" placeholder="Email" required /><input name="password" type="password" placeholder="Password" required /><button>Register</button><p>Already have an account? <span onClick={() => { setShowRegister(false); setShowLogin(true); }}>Login</span></p></form>}
        </div>
      </div>
    );
  }

  return (
    <div className="app-container">
      <header>
        <h1>🌙 MoodMate</h1>
        <div>
          <span>Coins: {user.coins}</span>
          <span>Streak: {user.streak}</span>
          <button onClick={() => setIsDarkMode(!isDarkMode)}>{isDarkMode ? "☀" : "🌙"}</button>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </header>

      <nav>
        <button onClick={() => { setActiveTab("chat"); }}>Chat</button>
        <button onClick={() => { setActiveTab("journal"); }}>Journal</button>
        <button onClick={() => { setActiveTab("therapy"); loadTherapists(); }}>Therapy</button>
        <button onClick={() => { setActiveTab("store"); loadStore(); }}>Store</button>
      </nav>

      <main>
        {activeTab === "chat" && <div>
          {messages.map((m,i)=><p key={i} className={m.sender}>{m.text}</p>)}
          {isTyping && <p>Typing...</p>}
          <input value={input} onChange={e=>setInput(e.target.value)} onKeyDown={e=>e.key==="Enter"&&sendMessage()} placeholder="Type..." />
          <button onClick={sendMessage}>Send</button>
          <div ref={chatEndRef}></div>
        </div>}

        {activeTab==="journal" && <button onClick={addJournalEntry}>+ New Entry</button>}

        {activeTab==="therapy" && <div>
          <h3>Resources</h3>{resources.map((r,i)=><div key={i}>{r.name}: {r.number}</div>)}
          <h3>Therapists</h3>{therapists.map(t=><div key={t.id}><h4>{t.name}</h4><p>{t.specialization} - 🪙 {t.price}</p><button onClick={()=>bookTherapist(t.id)}>Book</button></div>)}
        </div>}

        {activeTab==="store" && <div>
          {storeItems.map(i=><div key={i.id}><h4>{i.name} - 🪙 {i.price}</h4><button onClick={()=>buyItem(i.id)}>Buy</button></div>)}
        </div>}
      </main>
    </div>
  );
}

export default App;
