import React, { useState } from 'react';

// This is a sample plan. In a real app, this might be fetched from the backend.
const initialAiCoachPlan = [
    { id: 1, time: "Morning", activity: "Affirmation", description: "Start your day with a positive affirmation.", duration: "2 min", icon: "ðŸŒž", completed: false },
    { id: 2, time: "Midday", activity: "Mindful Check-in", description: "Take 3 deep breaths and notice how you feel.", duration: "3 min", icon: "ðŸ’¬", completed: false },
    { id: 3, time: "Evening", activity: "Gratitude Reflection", description: "Write down one thing you're grateful for today.", duration: "5 min", icon: "ðŸŒ™", completed: false },
    { id: 4, time: "Anytime", activity: "Hydration Reminder", description: "Drink a full glass of water.", duration: "1 min", icon: "ðŸ’§", completed: false }
];

function AICoachModal({ user, onClose, onUpgradeClick }) {
    
    // If the user is NOT premium, show the upgrade message
    
    // --- Premium Feature Logic ---
    const [plan, setPlan] = useState(initialAiCoachPlan);

    const handleToggleComplete = (taskId) => {
        setPlan(currentPlan => 
            currentPlan.map(task => 
                task.id === taskId ? { ...task, completed: !task.completed } : task
            )
        );
    };

    const completedTasks = plan.filter(task => task.completed).length;
    const progressPercentage = (completedTasks / plan.length) * 100;
    
    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content large" onClick={(e) => e.stopPropagation()}>
                <div className="modal-header">
                    <h2>ðŸ¤– AI Coach - Your Daily Plan</h2>
                    <button onClick={onClose} className="close-btn">Ã—</button>
                </div>
                
                <div className="ai-coach-progress">
                    <h3>Today's Progress</h3>
                    <div className="progress-bar-container">
                        <div className="progress-bar-fill" style={{ width: `${progressPercentage}%` }}></div>
                    </div>
                    <p>{completedTasks} / {plan.length} tasks completed</p>
                </div>

                <div className="ai-coach-plan">
                    {plan.map((item) => (
                        <div key={item.id} className={`coach-item ${item.completed ? 'completed' : ''}`}>
                            <div className="coach-time">{item.time}</div>
                            <div className="coach-icon">{item.icon}</div>
                            <div className="coach-details">
                                <h3>{item.activity}</h3>
                                <p>{item.description}</p>
                                <span className="coach-duration">{item.duration}</span>
                            </div>
                            <button 
                                className={`coach-action-btn ${item.completed ? 'done' : ''}`}
                                onClick={() => handleToggleComplete(item.id)}
                            >
                                {item.completed ? 'âœ“ Done' : 'Start'}
                            </button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default AICoachModal;
