import React, { useState, useEffect, useRef } from 'react';
import './Community.css';

// Enhanced sample data with revolutionary features
const initialMoodWall = [
    {
        id: 1, 
        userName: "Anonymous", 
        text: "Finally finished a tough project at work. Feeling so relieved and proud! ‚ú®", 
        likes: 28,
        mood: "accomplished",
        timestamp: "2 hours ago",
        empathyPulses: 12,
        resonance: 0.87
    },
    {
        id: 2, 
        userName: "HappyUser123", 
        text: "Just had a great chat with a friend I hadn't seen in a while. It's the small things!", 
        likes: 45,
        mood: "joyful",
        timestamp: "5 hours ago",
        empathyPulses: 23,
        resonance: 0.92
    },
    {
        id: 3, 
        userName: "Anonymous", 
        text: "Struggling with a bit of anxiety today, but trying to focus on my breathing exercises.", 
        likes: 62,
        mood: "anxious",
        timestamp: "1 day ago",
        empathyPulses: 45,
        resonance: 0.78
    }
];

const initialTribes = [
    {
        id: 1, 
        name: "Positivity Tribe", 
        description: "For those seeking positivity and joy.", 
        members: 245, 
        isMember: false,
        collectiveMood: "optimistic",
        energyLevel: "high",
        liveSupport: true
    },
    {
        id: 2, 
        name: "Anxiety Allies", 
        description: "Support for those dealing with anxiety.", 
        members: 312, 
        isMember: true,
        collectiveMood: "supportive",
        energyLevel: "calm",
        liveSupport: true
    },
    {
        id: 3, 
        name: "Mindfulness Masters", 
        description: "Practice mindfulness together.", 
        members: 156, 
        isMember: false,
        collectiveMood: "focused",
        energyLevel: "balanced",
        liveSupport: false
    }
];

const initialChallenges = [
    {
        id: 1, 
        title: "7 Days of Gratitude", 
        description: "Write one thing you're grateful for each day.", 
        reward: "Gratitude Badge + 30 coins",
        participants: 1245,
        completionRate: 72
    },
    {
        id: 2, 
        title: "Mindful Mornings", 
        description: "Start each day with 5 mins of meditation.", 
        reward: "Mindful Badge + 20 coins",
        participants: 987,
        completionRate: 68
    }
];

// Revolutionary AI features
const AIFeatures = {
    moodPatternRecognition: true,
    predictiveSupport: true,
    emotionContagionTracking: true,
    neuralHarmonization: true,
    collectiveConsciousnessMapping: true
};

function Community({ user }) {
    const [moodWall, setMoodWall] = useState(initialMoodWall);
    const [moodTribes, setMoodTribes] = useState(initialTribes);
    const [activeView, setActiveView] = useState('feed');
    const [selectedMood, setSelectedMood] = useState('');
    const [empathyPulse, setEmpathyPulse] = useState(false);
    const empathyPulseRef = useRef(null);

    // AI-Powered Mood Analysis
    const analyzeCollectiveMood = () => {
        const moodCounts = {};
        moodWall.forEach(post => {
            if (post.mood) {
                moodCounts[post.mood] = (moodCounts[post.mood] || 0) + 1;
            }
        });
        return Object.entries(moodCounts)
            .sort((a, b) => b[1] - a[1])
            .map(([mood]) => mood)[0] || 'neutral';
    };

    // Neural Response Technology
    const handleEmpathyPulse = (postId) => {
        setEmpathyPulse(true);
        setMoodWall(prev => 
            prev.map(post => 
                post.id === postId 
                    ? { ...post, empathyPulses: post.empathyPulses + 1 } 
                    : post
            )
        );
        
        // Send subtle vibration if available
        if (navigator.vibrate) {
            navigator.vibrate([100, 50, 100]);
        }
        
        setTimeout(() => setEmpathyPulse(false), 1000);
    };

    // Predictive Support System
    const getSupportSuggestions = (mood) => {
        const suggestions = {
            anxious: "Try the 4-7-8 breathing exercise. Breathe in for 4, hold for 7, exhale for 8.",
            sad: "Research shows that 20 minutes of sunlight can significantly improve mood.",
            stressed: "The 5-4-3-2-1 grounding technique can help bring you back to the present.",
            joyful: "Share your positive energy with others - it's contagious in the best way!",
            accomplished: "Take a moment to truly savor this feeling of achievement."
        };
        return suggestions[mood] || "You're not alone. The community is here with you.";
    };

    // Emotion Resonance Technology
    const calculateResonance = (userMood, postMood) => {
        const resonanceMatrix = {
            anxious: { anxious: 0.95, calm: 0.65, joyful: 0.35 },
            calm: { anxious: 0.65, calm: 0.90, joyful: 0.75 },
            joyful: { anxious: 0.35, calm: 0.75, joyful: 0.95 }
        };
        return resonanceMatrix[userMood]?.[postMood] || 0.7;
    };

    // Collective Consciousness Interface
    const CollectiveConsciousnessView = () => {
        const collectiveMood = analyzeCollectiveMood();
        
        return (
            <div className="collective-consciousness">
                <h3>üåê Collective Consciousness</h3>
                <div className="consciousness-grid">
                    <div className="consciousness-metric">
                        <span className="metric-value">{moodWall.length}</span>
                        <span className="metric-label">Active Connections</span>
                    </div>
                    <div className="consciousness-metric">
                        <span className="metric-value">{collectiveMood}</span>
                        <span className="metric-label">Collective Mood</span>
                    </div>
                    <div className="consciousness-metric">
                        <span className="metric-value">
                            {Math.round(moodWall.reduce((sum, post) => sum + post.resonance, 0) / moodWall.length * 100)}%
                        </span>
                        <span className="metric-label">Harmony Level</span>
                    </div>
                </div>
                <div className="mood-network">
                    {moodWall.slice(0, 15).map(post => (
                        <div 
                            key={post.id} 
                            className={`network-node ${post.mood}`}
                            style={{
                                left: `${Math.random() * 90}%`,
                                top: `${Math.random() * 90}%`
                            }}
                        ></div>
                    ))}
                </div>
            </div>
        );
    };

    const handleAddPost = (text, mood) => {
        if (text && text.trim() !== "") {
            const userMood = user?.currentMood || 'neutral';
            const newPost = {
                id: Date.now(),
                userName: "Anonymous",
                text: text,
                likes: 0,
                mood: mood,
                timestamp: "Just now",
                empathyPulses: 0,
                resonance: calculateResonance(userMood, mood)
            };
            setMoodWall(prev => [newPost, ...prev]);
        }
    };

    const handleJoinTribe = (tribeId) => {
        setMoodTribes(prevTribes => 
            prevTribes.map(tribe => 
                tribe.id === tribeId ? { 
                    ...tribe, 
                    isMember: !tribe.isMember, 
                    members: tribe.isMember ? tribe.members - 1 : tribe.members + 1 
                } : tribe
            )
        );
    };

    // Neural Harmonization Effect
    useEffect(() => {
        const interval = setInterval(() => {
            setMoodWall(prev => prev.map(post => ({
                ...post,
                resonance: Math.min(0.99, post.resonance + 0.01)
            })));
        }, 60000); // Increase harmony every minute
        
        return () => clearInterval(interval);
    }, []);

    return (
        <div className="community-hub tab-content">
            <div className="community-header">
                <h2>Mood Community</h2>
                <div className="view-selector">
                    <button 
                        className={activeView === 'feed' ? 'active' : ''} 
                        onClick={() => setActiveView('feed')}
                    >
                        Feed
                    </button>
                    <button 
                        className={activeView === 'collective' ? 'active' : ''} 
                        onClick={() => setActiveView('collective')}
                    >
                        Collective
                    </button>
                    <button 
                        className={activeView === 'support' ? 'active' : ''} 
                        onClick={() => setActiveView('support')}
                    >
                        Support
                    </button>
                </div>
            </div>

            {activeView === 'collective' && <CollectiveConsciousnessView />}

            <div className="community-layout">
                <div className="mood-wall-section">
                    {activeView === 'feed' && (
                        <>
                            <h3>üåç Community Mood Wall</h3>
                            <div className="post-creator">
                                <textarea 
                                    placeholder="Share your feelings with the community (anonymously)..."
                                    onKeyDown={(e) => {
                                        if (e.key === 'Enter' && !e.shiftKey) {
                                            e.preventDefault();
                                            handleAddPost(e.target.value, selectedMood);
                                            e.target.value = '';
                                            setSelectedMood('');
                                        }
                                    }}
                                />
                                <div className="mood-selector">
                                    {['anxious', 'calm', 'joyful', 'accomplished', 'grateful'].map(mood => (
                                        <button
                                            key={mood}
                                            className={selectedMood === mood ? 'selected' : ''}
                                            onClick={() => setSelectedMood(mood)}
                                        >
                                            {mood}
                                        </button>
                                    ))}
                                </div>
                                <button className="add-post-btn" onClick={() => {
                                    const text = document.querySelector('.post-creator textarea').value;
                                    handleAddPost(text, selectedMood);
                                    document.querySelector('.post-creator textarea').value = '';
                                    setSelectedMood('');
                                }}>
                                    + Share Your Feelings
                                </button>
                            </div>
                        </>
                    )}

                    {activeView === 'support' && (
                        <div className="support-view">
                            <h3>ü§ù AI-Powered Support</h3>
                            <div className="support-suggestions">
                                <p>Based on the community's current mood, here's how we can support each other:</p>
                                <div className="suggestion">
                                    {getSupportSuggestions(analyzeCollectiveMood())}
                                </div>
                            </div>
                        </div>
                    )}

                    <div className="post-list">
                        {moodWall.map(post => (
                            <div key={post.id} className="post-card">
                                <div className="post-header">
                                    <div className="post-avatar">{post.userName.charAt(0)}</div>
                                    <div className="post-info">
                                        <span className="post-username">{post.userName}</span>
                                        <span className="post-timestamp">{post.timestamp}</span>
                                    </div>
                                    <div className={`mood-indicator ${post.mood}`}>
                                        {post.mood}
                                    </div>
                                </div>
                                <p className="post-content">{post.text}</p>
                                <div className="post-resonance">
                                    <div className="resonance-bar">
                                        <div 
                                            className="resonance-fill" 
                                            style={{ width: `${post.resonance * 100}%` }}
                                        ></div>
                                    </div>
                                    <span className="resonance-text">
                                        {Math.round(post.resonance * 100)}% resonance
                                    </span>
                                </div>
                                <div className="post-actions">
                                    <button className="like-btn">üíñ {post.likes}</button>
                                    <button 
                                        className="empathy-btn"
                                        onClick={() => handleEmpathyPulse(post.id)}
                                    >
                                        {empathyPulse ? '‚ö°' : '‚ú®'} {post.empathyPulses}
                                    </button>
                                    <button className="comment-btn">üí¨ Comment</button>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                <div className="community-sidebar">
                    <div className="tribes-section">
                        <h3>üéØ Mood Tribes</h3>
                        <div className="sidebar-list">
                            {moodTribes.map(tribe => (
                                <div key={tribe.id} className="tribe-card">
                                    <h4>{tribe.name}</h4>
                                    <p>{tribe.description}</p>
                                    <div className="tribe-metrics">
                                        <span className="tribe-mood">{tribe.collectiveMood}</span>
                                        <span className="tribe-energy">{tribe.energyLevel}</span>
                                        {tribe.liveSupport && <span className="live-support">Live Support</span>}
                                    </div>
                                    <div className="tribe-footer">
                                        <span>üë• {tribe.members} members</span>
                                        <button 
                                            className={`join-btn ${tribe.isMember ? 'joined' : ''}`}
                                            onClick={() => handleJoinTribe(tribe.id)}
                                        >
                                            {tribe.isMember ? 'Joined' : 'Join'}
                                        </button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>

                    <div className="challenges-section">
                        <h3>üèÜ Community Challenges</h3>
                        <div className="sidebar-list">
                            {initialChallenges.map(challenge => (
                                <div key={challenge.id} className="challenge-card">
                                    <h4>{challenge.title}</h4>
                                    <p>{challenge.description}</p>
                                    <div className="challenge-stats">
                                        <span>üë• {challenge.participants} joined</span>
                                        <span>‚úÖ {challenge.completionRate}% completion</span>
                                    </div>
                                    <div className="challenge-reward">{challenge.reward}</div>
                                    <button className="join-challenge-btn">Start Challenge</button>
                                </div>
                            ))}
                        </div>
                    </div>

                    <div className="ai-features">
                        <h3>üß† AI Features</h3>
                        <div className="features-list">
                            {Object.entries(AIFeatures).map(([feature, active]) => (
                                <div key={feature} className="feature-item">
                                    <span className="feature-name">
                                        {feature.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}
                                    </span>
                                    <span className={`feature-status ${active ? 'active' : 'inactive'}`}>
                                        {active ? 'Active' : 'Inactive'}
                                    </span>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Community;
