import React, { useState, useEffect, useRef } from "react";
import "./app.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLogin, setShowLogin] = useState(true);
  const [showRegister, setShowRegister] = useState(false);
  const [activeTab, setActiveTab] = useState("chat");
  const [journalEntries, setJournalEntries] = useState([]);
  const [therapists, setTherapists] = useState([]);
  const [resources, setResources] = useState([]);
  const [moodWall, setMoodWall] = useState([]);
  const [moodTribes, setMoodTribes] = useState([]);
  const [shopItems, setShopItems] = useState([]);
  const [currentTheme, setCurrentTheme] = useState("default");
  const [typingIndicator, setTypingIndicator] = useState("");

  const chatEndRef = useRef(null);

  // Sample data for when API is not available
  const sampleTherapists = [
    {id: 1, name: "Dr. Priya Sharma", specialization: "Clinical Psychology", price: 799, rating: 4.7, experience: "8 years", avatar: "👩‍⚕️"},
    {id: 2, name: "Rajiv Mehta", specialization: "Counseling Psychology", price: 599, rating: 4.5, experience: "5 years", avatar: "👨‍⚕️"},
    {id: 3, name: "Dr. Anjali Singh", specialization: "Psychiatry", price: 999, rating: 4.8, experience: "12 years", avatar: "👩‍⚕️"},
    {id: 4, name: "Neha Gupta", specialization: "Art Therapy", price: 499, rating: 4.6, experience: "4 years", avatar: "🎨"}
  ];

  const sampleResources = [
    {type: "crisis", name: "National Suicide Prevention Lifeline", number: "1-800-273-8255", available: "24/7"},
    {type: "crisis", name: "Crisis Text Line", number: "Text HOME to 741741", available: "24/7"},
    {type: "support", name: "Mental Health America", website: "mhanational.org", description: "Resources and screening tools"}
  ];

  const sampleMoodTribes = [
    {id: 1, name: "Positivity Tribe", description: "For those seeking positivity and joy", member_count: 245},
    {id: 2, name: "Calm Collective", description: "Find your inner peace together", member_count: 189},
    {id: 3, name: "Anxiety Allies", description: "Support for those dealing with anxiety", member_count: 312},
    {id: 4, name: "Mindfulness Masters", description: "Practice mindfulness together", member_count: 156}
  ];

  const sampleShopItems = [
    {id: 1, name: "Sunset Theme", description: "Warm orange and purple theme", price: 50, category: "theme"},
    {id: 2, name: "Ocean Theme", description: "Calming blue theme", price: 50, category: "theme"},
    {id: 3, name: "Forest Theme", description: "Green nature-inspired theme", price: 50, category: "theme"},
    {id: 4, name: "Exclusive Avatar", description: "Special avatar frame", price: 100, category: "cosmetic"},
    {id: 5, name: "Meditation Pack", description: "Exclusive meditation guides", price: 75, category: "content"}
  ];

  // Load saved session
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    const savedTheme = localStorage.getItem('theme') || 'default';
    
    if (token && userData) {
      setUser(JSON.parse(userData));
      setIsLoggedIn(true);
      setShowLogin(false);
      setCurrentTheme(savedTheme);
      loadInitialData();
    }
  }, []);

  // Auto scroll
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const loadInitialData = async () => {
    setMessages([{
      text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
      sender: "bot"
    }]);
    
    // Load sample data
    setTherapists(sampleTherapists);
    setResources(sampleResources);
    setMoodTribes(sampleMoodTribes);
    setShopItems(sampleShopItems);
    
    // Load journal entries if user is logged in
    if (isLoggedIn) {
      await loadJournalEntries();
    }
  };

  const loadJournalEntries = async () => {
    try {
      // In a real app, you would fetch from your API
      // For demo purposes, we'll use sample data
      const sampleEntries = [
        {id: 1, text: "Today was a good day. I felt productive and happy.", summary: "Positive day with productivity", mood: "happy", date: "2023-10-25"},
        {id: 2, text: "Feeling a bit anxious about the upcoming presentation. Need to practice more.", summary: "Anxious about presentation", mood: "anxious", date: "2023-10-24"},
        {id: 3, text: "Had a great conversation with my friend. It felt good to open up.", summary: "Positive social interaction", mood: "happy", date: "2023-10-23"}
      ];
      setJournalEntries(sampleEntries);
    } catch (error) {
      console.error("Error loading journal entries:", error);
    }
  };

  // Simulate typing animation
  const simulateTyping = async () => {
    const typingPhrases = ["Thinking", "Thinking.", "Thinking..", "Thinking..."];
    for (let i = 0; i < 6; i++) {
      setTypingIndicator(typingPhrases[i % 4]);
      await new Promise(resolve => setTimeout(resolve, 300));
    }
    setTypingIndicator("");
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const credentials = {
      email: formData.get('email'),
      password: formData.get('password')
    };

    try {
      // For demo purposes, we'll simulate a successful login
      const demoUser = {
        email: credentials.email,
        name: credentials.email.split('@')[0],
        coins: 100,
        streak: 3
      };
      
      localStorage.setItem('token', 'demo-token');
      localStorage.setItem('user', JSON.stringify(demoUser));
      setUser(demoUser);
      setIsLoggedIn(true);
      setShowLogin(false);
      loadInitialData();
    } catch (error) {
      console.error("Login error:", error);
      alert("Login failed. Please try again.");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const userData = {
      email: formData.get('email'),
      password: formData.get('password'),
      name: formData.get('name')
    };

    try {
      // For demo purposes, we'll simulate a successful registration
      const demoUser = {
        email: userData.email,
        name: userData.name,
        coins: 20,
        streak: 0
      };
      
      alert("Registration successful! Please login.");
      setShowRegister(false);
      setShowLogin(true);
      e.target.reset();
    } catch (error) {
      console.error("Registration error:", error);
      alert("Registration failed. Please try again.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setIsLoggedIn(false);
    setShowLogin(true);
    setMessages([]);
  };

  const sendMessage = async () => {
    if (!input.trim() || !isLoggedIn) return;

    const userMsg = { text: input, sender: "user" };
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);
    
    // Simulate typing animation
    simulateTyping();

    try {
      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      // Generate contextually appropriate responses
      const userMessage = input.toLowerCase();
      let botResponse = "";
      let mood = "neutral";
      let moodEmoji = "😊";
      let phrase = "You're doing great. Keep going!";
      
      // Contextual response logic
      if (userMessage.includes('sad') || userMessage.includes('unhappy') || userMessage.includes('depressed')) {
        botResponse = "I'm sorry to hear you're feeling down. It's okay to feel this way. Would you like to talk about what's making you feel sad?";
        mood = "sad";
        moodEmoji = "😢";
        phrase = "This feeling will pass. Be gentle with yourself.";
      } 
      else if (userMessage.includes('anxious') || userMessage.includes('nervous') || userMessage.includes('worried')) {
        botResponse = "It sounds like you're feeling anxious. Let's try a quick breathing exercise together. Breathe in for 4 seconds, hold for 4, and exhale for 6. Would you like to try it?";
        mood = "anxious";
        moodEmoji = "😰";
        phrase = "Take deep breaths. This moment will pass.";
      }
      else if (userMessage.includes('happy') || userMessage.includes('good') || userMessage.includes('great')) {
        botResponse = "That's wonderful to hear! I'm glad you're feeling good today. What's contributing to your positive mood?";
        mood = "happy";
        moodEmoji = "😊";
        phrase = "Your joy brightens the world around you.";
      }
      else if (userMessage.includes('angry') || userMessage.includes('frustrated') || userMessage.includes('mad')) {
        botResponse = "I hear that you're feeling angry. It's completely valid to feel this way. Would it help to talk about what's causing these feelings?";
        mood = "angry";
        moodEmoji = "😠";
        phrase = "Anger is a message that something needs to change.";
      }
      else if (userMessage.includes('tired') || userMessage.includes('exhausted') || userMessage.includes('fatigued')) {
        botResponse = "It sounds like you're feeling tired. Remember to listen to your body and give yourself the rest you need. Have you been getting enough sleep lately?";
        mood = "tired";
        moodEmoji = "😴";
        phrase = "Rest is not idleness; it's essential renewal.";
      }
      else {
        // Default empathetic response
        const responses = [
          "Thank you for sharing that with me. How has that been affecting you?",
          "I appreciate you opening up about this. Would you like to explore it further?",
          "I'm here to listen. Can you tell me more about what's on your mind?",
          "That sounds challenging. How are you coping with these feelings?",
          "Your feelings are completely valid. What do you think would help right now?"
        ];
        botResponse = responses[Math.floor(Math.random() * responses.length)];
      }
      
      const botMsg = { 
        text: botResponse, 
        sender: "bot",
        mood: mood,
        moodEmoji: moodEmoji,
        phrase: phrase
      };
      setMessages(prev => [...prev, botMsg]);

      // Random surprise reward (15% chance)
      if (Math.random() < 0.15) {
        const surpriseCoins = Math.floor(Math.random() * 10) + 5;
        setUser(prev => ({ ...prev, coins: prev.coins + surpriseCoins }));
        setMessages(prev => [...prev, {
          text: `🎉 Surprise! You've earned ${surpriseCoins} bonus coins for engaging with your mental health!`,
          sender: "bot",
          isReward: true
        }]);
      }
    } catch (error) {
      console.error("Chat error:", error);
      const botMsg = { 
        text: "I'm having trouble connecting right now. Please check your internet connection.", 
        sender: "bot" 
      };
      setMessages(prev => [...prev, botMsg]);
    }

    setIsTyping(false);
    setTypingIndicator("");
  };

  const addJournalEntry = async () => {
    const entryText = prompt("What's on your mind today?");
    if (!entryText) return;

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      const newEntry = {
        id: journalEntries.length + 1,
        text: entryText,
        summary: "Your personal reflection",
        mood: "neutral",
        date: new Date().toISOString().split('T')[0]
      };
      
      setJournalEntries(prev => [newEntry, ...prev]);
      setUser(prev => ({ ...prev, coins: prev.coins + 3 }));
      alert("Journal entry saved! +3 coins earned!");
    } catch (error) {
      console.error("Journal error:", error);
      alert("Failed to save journal entry. Please try again.");
    }
  };

  const addMoodWallPost = async () => {
    const postText = prompt("Share your feelings with the community (anonymous):");
    if (!postText) return;

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      const newPost = {
        id: moodWall.length + 1,
        text: postText,
        mood: "neutral",
        likes: 0,
        timestamp: new Date().toISOString()
      };
      
      setMoodWall(prev => [newPost, ...prev]);
      alert("Posted to mood wall!");
    } catch (error) {
      console.error("Mood wall error:", error);
    }
  };

  const joinTribe = (tribeId) => {
    const tribe = moodTribes.find(t => t.id === tribeId);
    if (tribe) {
      alert(`You've joined the ${tribe.name}! This is a supportive community where you can connect with others who share similar experiences.`);
    }
  };

  const playBreathingExercise = () => {
    setMessages(prev => [...prev, {
      text: "Let's do a quick breathing exercise. Breathe in for 4 seconds, hold for 4 seconds, exhale for 6 seconds. Repeat 5 times. I'll guide you through it...",
      sender: "bot",
      isExercise: true
    }]);
  };

  const buyItem = (item) => {
    if (user.coins < item.price) {
      alert("Not enough coins!");
      return;
    }
    
    if (window.confirm(`Buy ${item.name} for ${item.price} coins?`)) {
      setUser(prev => ({ ...prev, coins: prev.coins - item.price }));
      
      if (item.category === "theme") {
        setCurrentTheme(item.name.toLowerCase().replace(' theme', ''));
        localStorage.setItem('theme', item.name.toLowerCase().replace(' theme', ''));
        alert(`Theme applied! You're now using the ${item.name}.`);
      } else {
        alert(`Purchased ${item.name}!`);
      }
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="app-container auth-container">
        <div className="auth-box">
          <h1>🌙 MoodMate</h1>
          <p>Your mental health companion</p>
          
          {showLogin && (
            <form onSubmit={handleLogin} className="auth-form">
              <h2>Login</h2>
              <input type="email" name="email" placeholder="Email" required defaultValue="demo@example.com" />
              <input type="password" name="password" placeholder="Password" required defaultValue="password" />
              <button type="submit">Login</button>
              <p>
                Don't have an account?{" "}
                <span className="auth-link" onClick={() => setShowRegister(true)}>
                  Register
                </span>
              </p>
            </form>
          )}

          {showRegister && (
            <form onSubmit={handleRegister} className="auth-form">
              <h2>Register</h2>
              <input type="text" name="name" placeholder="Full Name" required />
              <input type="email" name="email" placeholder="Email" required />
              <input type="password" name="password" placeholder="Password (min 6 chars)" required />
              <button type="submit">Register</button>
              <p>
                Already have an account?{" "}
                <span className="auth-link" onClick={() => setShowRegister(false)}>
                  Login
                </span>
              </p>
            </form>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className={`app-container ${currentTheme}-theme`}>
      <header className="app-header">
        <div className="header-left">
          <h1>🌙 MoodMate</h1>
          <div className="user-stats">
            <span>🔥 {user?.streak || 0} day streak</span>
            <span>🪙 {user?.coins || 0} coins</span>
          </div>
        </div>
        <div className="header-right">
          <span>Welcome, {user?.name}</span>
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </div>
      </header>

      <nav className="app-nav">
        <button 
          className={activeTab === "chat" ? "active" : ""} 
          onClick={() => setActiveTab("chat")}
        >
          💬 Chat
        </button>
        <button 
          className={activeTab === "journal" ? "active" : ""} 
          onClick={() => {
            setActiveTab("journal");
            loadJournalEntries();
          }}
        >
          📓 Journal
        </button>
        <button 
          className={activeTab === "therapy" ? "active" : ""} 
          onClick={() => setActiveTab("therapy")}
        >
          👨‍⚕️ Therapy
        </button>
        <button 
          className={activeTab === "community" ? "active" : ""} 
          onClick={() => setActiveTab("community")}
        >
          🌍 Community
        </button>
        <button 
          className={activeTab === "shop" ? "active" : ""} 
          onClick={() => setActiveTab("shop")}
        >
          🎮 Rewards
        </button>
      </nav>

      <main className="app-main">
        {activeTab === "chat" && (
          <div className="chat-tab">
            <div className="chat-box">
              {messages.map((msg, i) => (
                <div key={i} className={`chat-message ${msg.sender} ${msg.isReward ? 'reward' : ''} ${msg.isExercise ? 'exercise' : ''}`}>
                  {msg.sender === "bot" && (
                    <img
                      src="https://i.imgur.com/1XK5n.jpg"
                      alt="MoodMate"
                      className="avatar"
                      onError={(e) => {
                        e.target.src = 'https://via.placeholder.com/40x40/4A90E2/FFFFFF?text=MM';
                      }}
                    />
                  )}
                  {msg.sender === "user" && (
                    <img
                      src={`https://ui-avatars.com/api/?name=${user?.name || 'User'}&background=random`}
                      alt="You"
                      className="avatar"
                    />
                  )}
                  <div className="bubble">
                    {msg.text}
                    {msg.moodEmoji && (
                      <div className="mood-indicator">
                        {msg.moodEmoji} {msg.phrase}
                      </div>
                    )}
                  </div>
                </div>
              ))}

              {isTyping && (
                <div className="chat-message bot">
                  <img
                    src="https://i.imgur.com/1XK5n.jpg"
                    alt="MoodMate"
                    className="avatar"
                    onError={(e) => {
                      e.target.src = 'https://via.placeholder.com/40x40/4A90E2/FFFFFF?text=MM';
                    }}
                  />
                  <div className="bubble typing">
                    {typingIndicator || "..."}
                  </div>
                </div>
              )}

              <div ref={chatEndRef}></div>
            </div>

            <div className="input-area">
              <input
                type="text"
                placeholder="How are you feeling today?"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
              />
              <button onClick={sendMessage} className="send-btn">Send</button>
              <button onClick={playBreathingExercise} className="exercise-btn" title="Breathing Exercise">🧘‍♂️</button>
            </div>
          </div>
        )}

        {activeTab === "journal" && (
          <div className="journal-tab">
            <div className="journal-header">
              <h2>Your Journal</h2>
              <button onClick={addJournalEntry} className="add-entry-btn">
                + New Entry
              </button>
            </div>
            <div className="journal-entries">
              {journalEntries.length === 0 ? (
                <div className="empty-state">
                  <p>No journal entries yet. Write your first entry!</p>
                  <button onClick={addJournalEntry}>Start Journaling</button>
                </div>
              ) : (
                journalEntries.map((entry) => (
                  <div key={entry.id} className="journal-entry">
                    <div className="entry-date">{entry.date}</div>
                    <div className="entry-text">{entry.text}</div>
                    {entry.summary && (
                      <div className="entry-summary">
                        <strong>Summary:</strong> {entry.summary}
                      </div>
                    )}
                    {entry.mood && (
                      <div className="entry-mood">
                        <strong>Mood:</strong> {entry.mood}
                      </div>
                    )}
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {activeTab === "therapy" && (
          <div className="therapy-tab">
            <div className="resources-section">
              <h3>🆘 Crisis Resources</h3>
              <div className="resources-grid">
                {resources.filter(r => r.type === 'crisis').map((resource, index) => (
                  <div key={index} className="resource-card">
                    <h4>{resource.name}</h4>
                    <p>{resource.number}</p>
                    <small>{resource.available}</small>
                  </div>
                ))}
              </div>
            </div>

            <div className="therapists-section">
              <h3>👨‍⚕️ Professional Therapists</h3>
              <div className="therapists-grid">
                {therapists.map((therapist) => (
                  <div key={therapist.id} className="therapist-card">
                    <div className="therapist-avatar">{therapist.avatar}</div>
                    <h4>{therapist.name}</h4>
                    <p>{therapist.specialization}</p>
                    <p>⭐ {therapist.rating} • {therapist.experience}</p>
                    <p>🪙 {therapist.price} coins/session</p>
                    <button 
                      className="book-btn"
                      disabled={user?.coins < therapist.price}
                    >
                      {user?.coins >= therapist.price ? "Book Session" : "Not Enough Coins"}
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {activeTab === "community" && (
          <div className="community-tab">
            <div className="mood-wall-section">
              <h3>🌍 Community Mood Wall</h3>
              <button onClick={addMoodWallPost} className="add-post-btn">+ Share Your Feelings</button>
              <div className="mood-wall">
                {moodWall.length === 0 ? (
                  <div className="empty-state">
                    <p>No posts yet. Be the first to share!</p>
                  </div>
                ) : (
                  moodWall.map((post) => (
                    <div key={post.id} className="mood-post">
                      <p>{post.text}</p>
                      <div className="post-reactions">
                        <button>💖</button>
                        <button>🤗</button>
                        <button>👍</button>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>

            <div className="tribes-section">
              <h3>🎯 Mood Tribes</h3>
              <div className="tribes-grid">
                {moodTribes.map((tribe) => (
                  <div key={tribe.id} className="tribe-card">
                    <h4>{tribe.name}</h4>
                    <p>{tribe.description}</p>
                    <p className="member-count">👥 {tribe.member_count} members</p>
                    <button onClick={() => joinTribe(tribe.id)} className="join-btn">
                      Join Tribe
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {activeTab === "shop" && (
          <div className="shop-tab">
            <h2>🎮 Reward Shop</h2>
            <div className="shop-items">
              {shopItems.map((item) => (
                <div key={item.id} className="shop-item">
                  <h4>{item.name}</h4>
                  <p>{item.description}</p>
                  <p className="price">🪙 {item.price} coins</p>
                  <button 
                    onClick={() => buyItem(item)}
                    className={user.coins >= item.price ? "buy-btn" : "disabled-btn"}
                  >
                    {user.coins >= item.price ? 
                      (item.category === "theme" && currentTheme === item.name.toLowerCase().replace(' theme', '') ? "Applied" : "Purchase") : 
                      "Not Enough Coins"}
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
