    if (window.confirm(`Buy ${item.name} for ${item.price} coins?`)) {
      const newCoins = user.coins - item.price;
      const updatedUser = {...user, coins: newCoins};
      
      if (item.category === "theme") {
        // Add to purchased themes but don't apply automatically
        updatedUser.purchasedThemes = [...user.purchasedThemes, item.name.toLowerCase().replace(' theme', '')];
        alert(`Theme purchased! You can now apply it from your profile.`);
      } else if (item.category === "content") {
        // Add to owned packs
        updatedUser.ownedPacks = [...user.ownedPacks, item.id];
        alert(`Purchased ${item.name}! You can now access it anytime.`);
      } else if (item.name === "Second Chance Token") {
        alert("Second Chance Token purchased! This will save your streak if you miss a day.");
      } else {
        alert(`Purchased ${item.name}!`);
      }
      
      setUser(updatedUser);
    }
