  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMsg = { 
      id: Date.now(),
      text: input, 
      sender: "user",
      timestamp: new Date().toISOString()
    };
    setMessages(prev => [...prev, userMsg]);
    setInput("");
