import React, { useState, useEffect, useRef } from "react";
import "./app.css";

// Base URL for API calls
const API_BASE_URL = 'http://127.0.0.1:5000';

function App({ user, onLogout, chatHistory = [], currentChat, onNewChat, onLoadChat, onDeleteChat, onUpdateChat }) {
  const [messages, setMessages] = useState(currentChat?.messages || []);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [activeTab, setActiveTab] = useState("chat");
  const [journalEntries, setJournalEntries] = useState([]);
  const [therapists, setTherapists] = useState([]);
  const [resources, setResources] = useState([]);
  const [moodTribes, setMoodTribes] = useState([]);
  const [shopItems, setShopItems] = useState([]);
  const [userTribes, setUserTribes] = useState([]);
  const [typingIndicator, setTypingIndicator] = useState("");
  const [showEmergencyModal, setShowEmergencyModal] = useState(false);
  const [achievements, setAchievements] = useState([]);
  const [activeTribe, setActiveTribe] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [showNewChatModal, setShowNewChatModal] = useState(false);
  const [activeShopCategory, setActiveShopCategory] = useState("All");
  const [showMeditationGuide, setShowMeditationGuide] = useState(false);
  const [activeMeditation, setActiveMeditation] = useState(null);
  const [breathingActive, setBreathingActive] = useState(false);
  const [breathingPhase, setBreathingPhase] = useState("inhale");
  const [breathingProgress, setBreathingProgress] = useState(0);
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [selectedTherapist, setSelectedTherapist] = useState(null);
  const [showGamesModal, setShowGamesModal] = useState(false);
  const [showMoodReport, setShowMoodReport] = useState(false);
  const [showAICoach, setShowAICoach] = useState(false);
  const [aiCoachPlan, setAiCoachPlan] = useState([]);
  const [showSleepCompanion, setShowSleepCompanion] = useState(false);
  const [activeChallenge, setActiveChallenge] = useState(null);
  const [showChallengeModal, setShowChallengeModal] = useState(false);
  const [aiProvider, setAiProvider] = useState("gemini");
  const [audioEnabled, setAudioEnabled] = useState(true);
  const [musicEnabled, setMusicEnabled] = useState(true);
  const [tribeChatMessages, setTribeChatMessages] = useState({});
  const [tribeInput, setTribeInput] = useState("");
  const [showLoginBonus, setShowLoginBonus] = useState(false);
  const [showTherapistForm, setShowTherapistForm] = useState(false);
  const [bookingForm, setBookingForm] = useState({
    name: "",
    age: "",
    gender: "",
    contact: "",
    problem: "",
    preferredTime: ""
  });
  const [activeGame, setActiveGame] = useState(null);
  const [bubbleGame, setBubbleGame] = useState({ bubbles: [], score: 0 });
  const [coloringImage, setColoringImage] = useState(null);
  const [stressBalloon, setStressBalloon] = useState({ inflated: false, progress: 0 });
  const [gratitudeWheel, setGratitudeWheel] = useState({ spinning: false, result: null });
  const [selectedColor, setSelectedColor] = useState("#FF6B6B");
  const [showTherapistInfo, setShowTherapistInfo] = useState(null);
  const [exerciseTimer, setExerciseTimer] = useState(0);
  const [exerciseDuration, setExerciseDuration] = useState(0);
  const [showJournalAnalysis, setShowJournalAnalysis] = useState(false);
  const [showMoodTracker, setShowMoodTracker] = useState(false);

  // Initialize user data with state for re-rendering
  const [userData, setUserData] = useState(user || {
    id: 1,
    name: "Demo User",
    coins: 100,
    streak: 0,
    mentalFitness: 85,
    purchasedItems: [],
    ownedThemes: ["default"],
    currentTheme: "default",
    avatar: "default",
    achievements: [],
    loginStreak: 0,
    lastLogin: new Date().toISOString().split('T')[0]
  });

  const chatEndRef = useRef(null);
  const breathingTimerRef = useRef(null);
  const gameIntervalRef = useRef(null);
  const tribeChatEndRef = useRef(null);
  const exerciseTimerRef = useRef(null);
  const inputRef = useRef(null);

  // Sample data (can be replaced with API calls)
  const sampleTherapists = [
      {id: 1, name: "Dr. Arnav Singh", specialization: "Clinical Psychology", price: 799, rating: 5.0, experience: "8 years", avatar: "ðŸ‘¨â€âš•ï¸", languages: ["Hindi", "English"], modes: ["Video", "Chat"], description: "Specialized in anxiety and depression treatment.", education: "PhD in Clinical Psychology, University of Delhi", approach: "Cognitive Behavioral Therapy (CBT)", availability: "Mon-Fri, 9AM-6PM", bio: "Dr. Arnav has helped hundreds of patients overcome anxiety and depression through evidence-based techniques. He believes in a holistic approach to mental health."},
      {id: 2, name: "Aaryan Kumar", specialization: "Counseling Psychology", price: 599, rating: 4.5, experience: "5 years", avatar: "ðŸ‘¨â€ðŸ’¼", languages: ["Hindi", "English"], modes: ["Voice", "Chat"], description: "Focus on youth mental health and career stress.", education: "Masters in Counseling Psychology, TISS Mumbai", approach: "Person-Centered Therapy", availability: "Tue-Sat, 10AM-7PM", bio: "Aaryan specializes in helping young adults navigate career stress and life transitions. He creates a safe, non-judgmental space for his clients."},
      {id: 3, name: "Ankur Verma", specialization: "Psychiatry", price: 999, rating: 4.8, experience: "12 years", avatar: "ðŸ‘¨â€âš•ï¸", languages: ["English"], modes: ["Video"], description: "MD in Psychiatry with medication management expertise.", education: "MD Psychiatry, AIIMS Delhi", approach: "Integrated Pharmacotherapy and Psychotherapy", availability: "Mon-Thu, 8AM-4PM", bio: "Dr. Verma is a board-certified psychiatrist with extensive experience in medication management and treatment-resistant depression."},
      {id: 4, name: "Aanchal", specialization: "Art Therapy", price: 499, rating: 4.6, experience: "4 years", avatar: "ðŸ‘©â€ðŸŽ¨", languages: ["Hindi"], modes: ["Video", "Chat"], description: "Creative approaches to healing through art therapy.", education: "Masters in Art Therapy, IATE Mumbai", approach: "Expressive Arts Therapy", availability: "Wed-Sun, 11AM-8PM", bio: "Aanchal uses creative processes to help clients explore emotions, reduce anxiety, and improve self-esteem. No artistic skill required!"},
      {id: 5, name: "Aakash Patel", specialization: "Cognitive Behavioral Therapy", price: 899, rating: 4.9, experience: "10 years", avatar: "ðŸ‘¨â€ðŸ«", languages: ["Hindi", "English", "Gujarati"], modes: ["Video", "Chat"], description: "CBT expert with focus on thought patterns.", education: "PhD in Psychology, University of Mumbai", approach: "Cognitive Behavioral Therapy (CBT)", availability: "Mon-Fri, 10AM-6PM", bio: "Dr. Patel specializes in helping clients identify and change negative thought patterns that contribute to emotional distress."},
      {id: 6, name: "Nitin", specialization: "Mindfulness & Meditation", price: 649, rating: 4.7, experience: "6 years", avatar: "ðŸ§˜â€â™‚ï¸", languages: ["English", "Telugu", "Hindi"], modes: ["Video", "Voice"], description: "Guided meditation and mindfulness practices.", education: "Masters in Clinical Psychology, Bangalore University", approach: "Mindfulness-Based Stress Reduction (MBSR)", availability: "Tue-Sat, 9AM-5PM", bio: "Nitin teaches mindfulness techniques to help clients manage stress, anxiety, and improve overall well-being."}
  ];

  const sampleResources = [
    {type: "crisis", name: "National Suicide Prevention Lifeline", number: "1-800-273-8255", available: "24/7"},
    {type: "crisis", name: "Crisis Call Line", number: "+91-9999666555", available: "24/7"},
    {type: "crisis", name: "iCall India Helpline", number: "+91-9152987821", available: "Mon-Sat, 10AM-8PM"},
    {type: "support", name: "Mental Health America", website: "mhanational.org", description: "Resources and screening tools"},
    {type: "support", name: "Mindful.org", website: "mindful.org", description: "Meditation and mindfulness resources"}
  ];

  const sampleMoodTribes = [
    {id: 1, name: "Positivity Tribe", description: "For those seeking positivity and joy", member_count: 245, isMember: false},
    {id: 2, name: "Calm Collective", description: "Find your inner peace together", member_count: 189, isMember: false},
    {id: 3, name: "Anxiety Allies", description: "Support for those dealing with anxiety", member_count: 312, isMember: false},
    {id: 4, name: "Mindfulness Masters", description: "Practice mindfulness together", member_count: 156, isMember: false},
    {id: 5, name: "Breakup Recovery", description: "Healing after relationship endings", member_count: 128, isMember: false},
    {id: 6, name: "Career Stress", description: "Managing work-related stress", member_count: 201, isMember: false},
    {id: 7, name: "Student Support", description: "Academic pressure and study stress", member_count: 178, isMember: false}
  ];

  const sampleShopItems = [
      {id: 1, name: "Sunset Theme", description: "Warm orange and purple theme", price: 50, category: "theme", permanent: true},
      {id: 2, name: "Ocean Theme", description: "Calming blue theme", price: 50, category: "theme", permanent: true},
      {id: 3, name: "Forest Theme", description: "Green nature-inspired theme", price: 50, category: "theme", permanent: true},
      {id: 4, name: "Neon Theme", description: "Vibrant neon colors", price: 75, category: "theme", permanent: true},
      {id: 5, name: "Glass Theme", description: "Modern glassmorphism design", price: 100, category: "theme", permanent: true},
      {id: 6, name: "Exclusive Avatar", description: "Special avatar frame", price: 100, category: "cosmetic", permanent: true},
      {id: 7, name: "Meditation Pack", description: "20 guided meditation sessions", price: 75, category: "content", permanent: true, content: "Includes:\n- 5 breathing exercises\n- 5 mindfulness meditations\n- 5 sleep meditations\n- 5 stress relief sessions"},
      {id: 8, name: "Second Chance Token", description: "Save your streak if you miss a day", price: 30, category: "utility", permanent: false},
      {id: 9, name: "Sleep Stories Pack", description: "10 bedtime stories for better sleep", price: 60, category: "content", permanent: true, content: "Includes:\n- Beach Sunset (12 min)\n- Mountain Cabin (15 min)\n- Starry Night (10 min)\n- Forest Walk (18 min)\n- And 6 more calming stories"},
      {id: 10, name: "Breathing Animations", description: "Enhanced breathing exercise visuals", price: 40, category: "feature", permanent: true},
      {id: 11, name: "Astronaut Avatar", description: "Space-themed avatar", price: 120, category: "avatar", permanent: true},
      {id: 12, name: "Nature Lover Avatar", description: "Eco-friendly avatar", price: 90, category: "avatar", permanent: true},
      {id: 13, name: "Tech Guru Avatar", description: "Futuristic avatar", price: 150, category: "avatar", permanent: true},
      {id: 14, name: "Bookworm Avatar", description: "Literary-themed avatar", price: 80, category: "avatar", permanent: true},
      {id: 15, name: "Music Maestro Avatar", description: "Music-themed avatar", price: 110, category: "avatar", permanent: true},
      {id: 16, name: "Yoga Master Avatar", description: "Wellness-themed avatar", price: 95, category: "avatar", permanent: true}
  ];

  const sampleAchievements = [
    {id: 1, name: "First Steps", description: "Complete your first journal entry", earned: true, icon: "ðŸ“", coins: 10},
    {id: 2, name: "7-Day Warrior", description: "Maintain a 7-day streak", earned: false, icon: "ðŸ”¥", coins: 25},
    {id: 3, name: "Community Builder", description: "Join your first tribe", earned: false, icon: "ðŸ‘¥", coins: 15},
    {id: 4, name: "Meditation Master", description: "Complete 10 meditation sessions", earned: false, icon: "ðŸ§˜", coins: 20},
    {id: 5, name: "Therapy Explorer", description: "Book your first therapy session", earned: false, icon: "ðŸ‘¨â€âš•ï¸", coins: 25},
    {id: 6, name: "Game Champion", description: "Play all mini-games", earned: false, icon: "ðŸŽ®", coins: 30}
  ];

  const meditationExercises = [
    {id: 1, title: "4-7-8 Breathing", duration: "5 min", description: "Inhale for 4, hold for 7, exhale for 8", steps: ["Sit comfortably", "Inhale through nose for 4 seconds", "Hold breath for 7 seconds", "Exhale through mouth for 8 seconds", "Repeat 5 times"], image: "ðŸŒ¬ï¸"},
    {id: 2, title: "Body Scan", duration: "10 min", description: "Progressive relaxation technique", steps: ["Lie down comfortably", "Focus on your toes", "Slowly move attention up through your body", "Notice sensations without judgment", "Release tension in each area"], image: "ðŸ”"},
    {id: 3, title: "Mindful Breathing", duration: "5 min", description: "Focus on the breath", steps: ["Sit in comfortable position", "Focus on your natural breath", "Notice the sensation of air moving", "When mind wanders, gently return to breath"], image: "ðŸŒ¬ï¸"},
    {id: 4, title: "Loving Kindness", duration: "8 min", description: "Cultivate compassion", steps: ["Close your eyes", "Visualize someone you love", "Send them good wishes", "Extend to yourself, then to all beings"], image: "ðŸ’–"},
    {id: 5, title: "Morning Intention", duration: "3 min", description: "Set purpose for the day", steps: ["Sit quietly", "Reflect on your values", "Set a positive intention", "Carry it through your day"], image: "ðŸŒ…"},
    {id: 6, title: "Box Breathing", duration: "4 min", description: "4x4 square breathing technique", steps: ["Inhale for 4 seconds", "Hold for 4 seconds", "Exhale for 4 seconds", "Hold for 4 seconds", "Repeat 5-10 times"], image: "ðŸ§Š"}
  ];

  const moodGames = [
    {id: 1, title: "Bubble Pop", description: "Click/tap on bubbles to pop them", icon: "ðŸ«§"},
    {id: 2, title: "Mindful Coloring", description: "Digital coloring book with patterns", icon: "ðŸŽ¨"},
    {id: 3, title: "Breathing Guide", description: "Interactive visual breathing guide", icon: "ðŸŒ¬ï¸"},
    {id: 4, title: "Stress Balloon", description: "Deflate a balloon to release stress", icon: "ðŸŽˆ"},
    {id: 5, title: "Gratitude Wheel", description: "Spin for random gratitude prompts", icon: "ðŸŽ¡"}
  ];

  const moodChallenges = [
    {id: 1, title: "7 Days of Gratitude", duration: "7 days", description: "Write one thing you're grateful for each day", reward: "Gratitude Badge + 30 coins", progress: 0},
    {id: 2, title: "Mindful Mornings", duration: "5 days", description: "Start each day with 5 minutes of meditation", reward: "Morning Person Badge + 20 coins", progress: 0},
    {id: 3, title: "Digital Detox", duration: "3 days", description: "Reduce screen time by 50% for 3 days", reward: "Digital Wellness Badge + 25 coins", progress: 0},
    {id: 4, title: "Kindness Challenge", duration: "7 days", description: "Perform one act of kindness daily", reward: "Kindness Ambassador Badge + 35 coins", progress: 0}
  ];

  const sleepStories = [
    {id: 1, title: "Beach Sunset", duration: "12 min", description: "Visualize a peaceful beach at sunset"},
    {id: 2, title: "Mountain Cabin", duration: "15 min", description: "Imagine a cozy cabin in the mountains"},
    {id: 3, title: "Starry Night", duration: "10 min", description: "Float among the stars in the night sky"},
    {id: 4, title: "Forest Walk", duration: "18 min", description: "Take a calming walk through a peaceful forest"}
  ];

  const aiCoachPlans = [
    {time: "Morning", activity: "Affirmation", description: "Start your day with a positive affirmation", duration: "2 min", icon: "ðŸŒž"},
    {time: "Midday", activity: "Check-in", description: "How are you feeling right now?", duration: "3 min", icon: "ðŸ’¬"},
    {time: "Evening", activity: "Reflection", description: "Reflect on your day and things you're grateful for", duration: "5 min", icon: "ðŸŒ™"},
    {time: "Weekly", activity: "Review", description: "Review your progress and set intentions", duration: "10 min", icon: "ðŸ“Š"}
  ];

  const coloringImages = [
    {id: 1, name: "Mandala", complexity: "Medium", colors: ["#FF6B6B", "#4ECDC4", "#FFE66D", "#1A535C", "#F7FFF7"]},
    {id: 2, name: "Nature Scene", complexity: "High", colors: ["#2A9D8F", "#E9C46A", "#F4A261", "#E76F51", "#264653"]},
    {id: 3, name: "Geometric Patterns", complexity: "Easy", colors: ["#50514F", "#F25F5C", "#FFE066", "#247BA0", "#70C1B3"]}
  ];

  const gratitudePrompts = [
    "What made you smile today?",
    "Who are you grateful for in your life?",
    "What ability are you grateful for?",
    "What experience are you grateful for?",
    "What about nature are you grateful for?",
    "What simple pleasure are you grateful for?",
    "What challenge are you grateful for?",
    "What lesson are you grateful for learning?"
  ];

  // Update messages when currentChat changes
  useEffect(() => {
    setMessages(currentChat?.messages || []);
  }, [currentChat]);

  // Update chat history when messages change
  useEffect(() => {
    if (currentChat && messages.length > 0) {
      const updatedChat = {
        ...currentChat,
        messages: messages,
        preview: messages.length > 0 ? messages[messages.length - 1].text.substring(0, 50) + "..." : "New chat",
        timestamp: new Date().toISOString()
      };
      
      // Update title based on first message if it's a new chat
      if (currentChat.title === "New Chat" && messages.length === 1 && messages[0].sender === "user") {
        updatedChat.title = messages[0].text.substring(0, 30) + (messages[0].text.length > 30 ? "..." : "");
      }
      
      onUpdateChat && onUpdateChat(updatedChat);
    }
  }, [messages, currentChat, onUpdateChat]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  useEffect(() => {
    if (tribeChatEndRef.current && activeTribe) {
      tribeChatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [tribeChatMessages, activeTribe]);

  // Auto-focus the first input in the therapist form when it opens
  useEffect(() => {
      if (showTherapistForm && inputRef.current) {
          inputRef.current.focus();
      }
  }, [showTherapistForm]);

  useEffect(() => {
    // Clean up timers on unmount
    return () => {
      if (breathingTimerRef.current) clearInterval(breathingTimerRef.current);
      if (gameIntervalRef.current) clearInterval(gameIntervalRef.current);
      if (exerciseTimerRef.current) clearInterval(exerciseTimerRef.current);
    };
  }, []);

  useEffect(() => {
    loadInitialData();
    checkDailyLoginBonus();
  }, []);

  const checkDailyLoginBonus = () => {
    const today = new Date().toISOString().split('T')[0];
    const lastLogin = localStorage.getItem('lastLogin');
    
    if (lastLogin !== today) {
        setUserData(prev => {
            const newStreak = (prev.loginStreak || 0) + 1;
            const coinsEarned = Math.min(newStreak, 7) * 5;
            return {
                ...prev,
                loginStreak: newStreak,
                coins: (prev.coins || 0) + coinsEarned
            };
        });
      
      localStorage.setItem('lastLogin', today);
      setShowLoginBonus(true);
      
      setTimeout(() => {
        setShowLoginBonus(false);
      }, 3000);
    }
  };

