            const audio = new Audio(`${API_BASE_URL}${data.audioUrl}`);
            await audio.play();
          } catch (error) {
            console.error("Audio playback failed. This can happen due to network issues or unsupported audio formats.", error);
          }
      }

    } catch (error) {
      console.log("Chat error:", error);
      // Fallback response
      const botMsg = { 
        id: Date.now(),
        text: "I'm here to listen. How does that make you feel?", 
        sender: "bot",
