    setActiveMeditation(exercise);
    
    const durationMinutes = parseInt(exercise.duration);
    setExerciseDuration(durationMinutes * 60);
    setExerciseTimer(durationMinutes * 60);
    
    // Start exercise timer
    exerciseTimerRef.current = setInterval(() => {
      setExerciseTimer(prev => {
        if (prev <= 1) {
          clearInterval(exerciseTimerRef.current);
          stopBreathingExercise();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    
    // **FIXED BREATHING LOGIC**
    let phase = "inhale";
    const phaseDurations = { inhale: 4000, hold: 4000, exhale: 4000 };
    let phaseProgress = 0;
    const intervalTime = 50; // Update every 50ms for smooth animation

    const runCycle = () => {
        phaseProgress += intervalTime;
        const currentPhaseDuration = phaseDurations[phase];
        setBreathingProgress((phaseProgress / currentPhaseDuration) * 100);

        if (phaseProgress >= currentPhaseDuration) {
            phaseProgress = 0;
            if (phase === "inhale") phase = "hold";
            else if (phase === "hold") phase = "exhale";
            else phase = "inhale";
            setBreathingPhase(phase);
        }
    };
    
    breathingTimerRef.current = setInterval(runCycle, intervalTime);
    
    // Add message about the exercise
    setMessages(prev => [...prev, {
      id: Date.now(),
