    const anxiousWords = ['anxious', 'worried', 'stressed', 'nervous', 'scared', 'overwhelmed'];
    const sadWords = ['sad', 'depressed', 'unhappy', 'down', 'crying', 'miserable'];

    if (sadWords.some(word => lowerText.includes(word))) return 'sad';
    if (anxiousWords.some(word => lowerText.includes(word))) return 'anxious';
    if (positiveWords.some(word => lowerText.includes(word))) return 'happy';
    return 'neutral';
