    const phaseText = {
      inhale: "Breathe In...",
      hold: "Hold...",
      exhale: "Breathe Out..."
    };
    
    const phaseDescription = {
      inhale: "Fill your lungs completely",
      hold: "Feel the stillness",
      exhale: "Release all tension"
    };
    
    return (
      <div className="breathing-overlay">
        <div className="breathing-container">
          <div className="breathing-circle">
            <div 
              className="breathing-animation"
              style={{ 
                transform: `scale(${breathingPhase === 'inhale' ? 1 + (breathingProgress/100) : 
                  breathingPhase === 'hold' ? 2 : 
                  2 - (breathingProgress/100)})`,
                opacity: breathingPhase === 'hold' ? 0.8 : 0.6
              }}
            ></div>
          </div>
          
          <div className="breathing-instructions">
            <h2>{phaseText[breathingPhase]}</h2>
            <p>{phaseDescription[breathingPhase]}</p>
            <div className="breathing-timer">
              <div className="timer-bar">
                <div 
                  className="timer-progress" 
                  style={{ width: `${breathingProgress}%` }}
                ></div>
              </div>
            </div>
          </div>
          
          <button onClick={stopBreathingExercise} className="stop-breathing-btn">
            Stop Exercise
          </button>
        </div>
      </div>
    );
  };

