  const bookAppointment = async () => {
    if (!selectedDoctor || !selectedSlot || !selectedMode) {
      alert("Please select a doctor, time slot, and mode");
      return;
    }

    try {
      const bookingData = {
        doctor_id: selectedDoctor.id,
        slot_time: selectedSlot,
        mode: selectedMode
      };

      const data = await apiService.bookAppointment(bookingData);

      if (data.status === "success") {
        alert("Appointment booked successfully!");
        setBookingStep("select-doctor");
        setSelectedDoctor(null);
        setSelectedSlot(null);
        setSelectedMode("chat");
        
        // Refresh bookings
        const bookingsData = await apiService.getUserBookings();
        setUserBookings(bookingsData.bookings || bookingsData);
      } else {
        alert(data.error || "Failed to book appointment");
      }
    } catch (error) {
      console.error("Booking error:", error);
      alert("Failed to book appointment. Please try again.");
    }
  };

  const joinSession = async (booking) => {
    try {
      const data = await apiService.joinSession(booking.id);
      
      if (data.status === "success") {
        if (booking.mode === "chat") {
          // Open chat interface
          setActiveTab("chat");
        } else if (booking.mode === "audio" || booking.mode === "video") {
          // Open meeting link in new tab
          window.open(data.meeting_link, "_blank");
        }
      } else {
        alert(data.error || "Failed to join session");
      }
    } catch (error) {
      console.error("Join session error:", error);
      alert("Failed to join session. Please try again.");
    }
  };

  const formatDate = (dateString) => {
    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const formatTime = (timeString) => {
    return new Date(timeString).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const EmergencyModal = () => {
    if (!showEmergencyModal) return null;
    
    return (
      <div className="emergency-modal">
        <div className="emergency-content">
          <h2>‚ö†Ô∏è We're concerned about you</h2>
          <p>Please choose an option below. Your safety matters üíô</p>
          
          <div className="emergency-buttons">
            <button 
              className="emergency-call" 
              onClick={() => {
                window.open('tel:1-800-273-8255');
                setShowEmergencyModal(false);
              }}
            >
              Call for Help üî¥
            </button>
            
            <button 
              className="emergency-resources" 
              onClick={() => {
                alert("Crisis Resources:\n\n1. National Suicide Prevention Lifeline: 1-800-273-8255\n2. Crisis Text Line: Text HOME to 741741\n3. iCall India: +91-9152987821");
                setShowEmergencyModal(false);
              }}
            >
              Show Resources üìã
            </button>
            
            <button 
              className="emergency-ok" 
              onClick={() => setShowEmergencyModal(false)}
            >
              I'm Fine Now ‚úÖ
            </button>
          </div>
        </div>
      </div>
    );
  };

  const NewChatModal = () => {
    if (!showNewChatModal) return null;
    
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <h2>Start New Chat</h2>
          <p>Are you sure you want to start a new chat? Your current conversation will be cleared.</p>
          <div className="modal-buttons">
            <button onClick={startNewChat} className="btn-primary">Yes, Start New Chat</button>
            <button onClick={() => setShowNewChatModal(false)} className="btn-secondary">Cancel</button>
          </div>
        </div>
      </div>
    );
  };

  const BookingWizard = () => {
    if (bookingStep === "select-doctor") {
      return (
        <div className="booking-step">
          <h3>Select a Doctor</h3>
          <div className="doctors-grid">
            {therapists.map(doctor => (
              <div key={doctor.id} className="doctor-card" onClick={() => {
                setSelectedDoctor(doctor);
                setBookingStep("select-slot");
              }}>
                <div className="doctor-avatar">{doctor.avatar || doctor.name.charAt(0)}</div>
                <h4>{doctor.name}</h4>
                <p>{doctor.specialization}</p>
                <p>Fee: {doctor.price} coins</p>
                <div className="modes">
                  {doctor.mode && doctor.mode.includes("chat") && <span>üí¨</span>}
                  {doctor.mode && doctor.mode.includes("audio") && <span>üéß</span>}
                  {doctor.mode && doctor.mode.includes("video") && <span>üìπ</span>}
                </div>
              </div>
            ))}
          </div>
          <button className="back-button" onClick={() => setActiveTab("therapy")}>Back to Therapy</button>
        </div>
      );
    } else if (bookingStep === "select-slot") {
      return (
        <div className="booking-step">
          <h3>Select Time Slot with Dr. {selectedDoctor.name}</h3>
          <div className="slots-grid">
            {selectedDoctor.availability_slots && selectedDoctor.availability_slots.map(slot => (
              <div 
                key={slot} 
                className={`slot-card ${selectedSlot === slot ? "selected" : ""}`}
                onClick={() => setSelectedSlot(slot)}
              >
                {formatTime(slot)}
              </div>
            ))}
          </div>
          <div className="booking-controls">
            <button className="back-button" onClick={() => setBookingStep("select-doctor")}>Back to Doctors</button>
            {selectedSlot && (
              <button className="next-button" onClick={() => setBookingStep("select-mode")}>Next</button>
            )}
          </div>
        </div>
      );
    } else if (bookingStep === "select-mode") {
      return (
        <div className="booking-step">
          <h3>Select Session Mode with Dr. {selectedDoctor.name}</h3>
          <p>Selected time: {formatTime(selectedSlot)}</p>
          <div className="modes-grid">
            {selectedDoctor.mode && selectedDoctor.mode.includes("chat") && (
              <div 
                className={`mode-card ${selectedMode === "chat" ? "selected" : ""}`}
                onClick={() => setSelectedMode("chat")}
              >
                <h4>üí¨ Chat Session</h4>
                <p>Text-based messaging</p>
              </div>
            )}
            {selectedDoctor.mode && selectedDoctor.mode.includes("audio") && (
              <div 
                className={`mode-card ${selectedMode === "audio" ? "selected" : ""}`}
                onClick={() => setSelectedMode("audio")}
              >
                <h4>üéß Audio Call</h4>
                <p>Voice conversation</p>
              </div>
            )}
            {selectedDoctor.mode && selectedDoctor.mode.includes("video") && (
              <div 
                className={`mode-card ${selectedMode === "video" ? "selected" : ""}`}
                onClick={() => setSelectedMode("video")}
              >
                <h4>üìπ Video Call</h4>
                <p>Face-to-face conversation</p>
              </div>
            )}
          </div>
          <div className="booking-controls">
            <button className="back-button" onClick={() => setBookingStep("select-slot")}>Back to Time Slots</button>
            {selectedMode && (
              <button className="next-button" onClick={() => setBookingStep("confirm-booking")}>Next</button>
            )}
          </div>
        </div>
      );
    } else if (bookingStep === "confirm-booking") {
      return (
        <div className="booking-step">
          <h3>Confirm Your Booking</h3>
          <div className="booking-summary">
            <h4>Appointment Details</h4>
            <p><strong>Doctor:</strong> Dr. {selectedDoctor.name}</p>
            <p><strong>Specialization:</strong> {selectedDoctor.specialization}</p>
            <p><strong>Date & Time:</strong> {formatTime(selectedSlot)}</p>
            <p><strong>Mode:</strong> 
              {selectedMode === "chat" && " üí¨ Chat"}
              {selectedMode === "audio" && " üéß Audio Call"}
              {selectedMode === "video" && " üìπ Video Call"}
            </p>
            <p><strong>Fee:</strong> {selectedDoctor.price} coins</p>
          </div>
          <div className="booking-controls">
            <button className="back-button" onClick={() => setBookingStep("select-mode")}>Back to Mode Selection</button>
            <button className="confirm-button" onClick={bookAppointment}>Confirm Booking</button>
          </div>
        </div>
      );
    }
  };

  const TribeCommunity = () => {
    if (!activeTribe) return null;
    
    const tribeChat = tribeChatMessages[activeTribe.id] || [];
    
    return (
      <div className="tribe-community">
        <div className="tribe-header">
          <button onClick={() => setActiveTribe(null)} className="back-btn">
            ‚Üê Back to Communities
          </button>
          <h2>{activeTribe.name} Community</h2>
          <p>{activeTribe.description}</p>
          <div className="tribe-stats">
            <span>üë• {activeTribe.member_count} members</span>
            <span>üí¨ {tribeChat.length} messages</span>
          </div>
        </div>
        
        <div className="tribe-content">
          <div className="tribe-chat">
            <h3>Community Chat</h3>
            <div className="tribe-messages">
              {tribeChat.map((msg) => (
                <div key={`tribe-msg-${msg.id}-${Date.now()}`} className="tribe-message">
                  <div className="message-avatar">{msg.user_name.charAt(0)}</div>
                  <div className="message-content">
                    <span className="message-sender">{msg.user_name}</span>
                    <p>{msg.message}</p>
                    <span className="message-time">{new Date(msg.timestamp).toLocaleTimeString()}</span>
                  </div>
                </div>
              ))}
            </div>
            
            <div className="tribe-input">
              <input 
                type="text" 
                placeholder="Share something with the community..." 
                value={tribeInput}
                onChange={(e) => setTribeInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendTribeMessage(activeTribe.id)}
              />
              <button onClick={() => sendTribeMessage(activeTribe.id)}>Send</button>
            </div>
          </div>
          
          <div className="tribe-resources">
            <h3>Community Resources</h3>
            <div className="resource-list">
              <div className="resource-item">
                <h4>Mindfulness Exercises</h4>
                <p>Guided meditation for beginners</p>
                <button>View</button>
              </div>
              <div className="resource-item">
                <h4>Support Threads</h4>
                <p>Connect with others who understand</p>
                <button>Explore</button>
              </div>
              <div className="resource-item">
                <h4>Weekly Challenges</h4>
                <p>Join community wellness challenges</p>
                <button>Participate</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  const Sidebar = () => {
    if (!sidebarOpen) return null;
    
    return (
      <div className="sidebar-overlay" onClick={() => setSidebarOpen(false)}>
        <div className="sidebar" onClick={(e) => e.stopPropagation()}>
          <div className="sidebar-header">
            <h2>MoodMate</h2>
            <button onClick={() => setSidebarOpen(false)} className="close-btn">√ó</button>
          </div>
          
          <div className="sidebar-content">
            <div className="sidebar-section">
              <h3>Navigation</h3>
              <button 
                onClick={() => { setActiveTab("chat"); setSidebarOpen(false); }} 
                className={`sidebar-btn ${activeTab === "chat" ? "active" : ""}`}
              >
                üí¨ Chat
              </button>
              <button 
                onClick={() => { setActiveTab("journal"); setSidebarOpen(false); }} 
                className={`sidebar-btn ${activeTab === "journal" ? "active" : ""}`}
              >
                üìì Journal
              </button>
              <button 
                onClick={() => { setActiveTab("therapy"); setSidebarOpen(false); }} 
                className={`sidebar-btn ${activeTab === "therapy" ? "active" : ""}`}
              >
                üë®‚Äç‚öïÔ∏è Therapy
              </button>
              <button 
                onClick={() => { setActiveTab("community"); setSidebarOpen(false); }} 
                className={`sidebar-btn ${activeTab === "community" ? "active" : ""}`}
              >
                üåç Community
              </button>
              <button 
                onClick={() => { setActiveTab("shop"); setSidebarOpen(false); }} 
                className={`sidebar-btn ${activeTab === "shop" ? "active" : ""}`}
              >
                üõí Shop
              </button>
              <button 
                onClick={() => { setActiveTab("profile"); setSidebarOpen(false); }} 
                className={`sidebar-btn ${activeTab === "profile" ? "active" : ""}`}
              >
                üë§ Profile
              </button>
            </div>
            
            <div className="sidebar-section">
              <h3>Quick Actions</h3>
              <button onClick={() => { setShowNewChatModal(true); setSidebarOpen(false); }} className="sidebar-btn">
                New Chat
              </button>
              <button onClick={() => { playBreathingExercise(); setSidebarOpen(false); }} className="sidebar-btn">
                Breathing Exercise
              </button>
            </div>
            
            <div className="sidebar-section">
              <h3>Settings</h3>
              <div className="setting-item">
                <span>Voice Responses</span>
                <label className="switch">
                  <input type="checkbox" checked={audioEnabled} onChange={toggleAudio} />
                  <span className="slider"></span>
                </label>
              </div>
              <div className="setting-item">
                <span>Background Music</span>
                <label className="switch">
                  <input type="checkbox" checked={musicEnabled} onChange={toggleMusic} />
                  <span className="slider"></span>
                </label>
              </div>
              <div className="setting-item">
                <span>AI Provider</span>
                <select value={aiProvider} onChange={(e) => switchAiProvider(e.target.value)}>
                  <option value="gemini">Gemini AI</option>
                  <option value="fallback">Fallback Mode</option>
                </select>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  const TherapyTab = () => {
    if (bookingStep !== "select-doctor") {
      return <BookingWizard />;
    }

    return (
      <div className="therapy-tab">
        <div className="therapy-header">
          <h2>Professional Therapy Sessions</h2>
          <button className="book-session-btn" onClick={() => setBookingStep("select-doctor")}>
            Book a Session
          </button>
        </div>

        <div className="my-bookings-section">
          <h3>My Upcoming Sessions</h3>
          {userBookings.length === 0 ? (
            <p>You don't have any upcoming sessions.</p>
          ) : (
            <div className="bookings-list">
              {userBookings.map(booking => (
                <div key={booking.id} className="booking-item">
                  <div className="booking-info">
                    <h4>Session with Dr. {booking.doctor_name}</h4>
                    <p>{formatDate(booking.slot_time)} at {formatTime(booking.slot_time)}</p>
                    <p>Mode: 
                      {booking.mode === "chat" && " üí¨ Chat"}
                      {booking.mode === "audio" && " üéß Audio"}
                      {booking.mode === "video" && " üìπ Video"}
                    </p>
                    <p>Status: <span className={`status ${booking.status}`}>{booking.status}</span></p>
                  </div>
                  <div className="booking-actions">
                    {booking.status === "confirmed" && (
                      <button 
                        className="join-session-btn"
                        onClick={() => joinSession(booking)}
                      >
                        Join Session
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="available-doctors-section">
          <h3>Available Doctors</h3>
          <div className="doctors-grid">
            {therapists.map(doctor => (
              <div key={doctor.id} className="doctor-card">
                <div className="doctor-avatar">{doctor.avatar || doctor.name.charAt(0)}</div>
                <h4>{doctor.name}</h4>
                <p>{doctor.specialization}</p>
                <p>Fee: {doctor.price} coins</p>
                <div className="modes">
                  {doctor.mode && doctor.mode.includes("chat") && <span>üí¨</span>}
                  {doctor.mode && doctor.mode.includes("audio") && <span>üéß</span>}
                  {doctor.mode && doctor.mode.includes("video") && <span>üìπ</span>}
                </div>
                <button 
                  className="book-btn"
                  onClick={() => {
                    setSelectedDoctor(doctor);
                    setBookingStep("select-slot");
                  }}
                >
                  Book Now
                </button>
              </div>
            ))}
          </div>
        </div>

        <div className="resources-section">
          <h3>üÜò Crisis Resources</h3>
          <div className="resources-grid">
            {resources.filter(r => r.type === 'crisis').map((resource, index) => (
              <div key={`resource-${index}`} className="resource-card">
                <h4>{resource.name}</h4>
                <p>{resource.number}</p>
                <small>{resource.available}</small>
                {resource.number && (
                  <button 
                    className="call-btn"
                    onClick={() => window.open(`tel:${resource.number.replace(/\D/g, '')}`)}
                  >
                    üìû Call Now
                  </button>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  };

  const shopCategories = ["All", "theme", "cosmetic", "content", "utility", "feature"];

  const filteredShopItems = activeShopCategory === "All"
    ? shopItems
    : shopItems.filter(item => item.category.toLowerCase() === activeShopCategory.toLowerCase());

  if (!isLoggedIn) {
    return (
      <div className="app-container auth-container">
        <div className="auth-box">
          <h1>üåô MoodMate</h1>
          <p>Your mental health companion</p>
          
          {showLogin && (
            <form onSubmit={handleLogin} className="auth-form">
              <h2>Login</h2>
              {loginError && <div className="error-message">{loginError}</div>}
              <input type="email" name="email" placeholder="Email" required />
              <input type="password" name="password" placeholder="Password" required />
              <button type="submit">Login</button>
              
              <div className="auth-links">
                <span className="auth-link" onClick={() => setShowRegister(true)}>
                  Create Account
                </span>
                <span className="auth-link" onClick={() => setShowForgotPassword(true)}>
                  Forgot Password?
                </span>
              </div>
            </form>
          )}

          {showRegister && (
            <form onSubmit={handleRegister} className="auth-form">
              <h2>Register</h2>
              {registerError && <div className="error-message">{registerError}</div>}
              <input type="text" name="name" placeholder="Full Name" required />
              <input type="email" name="email" placeholder="Email" required />
              <input type="password" name="password" placeholder="Password (min 8 chars)" required />
              <input type="password" name="confirmPassword" placeholder="Confirm Password" required />
              <button type="submit">Register</button>
              <p>
                Already have an account?{" "}
                <span className="auth-link" onClick={() => setShowRegister(false)}>
                  Login
                </span>
              </p>
            </form>
          )}

          {showForgotPassword && (
            <div className="auth-form">
              <h2>Reset Password</h2>
              <p>Please contact support to reset your password.</p>
              <p>
                Remember your password?{" "}
                <span className="auth-link" onClick={() => setShowForgotPassword(false)}>
                  Login
                </span>
              </p>
            </div>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className={`app-container ${currentTheme}-theme`}>
      <EmergencyModal />
      <NewChatModal />
      <Sidebar />
      
      <header className="app-header">
        <div className="header-left">
          <button onClick={() => setSidebarOpen(true)} className="menu-btn">‚ò∞</button>
          <h1>üåô MoodMate</h1>
          <div className="user-stats">
            <span className="streak-display">üî• {user?.streak || 0} day streak</span>
            <span>ü™ô {user?.coins || 0} coins</span>
          </div>
        </div>
        <div className="header-right">
          <div className="user-info">
            <span className="user-name">{user?.name}</span>
            <span className="user-status">Online</span>
          </div>
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </div>
      </header>

      <nav className="app-nav">
        <button 
          className={activeTab === "chat" ? "active" : ""} 
          onClick={() => setActiveTab("chat")}
        >
          üí¨ Chat
        </button>
        <button 
          className={activeTab === "journal" ? "active" : ""} 
          onClick={() => setActiveTab("journal")}
        >
          üìì Journal
        </button>
        <button 
          className={activeTab === "therapy" ? "active" : ""} 
          onClick={() => {
            setActiveTab("therapy");
            setBookingStep("select-doctor");
          }}
        >
          üë®‚Äç‚öïÔ∏è Therapy
        </button>
        <button 
          className={activeTab === "community" ? "active" : ""} 
          onClick={() => setActiveTab("community")}
        >
          üåç Community
        </button>
        <button 
          className={activeTab === "shop" ? "active" : ""} 
          onClick={() => setActiveTab("shop")}
        >
          üõí Shop
        </button>
        <button 
          className={activeTab === "profile" ? "active" : ""} 
          onClick={() => setActiveTab("profile")}
        >
          üë§ Profile
        </button>
      </nav>

      <main className="app-main">
        {activeTab === "chat" && (
          <div className="chat-tab">
            <div className="chat-header">
              <h2>Chat with MoodMate</h2>
              <button onClick={() => setShowNewChatModal(true)} className="new-chat-btn">
                + New Chat
              </button>
            </div>
            <div className="chat-box">
              {messages.map((msg, i) => (
                <div key={`msg-${i}-${msg.timestamp || Date.now()}`} className={`chat-message ${msg.sender} ${msg.isReward ? 'reward' : ''} ${msg.isExercise ? 'exercise' : ''}`}>
                  {msg.sender === "bot" && (
                    <div className="avatar bot-avatar">ü§ñ</div>
                  )}
                  {msg.sender === "user" && (
                    <div className="avatar user-avatar">üë§</div>
                  )}
                  <div className="bubble">
                    {msg.text}
                    {msg.moodEmoji && (
                      <div className="mood-indicator">
                        {msg.moodEmoji} {msg.phrase}
                      </div>
                    )}
                  </div>
                </div>
              ))}

              {isTyping && (
                <div className="chat-message bot">
                  <div className="avatar bot-avatar">ü§ñ</div>
                  <div className="bubble typing">
                    {typingIndicator || "..."}
                  </div>
                </div>
              )}

              <div ref={chatEndRef}></div>
            </div>

            <div className="input-area">
              <input
                type="text"
                placeholder="How are you feeling today?"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
              />
              <button onClick={sendMessage} className="send-btn">Send</button>
              <button onClick={playBreathingExercise} className="exercise-btn" title="Breathing Exercise">üßò‚Äç‚ôÇÔ∏è</button>
            </div>
          </div>
        )}

        {activeTab === "journal" && (
          <div className="journal-tab">
            <div className="journal-header">
              <h2>Your Journal</h2>
              <button onClick={addJournalEntry} className="add-entry-btn">
                + New Entry
              </button>
            </div>
            
            <div className="journal-entries">
              {journalEntries.length === 0 ? (
                <div className="empty-state">
                  <p>No journal entries yet. Write your first entry!</p>
                  <button onClick={addJournalEntry}>Start Journaling</button>
                </div>
              ) : (
                journalEntries.map((entry, index) => (
                  <div key={`entry-${entry.id}-${index}`} className="journal-entry">
                    <div className="entry-header">
                      <div className="entry-date">{entry.date}</div>
                      {!entry.shared && userTribes.length > 0 && (
                        <button 
                          onClick={() => shareJournalEntry(entry.id)}
                          className="share-btn"
                          title="Share to your tribes"
                        >
                          Share
                        </button>
                      )}
                    </div>
                    <div className="entry-text">{entry.text}</div>
                    {entry.summary && (
                      <div className="entry-summary">
                        <strong>Summary:</strong> {entry.summary}
                      </div>
                    )}
                    {entry.mood && (
                      <div className="entry-mood">
                        <strong>Mood:</strong> {entry.mood}
                      </div>
                    )}
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {activeTab === "therapy" && (
          <TherapyTab />
        )}

        {activeTab === "community" && activeTribe ? (
          <TribeCommunity />
        ) : activeTab === "community" ? (
          <div className="community-tab">
            <div className="community-layout">
              <div className="mood-wall-section">
                <h3>üåç Community Mood Wall</h3>
                <button onClick={addMoodWallPost} className="add-post-btn">+ Share Your Feelings</button>
                <div className="mood-wall">
                  {moodWall.length === 0 ? (
                    <div className="empty-state">
                      <p>Be the first to share your thoughts with the community!</p>
                    </div>
                  ) : (
                    moodWall.slice(0, 3).map((post, index) => (
                      <div key={`post-${post.id}-${index}`} className="mood-post">
                        <p>{post.text}</p>
                        <div className="post-footer">
                          <div className="post-reactions">
                            <button>üíñ</button>
                            <button>ü§ó</button>
                            <button>üëç</button>
                          </div>
                          <span className="post-time">{new Date(post.timestamp).toLocaleDateString()}</span>
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </div>

              <div className="tribes-section">
                <h3>üéØ Mood Tribes</h3>
                <div className="tribes-grid">
                  {moodTribes.map((tribe) => (
                    <div key={`tribe-${tribe.id}`} className="tribe-card">
                      <h4>{tribe.name}</h4>
                      <p>{tribe.description}</p>
                      <p className="member-count">üë• {tribe.member_count} members</p>
                      <button 
                        onClick={() => tribe.isMember ? setActiveTribe(tribe) : joinTribe(tribe.id)}
                        className={tribe.isMember ? "joined-btn" : "join-btn"}
                      >
                        {tribe.isMember ? "Visit Community" : "Join Tribe"}
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        ) : null}

        {activeTab === "shop" && (
          <div className="shop-tab">
            <h2>üõí MoodMate Shop</h2>
            <p>Use your Mood Coins to unlock special features and customization options!</p>
            
            <div className="shop-categories">
              {shopCategories.map(category => (
                <button 
                  key={category} 
                  className={`category-btn ${activeShopCategory === category ? 'active' : ''}`}
                  onClick={() => setActiveShopCategory(category)}
                >
                  {category}
                </button>
              ))}
            </div>
            
            <div className="shop-grid">
              {filteredShopItems.length > 0 ? (
                filteredShopItems.map((item) => (
                  <div key={`shop-item-${item.id}`} className="shop-item-card">
                    <div className="item-image">
                      {item.category === "theme" && <div className={`theme-preview ${item.name.toLowerCase().replace(' theme', '')}-theme`}></div>}
                      {item.category === "cosmetic" && <span className="item-icon">üé®</span>}
                      {item.category === "content" && <span className="item-icon">üìö</span>}
                      {item.category === "utility" && <span className="item-icon">‚ö°</span>}
                      {item.category === "feature" && <span className="item-icon">‚öôÔ∏è</span>}
                    </div>
                    <div className="item-info">
                      <h4>{item.name}</h4>
                      <p>{item.description}</p>
                      <div className="item-price">ü™ô {item.price} coins</div>
                      <button 
                        onClick={() => buyItem(item)}
                        disabled={user?.coins < item.price}
                        className={user?.coins >= item.price ? "buy-btn" : "disabled-btn"}
                      >
                        {user?.coins >= item.price ? "Purchase" : "Not Enough Coins"}
                      </button>
                    </div>
                  </div>
                ))
              ) : (
                <div className="empty-state">
                  <p>No items in this category yet.</p>
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === "profile" && (
          <div className="profile-tab">
            <div className="profile-header">
              <div className="profile-avatar">
                <div className="avatar-image">
                  {user?.name?.charAt(0) || 'U'}
                </div>
                <div className="profile-badge">
                  <span>üî• {user?.streak || 0}</span>
                </div>
              </div>
              <div className="profile-info">
                <h2>{user?.name}</h2>
                <p className="member-since">Active Member</p>
                
                <div className="profile-stats">
                  <div className="stat">
                    <span className="stat-value">{user?.streak || 0}</span>
                    <span className="stat-label">Day Streak</span>
                  </div>
                  <div className="stat">
                    <span className="stat-value">{user?.coins || 0}</span>
                    <span className="stat-label">Mood Coins</span>
                  </div>
                  <div className="stat">
                    <span className="stat-value">{userTribes.length}</span>
                    <span className="stat-label">Tribes Joined</span>
                  </div>
                  <div className="stat">
                    <span className="stat-value">{journalEntries.length}</span>
                    <span className="stat-label">Journal Entries</span>
                  </div>
                </div>
              </div>
            </div>

            <div className="profile-section">
              <h3>üî• Streak Progress</h3>
              <div className="streak-progress">
                <div className="streak-days">
                  {[7, 30, 90, 180, 365].map(day => (
                    <div key={`milestone-${day}`} className={`streak-milestone ${user?.streak >= day ? 'achieved' : ''}`}>
                      <span className="milestone-day">{day}</span>
                      <span className="milestone-label">{day} Days</span>
                    </div>
                  ))}
                </div>
                <div className="streak-bar">
                  <div 
                    className="streak-progress-bar" 
                    style={{width: `${Math.min(100, (user?.streak / 365) * 100)}%`}}
                  ></div>
                </div>
                <div className="current-streak">
                  <span>Current Streak: {user?.streak || 0} days</span>
                  <span>Next milestone: {user?.streak < 7 ? 7 : user?.streak < 30 ? 30 : user?.streak < 90 ? 90 : user?.streak < 180 ? 180 : 365} days</span>
                </div>
              </div>
            </div>

            <div className="profile-section">
              <h3>üèÖ Achievements & Badges</h3>
              <div className="achievements-grid">
                {achievements.map(achievement => (
                  <div key={`achievement-${achievement.id}`} className={`achievement ${achievement.earned ? 'earned' : 'locked'}`}>
                    <span className="achievement-icon">{achievement.icon}</span>
                    <div className="achievement-info">
                      <h4>{achievement.name}</h4>
                      <p>{achievement.description}</p>
                    </div>
                    <span className="achievement-status">
                      {achievement.earned ? '‚úì' : 'üîí'}
                    </span>
                  </div>
                ))}
              </div>
            </div>

            <div className="profile-section">
              <h3>üë• My Tribes</h3>
              {userTribes.length === 0 ? (
                <p>You haven't joined any tribes yet. Visit the Community tab to join one!</p>
              ) : (
                <div className="user-tribes">
                  {userTribes.map(tribe => (
                    <div key={`user-tribe-${tribe.id}`} className="user-tribe">
                      <span>{tribe.name}</span>
                      <button onClick={() => setActiveTribe(tribe)}>
                        Visit
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>

            <div className="profile-section">
              <h3>‚öôÔ∏è Settings</h3>
              <div className="settings-grid">
                <div className="setting-item">
                  <span>Voice Responses</span>
                  <label className="switch">
                    <input type="checkbox" checked={audioEnabled} onChange={toggleAudio} />
                    <span className="slider"></span>
                  </label>
                </div>
                <div className="setting-item">
                  <span>Background Music</span>
                  <label className="switch">
                    <input type="checkbox" checked={musicEnabled} onChange={toggleMusic} />
                    <span className="slider"></span>
                  </label>
                </div>
                <div className="setting-item">
                  <span>AI Provider</span>
                  <select value={aiProvider} onChange={(e) => switchAiProvider(e.target.value)}>
                    <option value="gemini">Gemini AI</option>
                    <option value="fallback">Fallback Mode</option>
                  </select>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
