      <nav className="app-nav">
        <button className={activeTab === "chat" ? "active" : ""} onClick={() => setActiveTab("chat")}>üí¨ Chat</button>
        <button className={activeTab === "journal" ? "active" : ""} onClick={() => setActiveTab("journal")}>üìì Journal</button>
        <button className={activeTab === "therapy" ? "active" : ""} onClick={() => setActiveTab("therapy")}>üë®‚Äç‚öïÔ∏è Therapy</button>
        <button className={activeTab === "community" ? "active" : ""} onClick={() => setActiveTab("community")}>üåç Community</button>
        <button className={activeTab === "shop" ? "active" : ""} onClick={() => setActiveTab("shop")}>üõí Shop</button>
        <button className={activeTab === "profile" ? "active" : ""} onClick={() => setActiveTab("profile")}>üë§ Profile</button>
      </nav>
  
      <main className="app-main">
        {activeTab === "chat" && (
          <div className="chat-tab">
            <div className="chat-content-wrapper">
              <QuickActionsMenu />
              <div className="chat-box">
                {messages.map((msg, i) => (
                  <div key={i} className={`chat-message ${msg.sender} ${msg.isReward ? 'reward' : ''} ${msg.isExercise ? 'exercise' : ''}`}>
                    <div className="avatar">{msg.sender === "bot" ? "ü§ñ" : "üë§"}</div>
                    <div className="bubble">
                      <p>{msg.text}</p>
                      {msg.moodEmoji && (
                        <div className="mood-indicator">
                          {msg.moodEmoji} {msg.phrase}
                        </div>
                      )}
                    </div>
                  </div>
                ))}
                {isTyping && (
                  <div className="chat-message bot">
                    <div className="avatar bot-avatar">ü§ñ</div>
                    <div className="bubble typing">...</div>
                  </div>
                )}
                <div ref={chatEndRef}></div>
              </div>
              <div className="input-area">
                <input
                  type="text"
                  placeholder="How are you feeling today?"
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                  ref={inputRef}
                />
                <button onClick={sendMessage} className="send-btn">Send</button>
              </div>
            </div>
          </div>
        )}
        {activeTab === "journal" && (
          <div className="journal-tab">
            <div className="journal-header">
              <h2>Your Journal</h2>
              <div className="journal-actions">
                <button onClick={addJournalEntry} className="add-entry-btn">+ New Entry</button>
                <button onClick={() => setShowJournalAnalysis(true)} className="analyze-btn">üìä Analyze</button>
              </div>
            </div>
            <div className="journal-entries">
              {journalEntries.length === 0 ? (
                <div className="empty-state">
                  <p>No journal entries yet. Write your first entry!</p>
                  <button onClick={addJournalEntry}>Start Journaling</button>
                </div>
              ) : (
                journalEntries.map(entry => (
                  <div key={entry.id} className="journal-entry">
                    <div className="entry-header">
                      <div className="entry-date">{entry.date}</div>
                      <div className="entry-actions">
                        {!entry.shared && userTribes.length > 0 && (
                          <button onClick={() => shareJournalEntry(entry.id)} className="share-btn">Share</button>
                        )}
                        <button className="edit-btn">Edit</button>
                      </div>
                    </div>
                    <div className="entry-text">{entry.text}</div>
                    {entry.mood && <div className="entry-mood"><strong>Mood:</strong> {entry.mood}</div>}
                  </div>
                ))
              )}
            </div>
          </div>
        )}
        {activeTab === "therapy" && (
          <div className="therapy-tab">
            <div className="resources-section">
              <h3>üÜò Crisis Resources</h3>
              <div className="resources-grid">
                {resources.map(resource => (
                  <div key={resource.number} className="resource-card">
                    <h4>{resource.name}</h4>
                    <p>{resource.number}</p>
                    <button onClick={() => window.open(`tel:${resource.number.replace(/\D/g, '')}`)}>üìû Call Now</button>
                  </div>
                ))}
              </div>
            </div>
            <div className="therapists-section">
              <h3>üë®‚Äç‚öïÔ∏è Professional Therapists</h3>
              <div className="therapists-grid">
                {therapists.map(therapist => (
                  <div key={therapist.id} className="therapist-card">
                    <div className="therapist-info">
                      <h4>{therapist.name}</h4>
                      <p>{therapist.specialization}</p>
                      <p>‚≠ê {therapist.rating} ‚Ä¢ {therapist.experience}</p>
                      <p>ü™ô {therapist.price} coins/session</p>
                    </div>
                    <div className="therapist-actions">
                      <button onClick={() => bookTherapist(therapist)} className="book-btn">Book Session</button>
                      <button onClick={() => setShowTherapistInfo(therapist)} className="info-btn">More Info</button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
        {activeTab === "community" && activeTribe ? (
          <TribeCommunity
            activeTribe={activeTribe}
            setActiveTribe={setActiveTribe}
            tribeChatMessages={tribeChatMessages}
            tribeInput={tribeInput}
            setTribeInput={setTribeInput}
            sendTribeMessage={sendTribeMessage}
            tribeChatEndRef={tribeChatEndRef}
            moodTribes={moodTribes}
            setMoodTribes={setMoodTribes}
            userTribes={userTribes}
            setUserTribes={setUserTribes}
          />
        ) : activeTab === "community" && (
          <div className="community-tab">
            <div className="community-layout">
              <div className="tribes-section">
                <h3>üéØ Mood Tribes</h3>
                <div className="tribes-grid">
                  {moodTribes.map(tribe => (
                    <div key={tribe.id} className="tribe-card">
                      <h4>{tribe.name}</h4>
                      <p>{tribe.description}</p>
                      <p className="member-count">üë• {tribe.member_count} members</p>
                      <button onClick={() => tribe.isMember ? setActiveTribe(tribe) : joinTribe(tribe.id)} className={tribe.isMember ? "joined-btn" : "join-btn"}>
                        {tribe.isMember ? "Visit Community" : "Join Tribe"}
                      </button>
                    </div>
                  ))}
                </div>
              </div>
              <div className="challenges-section">
                <h3>üèÜ Community Challenges</h3>
                <div className="challenges-grid">
                  {moodChallenges.map(challenge => (
                    <div key={challenge.id} className="challenge-card">
                      <h4>{challenge.title}</h4>
                      <p className="challenge-duration">{challenge.duration}</p>
                      <p>{challenge.description}</p>
                      <div className="challenge-reward">{challenge.reward}</div>
                      <button onClick={() => startChallenge(challenge.id)} className="join-challenge-btn">Join Challenge</button>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        )}
        {activeTab === "shop" && (
          <div className="shop-tab">
            <h2>üõí MoodMate Shop</h2>
            <div className="shop-grid">
              {shopItems.map(item => {
                const isOwned = userData.purchasedItems.includes(item.id);
                const isThemeOwned = item.category === "theme" && userData.ownedThemes.includes(item.name.toLowerCase().replace(' theme', ''));
                const isAvatarOwned = item.category === "avatar" && userData.purchasedItems.includes(item.id);
                return (
                  <div key={item.id} className="shop-item-card">
                    <div className="item-info">
                      <h4>{item.name}</h4>
                      <p>{item.description}</p>
                      <div className="item-price">ü™ô {item.price} coins</div>
                      <div className="item-actions">
                        {(isOwned || isThemeOwned || isAvatarOwned) ? (
                          <>
                            {item.category === "theme" && (
                              <button
                                onClick={() => applyTheme(item.name.toLowerCase().replace(' theme', ''))}
                                className={userData.currentTheme === item.name.toLowerCase().replace(' theme', '') ? "applied-btn" : "apply-btn"}
                              >
                                {userData.currentTheme === item.name.toLowerCase().replace(' theme', '') ? "Applied" : "Apply"}
                              </button>
                            )}
                            {item.category === "avatar" && (
                              <button
                                onClick={() => applyAvatar(item.name)}
                                className={userData.avatar === item.name ? "applied-btn" : "apply-btn"}
                              >
                                {userData.avatar === item.name ? "Applied" : "Apply"}
                              </button>
                            )}
                            {(item.category !== "theme" && item.category !== "avatar") && (
                              <button className="owned-btn">Owned</button>
                            )}
                          </>
                        ) : (
                          <button onClick={() => buyItem(item)} className="buy-btn">Purchase</button>
                        )}
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        )}
        {activeTab === "profile" && (
          <div className="profile-tab">
            <div className="profile-header">
              <div className="profile-avatar"><div className="avatar-image">{userData.avatar.charAt(0)}</div></div>
              <div className="profile-info"><h2>{userData.name}</h2></div>
            </div>
            <div className="profile-content">
              <div className="profile-section">
                <h3>üî• My Stats</h3>
                <div className="profile-stats">
                  <div className="stat"><span className="stat-value">{userData.streak}</span><span className="stat-label">Day Streak</span></div>
                  <div className="stat"><span className="stat-value">{userData.coins}</span><span className="stat-label">Mood Coins</span></div>
                  <div className="stat"><span className="stat-value">{journalEntries.length}</span><span className="stat-label">Journal Entries</span></div>
                </div>
              </div>
              <div className="profile-section">
                <h3>üèÖ Achievements</h3>
                <div className="achievements-grid">
                  {achievements.map(achievement => (
                    <div key={achievement.id} className={`achievement ${userData.achievements.includes(achievement.id) ? 'earned' : 'locked'}`}>
                      <span className="achievement-icon">{achievement.icon}</span>
                      <div className="achievement-info">
                        <h4>{achievement.name}</h4>
                        <p>{achievement.description}</p>
                      </div>
                      <span className="achievement-status">{userData.achievements.includes(achievement.id) ? '‚úì' : 'üîí'}</span>
                    </div>
                  ))}
                </div>
              </div>
              <div className="profile-section">
                <h3>‚öôÔ∏è Settings</h3>
                <div className="settings-grid">
                  <div className="setting-item"><span>Voice Responses</span><label className="switch"><input type="checkbox" checked={audioEnabled} onChange={toggleAudio} /><span className="slider"></span></label></div>
                  <div className="setting-item"><span>Background Music</span><label className="switch"><input type="checkbox" checked={musicEnabled} onChange={toggleMusic} /><span className="slider"></span></label></div>
                  <div className="setting-item">
                    <span>AI Provider</span>
                    <select value={aiProvider} onChange={(e) => switchAiProvider(e.target.value)}>
                      <option value="gemini">Gemini AI</option>
                      <option value="fallback">Fallback Mode</option>
                    </select>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

const TribeCommunity = ({ activeTribe, setActiveTribe, tribeChatMessages, tribeInput, setTribeInput, sendTribeMessage, tribeChatEndRef }) => {
  if (!activeTribe) return null;
  const tribeChat = tribeChatMessages[activeTribe.id] || [];
  return (
    <div className="tribe-community">
      <div className="tribe-header">
        <button onClick={() => setActiveTribe(null)} className="back-btn">‚Üê Back to Communities</button>
        <h2>{activeTribe.name} Community</h2>
      </div>
      <div className="tribe-content">
        <div className="tribe-chat">
          <div className="tribe-messages">
            {tribeChat.map(msg => (
              <div key={msg.id} className="tribe-message">
                <div className="message-avatar">{msg.user_name.charAt(0)}</div>
                <div className="message-content">
                  <span className="message-sender">{msg.user_name}</span>
                  <p>{msg.message}</p>
                </div>
              </div>
            ))}
            <div ref={tribeChatEndRef}></div>
          </div>
          <div className="tribe-input">
            <input type="text" placeholder="Share something with the community..." value={tribeInput} onChange={e => setTribeInput(e.target.value)} onKeyDown={e => e.key === "Enter" && sendTribeMessage(activeTribe.id)} />
            <button onClick={() => sendTribeMessage(activeTribe.id)}>Send</button>
          </div>
        </div>
      </div>
    </div>
  );
};

const BubbleGame = ({ bubbleGame, popBubble, exitGame }) => (
  <div className="game-container">
    <div className="game-header"><h2>Bubble Pop</h2><button onClick={exitGame} className="exit-game-btn">√ó</button></div>
    <div className="game-score">Score: {bubbleGame.score}</div>
    <div className="bubble-game-area">
      {bubbleGame.bubbles.map(bubble => !bubble.popped && (
        <div key={bubble.id} className="bubble" style={{ left: `${bubble.x}%`, top: `${bubble.y}%`, width: `${bubble.size}px`, height: `${bubble.size}px` }} onClick={() => popBubble(bubble.id)}></div>
      ))}
    </div>
    <div className="game-instructions">Click on the bubbles to pop them!</div>
  </div>
);

const ColoringGame = ({ coloringImage, selectedColor, setSelectedColor, exitGame }) => (
  <div className="game-container">
    <div className="game-header"><h2>Mindful Coloring</h2><button onClick={exitGame} className="exit-game-btn">√ó</button></div>
    <div className="coloring-game-area">
      {coloringImage && (
        <>
          <div className="coloring-canvas"><div className="coloring-pattern"></div></div>
          <div className="color-palette">
            {coloringImage.colors.map(color => (
              <div key={color} className={`color-option ${selectedColor === color ? 'selected' : ''}`} style={{ backgroundColor: color }} onClick={() => setSelectedColor(color)}></div>
            ))}
          </div>
          <div className="coloring-instructions"><p>Choose a color and click to fill areas</p></div>
        </>
      )}
    </div>
  </div>
);

const StressBalloonGame = ({ stressBalloon, inflateBalloon, releaseBalloon, exitGame }) => (
  <div className="game-container">
    <div className="game-header"><h2>Stress Balloon</h2><button onClick={exitGame} className="exit-game-btn">√ó</button></div>
    <div className="balloon-game-area">
      <div className="balloon" style={{ transform: `scale(${1 + stressBalloon.progress / 100})`, opacity: 1 - (stressBalloon.progress / 200) }} onMouseDown={inflateBalloon} onMouseUp={releaseBalloon}></div>
      <div className="balloon-instructions">{stressBalloon.progress < 100 ? (<p>Press and hold to inflate the balloon</p>) : (<p>Release to pop the balloon and release stress</p>)}</div>
    </div>
  </div>
);

const GratitudeWheelGame = ({ gratitudeWheel, startGratitudeWheel, exitGame }) => (
  <div className="game-container">
    <div className="game-header"><h2>Gratitude Wheel</h2><button onClick={exitGame} className="exit-game-btn">√ó</button></div>
    <div className="wheel-game-area">
      <div className={`gratitude-wheel ${gratitudeWheel.spinning ? 'spinning' : ''}`}></div>
      {gratitudeWheel.result && (<div className="wheel-result"><h3>Today's Prompt:</h3><p>{gratitudeWheel.result}</p></div>)}
      <button onClick={startGratitudeWheel} disabled={gratitudeWheel.spinning} className="spin-btn">{gratitudeWheel.spinning ? 'Spinning...' : 'Spin Wheel'}</button>
    </div>
  </div>
);

const MeditationGuideModal = ({ showMeditationGuide, setShowMeditationGuide, startBreathingExercise, meditationExercises }) => {
  if (!showMeditationGuide) return null;
  return (
    <div className="modal-overlay">
      <div className="modal-content large">
        <div className="modal-header">
          <h2>üßò Meditation & Breathing Exercises</h2>
          <button onClick={() => setShowMeditationGuide(false)} className="close-btn">√ó</button>
        </div>
        <div className="meditation-grid">
          {meditationExercises.map(exercise => (
            <div key={exercise.id} className="meditation-card">
              <h3>{exercise.title}</h3>
              <p className="duration">{exercise.duration}</p>
              <p className="description">{exercise.description}</p>
              <button onClick={() => { setShowMeditationGuide(false); startBreathingExercise(exercise.id); }} className="start-btn">Start Exercise</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

const ChallengeModal = ({ showChallengeModal, setShowChallengeModal, activeChallenge, updateChallengeProgress }) => {
  if (!showChallengeModal || !activeChallenge) return null;
  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>üèÜ {activeChallenge.title}</h2>
          <button onClick={() => setShowChallengeModal(false)} className="close-btn">√ó</button>
        </div>
        <div className="challenge-details">
          <p className="challenge-description">{activeChallenge.description}</p>
          <div className="challenge-progress">
            <h4>Progress: {activeChallenge.progress}%</h4>
            <div className="progress-bar">
              <div className="progress-fill" style={{ width: `${activeChallenge.progress}%` }}></div>
            </div>
          </div>
          <button onClick={updateChallengeProgress} className="btn-primary">
            {activeChallenge.progress > 0 ? "Update Progress" : "Start Challenge"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default App;
