import React, { useState } from 'react';
const API_BASE_URL = 'http://127.0.0.1:5000';

// This component now takes an `onLogin` prop from App.jsx
function Auth({ onLogin }) {
  const [isSignup, setIsSignup] = useState(false);
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: "",
    loginId: "" // This field will be used for email/phone on the login form
  });
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setMessage("");
    setIsLoading(true);

    let endpoint;
    let payload;

    if (isSignup) {
      if (formData.password !== formData.confirmPassword) {
        setError("Passwords do not match!");
        setIsLoading(false);
        return;
      }
      endpoint = `${API_BASE_URL}/api/register`;
      payload = {
        username: formData.username,
        email: formData.email,
        phone: formData.phone,
        password: formData.password,
      };
    } else {
      endpoint = `${API_BASE_URL}/api/login`;
      payload = {
        loginId: formData.loginId,
        password: formData.password,
      };
    }

    try {
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include', // This is crucial for session cookies to work
        body: JSON.stringify(payload),
      });
      const data = await response.json();

      if (response.ok) {
        if (isSignup) {
          setMessage(data.message || "Signup successful! Please log in.");
          setIsSignup(false); // Switch to login form
        } else {
          // On successful login, call the onLogin function from App.jsx
          onLogin(data.user);
        }
      } else {
        setError(data.message || data.error || "An error occurred.");
      }
    } catch (err) {
      setError('Failed to connect to the server. Please check if it is running.');
      console.error("Authentication error:", err);
    } finally {
      setIsLoading(false);
    }
  };

  const switchMode = () => {
    setIsSignup(!isSignup);
    setError("");
    setMessage("");
    setFormData({ // Reset form data on mode switch
      username: "", email: "", phone: "", password: "",
      confirmPassword: "", loginId: ""
    });
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        <h1>🌙 MoodMate</h1>
        <p>Your mental health companion</p>

        <form onSubmit={handleSubmit} className="auth-form">
          {isSignup && (
            <>
              <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} required disabled={isLoading} />
              <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required disabled={isLoading} />
              <input type="tel" name="phone" placeholder="Phone Number" value={formData.phone} onChange={handleChange} required disabled={isLoading} />
            </>
          )}

          {!isSignup && (
            <input type="text" name="loginId" placeholder="Email or Phone Number" value={formData.loginId} onChange={handleChange} required disabled={isLoading} />
          )}

          <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required disabled={isLoading} />

          {isSignup && (
            <input type="password" name="confirmPassword" placeholder="Confirm Password" value={formData.confirmPassword} onChange={handleChange} required disabled={isLoading} />
          )}

          {error && <div className="error-message">{error}</div>}
          {message && <div className="success-message">{message}</div>}

          <button type="submit" disabled={isLoading}>
            {isLoading ? "Processing..." : (isSignup ? "Sign Up" : "Login")}
          </button>
        </form>

        <p className="toggle-text">
          {isSignup ? "Already have an account? " : "New here? "}
          <span onClick={switchMode} className="toggle-link">
            {isSignup ? "Login" : "Sign Up"}
          </span>
        </p>
      </div>
    </div>
  );
}

export default Auth;
