import React, { useState } from "react";
import "./AppWrapper.css";

export default function AppWrapper({ onLoginSuccess }) {
  const [isLogin, setIsLogin] = useState(true);
  const [isForgot, setIsForgot] = useState(false);

  const API_URL = "http://127.0.0.1:5000"; // ‚úÖ Backend base URL

  // Form state
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: "",
    loginId: "",
    otp: ""
  });

  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ‚úÖ Signup request
  const handleSignup = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      setMessage("‚ùå Passwords do not match");
      return;
    }

    try {
      const res = await fetch(`${API_URL}/signup`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await res.json();
      setMessage(data.message);

      if (data.success) {
        setIsLogin(true); // Switch to login after signup
      }
    } catch (err) {
      setMessage("‚ö†Ô∏è Server error during signup");
    }
  };

  // ‚úÖ Login request
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${API_URL}/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          loginId: formData.loginId,
          password: formData.password,
        }),
      });

      const data = await res.json();
      setMessage(data.message);

      if (data.success) {
        onLoginSuccess(); // ‚úÖ redirect to main App.jsx
      }
    } catch (err) {
      setMessage("‚ö†Ô∏è Server error during login");
    }
  };

  // ‚úÖ Forgot Password ‚Üí request OTP
  const handleForgot = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${API_URL}/forgot`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ loginId: formData.loginId }),
      });

      const data = await res.json();
      setMessage(data.message);
    } catch (err) {
      setMessage("‚ö†Ô∏è Server error while sending OTP");
    }
  };

  // ‚úÖ Reset with OTP
  const handleReset = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${API_URL}/reset`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          loginId: formData.loginId,
          otp: formData.otp,
          newPassword: formData.password,
        }),
      });

      const data = await res.json();
      setMessage(data.message);

      if (data.success) {
        setIsForgot(false);
        setIsLogin(true); // Go back to login after reset
      }
    } catch (err) {
      setMessage("‚ö†Ô∏è Server error during reset");
    }
  };

  return (
    <div className="wrapper-container">
      <div className="auth-box">
        <h1>üåô MoodMate</h1>
        <p>Your Mental Health Companion</p>

        {message && <div className="message">{message}</div>}

        {/* LOGIN */}
        {isLogin && !isForgot && (
          <form onSubmit={handleLogin} className="auth-form">
            <h2>Login</h2>
            <input
              type="text"
              name="loginId"
              placeholder="Email or Phone"
              value={formData.loginId}
              onChange={handleChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <button type="submit">Login</button>
            <p className="toggle" onClick={() => setIsForgot(true)}>
              Forgot Password?
            </p>
            <p className="toggle" onClick={() => setIsLogin(false)}>
              New here? Sign up
            </p>
          </form>
        )}

        {/* SIGNUP */}
        {!isLogin && (
          <form onSubmit={handleSignup} className="auth-form">
            <h2>Sign Up</h2>
            <input
              type="text"
              name="username"
              placeholder="Full Name"
              value={formData.username}
              onChange={handleChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <input
              type="tel"
              name="phone"
              placeholder="Phone Number"
              value={formData.phone}
              onChange={handleChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <input
              type="password"
              name="confirmPassword"
              placeholder="Confirm Password"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
            />
            <button type="submit">Sign Up</button>
            <p className="toggle" onClick={() => setIsLogin(true)}>
              Already have an account? Login
            </p>
          </form>
        )}

        {/* FORGOT PASSWORD */}
        {isForgot && (
          <form onSubmit={handleForgot} className="auth-form">
            <h2>Forgot Password</h2>
            <input
              type="text"
              name="loginId"
              placeholder="Enter Email or Phone"
              value={formData.loginId}
              onChange={handleChange}
              required
            />
            <button type="submit">Send OTP</button>
            <input
              type="text"
              name="otp"
              placeholder="Enter OTP"
              value={formData.otp}
              onChange={handleChange}
            />
            <input
              type="password"
              name="password"
              placeholder="New Password"
              value={formData.password}
              onChange={handleChange}
            />
            <button onClick={handleReset}>Reset Password</button>
            <p className="toggle" onClick={() => setIsForgot(false)}>
              Back to Login
            </p>
          </form>
        )}
      </div>
    </div>
  );
}
