import React, { useState, useEffect, useRef } from "react";
import "./app.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [activeTab, setActiveTab] = useState("chat");
  const [shopItems, setShopItems] = useState([]);
  const [currentTheme, setCurrentTheme] = useState("default");
  const [typingIndicator, setTypingIndicator] = useState("");
  const [shopCategory, setShopCategory] = useState("all");

  const chatEndRef = useRef(null);

  // Sample shop items
  const sampleShopItems = [
    {id: 1, name: "Sunset Theme", description: "Warm orange and purple theme", price: 50, category: "theme"},
    {id: 2, name: "Ocean Theme", description: "Calming blue theme", price: 50, category: "theme"},
    {id: 3, name: "Forest Theme", description: "Green nature-inspired theme", price: 50, category: "theme"},
    {id: 4, name: "Neon Theme", description: "Vibrant neon colors", price: 75, category: "theme"},
    {id: 5, name: "Glass Theme", description: "Modern glassmorphism design", price: 100, category: "theme"},
    {id: 6, name: "Exclusive Avatar", description: "Special avatar frame", price: 100, category: "cosmetic"},
    {id: 7, name: "Meditation Pack", description: "Exclusive meditation guides", price: 75, category: "content"},
    {id: 8, name: "Second Chance Token", description: "Save your streak if you miss a day", price: 30, category: "utility"},
    {id: 9, name: "Custom Voice", description: "Personalized AI voice", price: 150, category: "feature"}
  ];

  // Auto scroll
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Load initial data
  useEffect(() => {
    setMessages([{
      text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
      sender: "bot"
    }]);
    
    setShopItems(sampleShopItems);
  }, []);

  // Simulate typing animation
  const simulateTyping = async () => {
    const typingPhrases = ["Thinking", "Thinking.", "Thinking..", "Thinking..."];
    for (let i = 0; i < 6; i++) {
      setTypingIndicator(typingPhrases[i % 4]);
      await new Promise(resolve => setTimeout(resolve, 300));
    }
    setTypingIndicator("");
  };

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMsg = { text: input, sender: "user" };
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);
    
    // Simulate typing animation
    simulateTyping();

    try {
      const response = await fetch("http://localhost:5000/api/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ message: input })
      });

      if (response.ok) {
        const data = await response.json();
        const botMsg = { 
          text: data.reply, 
          sender: "bot"
        };
        setMessages(prev => [...prev, botMsg]);
      } else {
        throw new Error("API request failed");
      }
    } catch (error) {
      console.log("Chat error:", error);
      // Fallback response
      const botMsg = { 
        text: "I'm here for you. Would you like to talk more about how you're feeling?", 
        sender: "bot"
      };
      setMessages(prev => [...prev, botMsg]);
    }

    setIsTyping(false);
    setTypingIndicator("");
  };

  const startNewChat = () => {
    setMessages([{
      text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
      sender: "bot"
    }]);
  };

  const buyItem = (item) => {
    if (window.confirm(`Purchase ${item.name} for ${item.price} coins?`)) {
      if (item.category === "theme") {
        setCurrentTheme(item.name.toLowerCase().replace(' theme', ''));
        localStorage.setItem('theme', item.name.toLowerCase().replace(' theme', ''));
        alert(`Theme applied! You're now using the ${item.name}.`);
      } else {
        alert(`Purchased ${item.name}!`);
      }
    }
  };

  const applyTheme = (themeName) => {
    setCurrentTheme(themeName);
    localStorage.setItem('theme', themeName);
  };

  const filteredShopItems = shopCategory === "all" 
    ? shopItems 
    : shopItems.filter(item => item.category === shopCategory);

  const Sidebar = () => {
    return (
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>MoodMate</h2>
        </div>
        
        <div className="sidebar-content">
          <div className="sidebar-section">
            <h3>Navigation</h3>
            <button 
              onClick={() => setActiveTab("chat")} 
              className={`sidebar-btn ${activeTab === "chat" ? "active" : ""}`}
            >
              💬 Chat
            </button>
            <button 
              onClick={() => setActiveTab("shop")} 
              className={`sidebar-btn ${activeTab === "shop" ? "active" : ""}`}
            >
              🛒 Shop
            </button>
          </div>
          
          <div className="sidebar-section">
            <h3>Quick Actions</h3>
            <button onClick={startNewChat} className="sidebar-btn">
              New Chat
            </button>
          </div>
          
          <div className="sidebar-section">
            <h3>Themes</h3>
            <button onClick={() => applyTheme("default")} className="sidebar-btn">
              Default Theme
            </button>
            <button onClick={() => applyTheme("sunset")} className="sidebar-btn">
              Sunset Theme
            </button>
            <button onClick={() => applyTheme("ocean")} className="sidebar-btn">
              Ocean Theme
            </button>
            <button onClick={() => applyTheme("forest")} className="sidebar-btn">
              Forest Theme
            </button>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className={`app-container ${currentTheme}-theme`}>
      <Sidebar />
      
      <header className="app-header">
        <div className="header-left">
          <h1>🌙 MoodMate</h1>
          <div className="user-stats">
            <span className="streak-display">🔥 5 day streak</span>
            <span>🪙 100 coins</span>
          </div>
        </div>
        <div className="header-right">
          <div className="user-info">
            <span className="user-name">Guest User</span>
            <span className="user-status">Online</span>
          </div>
        </div>
      </header>

      <nav className="app-nav">
        <button 
          className={activeTab === "chat" ? "active" : ""} 
          onClick={() => setActiveTab("chat")}
        >
          💬 Chat
        </button>
        <button 
          className={activeTab === "shop" ? "active" : ""} 
          onClick={() => setActiveTab("shop")}
        >
          🛒 Shop
        </button>
      </nav>

      <main className="app-main">
        {activeTab === "chat" && (
          <div className="chat-tab">
            <div className="chat-header">
              <h2>Chat with MoodMate</h2>
              <button onClick={startNewChat} className="new-chat-btn">
                + New Chat
              </button>
            </div>
            <div className="chat-box">
              {messages.map((msg, i) => (
                <div key={`msg-${i}-${Date.now()}`} className={`chat-message ${msg.sender}`}>
                  {msg.sender === "bot" && (
                    <div className="avatar bot-avatar">🤖</div>
                  )}
                  {msg.sender === "user" && (
                    <div className="avatar user-avatar">👤</div>
                  )}
                  <div className="bubble">
                    {msg.text}
                  </div>
                </div>
              ))}

              {isTyping && (
                <div className="chat-message bot">
                  <div className="avatar bot-avatar">🤖</div>
                  <div className="bubble typing">
                    {typingIndicator || "..."}
                  </div>
                </div>
              )}

              <div ref={chatEndRef}></div>
            </div>

            <div className="input-area">
              <input
                type="text"
                placeholder="How are you feeling today?"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
              />
              <button onClick={sendMessage} className="send-btn">Send</button>
            </div>
          </div>
        )}

        {activeTab === "shop" && (
          <div className="shop-tab">
            <h2>🛒 MoodMate Shop</h2>
            <p>Explore customization options for your MoodMate experience!</p>
            
            <div className="shop-categories">
              <button 
                className={`category-btn ${shopCategory === "all" ? "active" : ""}`} 
                onClick={() => setShopCategory("all")}
              >
                All Items
              </button>
              <button 
                className={`category-btn ${shopCategory === "theme" ? "active" : ""}`} 
                onClick={() => setShopCategory("theme")}
              >
                Themes
              </button>
              <button 
                className={`category-btn ${shopCategory === "cosmetic" ? "active" : ""}`} 
                onClick={() => setShopCategory("cosmetic")}
              >
                Cosmetics
              </button>
              <button 
                className={`category-btn ${shopCategory === "content" ? "active" : ""}`} 
                onClick={() => setShopCategory("content")}
              >
                Content
              </button>
              <button 
                className={`category-btn ${shopCategory === "utility" ? "active" : ""}`} 
                onClick={() => setShopCategory("utility")}
              >
                Utilities
              </button>
            </div>
            
            <div className="shop-grid">
              {filteredShopItems.map((item) => (
                <div key={`shop-item-${item.id}`} className="shop-item-card">
                  <div className="item-image">
                    {item.category === "theme" && <div className={`theme-preview ${item.name.toLowerCase().replace(' theme', '')}-theme`}></div>}
                    {item.category === "cosmetic" && <span className="item-icon">🎨</span>}
                    {item.category === "content" && <span className="item-icon">📚</span>}
                    {item.category === "utility" && <span className="item-icon">⚡</span>}
                  </div>
                  <div className="item-info">
                    <h4>{item.name}</h4>
                    <p>{item.description}</p>
                    <div className="item-price">🪙 {item.price} coins</div>
                    <button 
                      onClick={() => buyItem(item)}
                      className="buy-btn"
                    >
                      Purchase
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
