import React, { useState, useEffect, useRef } from "react";
import "./app.css";

// API Configuration
const API_CONFIG = {
  BASE_URL: 'http://localhost:5000',
  ENDPOINTS: {
    HEALTH: '/api/health',
    LOGIN: '/api/login',
    REGISTER: '/api/register',
    CHAT: '/api/chat',
    REFRESH: '/api/refresh',
    FORGOT_PASSWORD: '/api/forgot-password',
    RESET_PASSWORD: '/api/reset-password',
    JOURNAL: '/api/journal',
    MOODWALL: '/api/moodwall',
    MOODTRIBES: '/api/moodtribes',
    THERAPISTS: '/api/therapists',
    RESOURCES: '/api/resources',
    SHOP: '/api/shop',
    PROFILE: '/api/profile',
    SETTINGS: '/api/settings',
    CHAT_HISTORY: '/api/chat/history',
    NEW_CHAT: '/api/chat/new',
    CRISIS_SAFE: '/api/crisis/safe',
    CRISIS_HELP: '/api/crisis/help'
  }
};

// API Service
class ApiService {
  constructor() {
    this.baseURL = API_CONFIG.BASE_URL;
  }

  async request(endpoint, options = {}) {
    const url = `${this.baseURL}${endpoint}`;
    const token = localStorage.getItem('accessToken');
    
    const defaultOptions = {
      headers: {
        'Content-Type': 'application/json',
        ...(token && { Authorization: `Bearer ${token}` }),
        ...options.headers,
      },
    };

    try {
      const response = await fetch(url, { ...defaultOptions, ...options });
      
      if (response.status === 401) {
        // Token expired, try to refresh
        const newToken = await this.refreshToken();
        if (newToken) {
          // Retry the request with new token
          defaultOptions.headers.Authorization = `Bearer ${newToken}`;
          const retryResponse = await fetch(url, { ...defaultOptions, ...options });
          return await this.handleResponse(retryResponse);
        }
      }
      
      return await this.handleResponse(response);
    } catch (error) {
      console.error('API request failed:', error);
      throw new Error('Network error. Please check your connection.');
    }
  }

  async handleResponse(response) {
    if (response.ok) {
      const contentType = response.headers.get('content-type');
      if (contentType && contentType.includes('application/json')) {
        return await response.json();
      }
      return await response.text();
    } else {
      const error = await response.text();
      throw new Error(error || `Request failed with status ${response.status}`);
    }
  }

  async refreshToken() {
    try {
      const refreshToken = localStorage.getItem('refreshToken');
      if (!refreshToken) {
        throw new Error("No refresh token available");
      }

      const response = await fetch(`${this.baseURL}${API_CONFIG.ENDPOINTS.REFRESH}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ refreshToken })
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('accessToken', data.token);
        return data.token;
      } else {
        console.error("Token refresh failed with status:", response.status);
        throw new Error("Token refresh failed");
      }
    } catch (error) {
      console.error("Token refresh error:", error);
      throw error;
    }
  }

  // Auth endpoints
  async login(credentials) {
    return this.request(API_CONFIG.ENDPOINTS.LOGIN, {
      method: 'POST',
      body: JSON.stringify(credentials)
    });
  }

  async register(userData) {
    return this.request(API_CONFIG.ENDPOINTS.REGISTER, {
      method: 'POST',
      body: JSON.stringify(userData)
    });
  }

  async forgotPassword(email) {
    return this.request(API_CONFIG.ENDPOINTS.FORGOT_PASSWORD, {
      method: 'POST',
      body: JSON.stringify({ email })
    });
  }

  async resetPassword(token, password, confirmPassword) {
    return this.request(API_CONFIG.ENDPOINTS.RESET_PASSWORD, {
      method: 'POST',
      body: JSON.stringify({ token, password, confirmPassword })
    });
  }

  // Chat endpoints
  async sendChatMessage(message) {
    return this.request(API_CONFIG.ENDPOINTS.CHAT, {
      method: 'POST',
      body: JSON.stringify({ message })
    });
  }

  async getChatHistory(limit = 50) {
    return this.request(`${API_CONFIG.ENDPOINTS.CHAT_HISTORY}?limit=${limit}`);
  }

  async newChat() {
    return this.request(API_CONFIG.ENDPOINTS.NEW_CHAT, {
      method: 'POST'
    });
  }

  // Other endpoints
  async getTherapists() {
    return this.request(API_CONFIG.ENDPOINTS.THERAPISTS);
  }

  async getResources() {
    return this.request(API_CONFIG.ENDPOINTS.RESOURCES);
  }

  async getMoodTribes() {
    return this.request(API_CONFIG.ENDPOINTS.MOODTRIBES);
  }

  async joinTribe(tribeId) {
    return this.request(`${API_CONFIG.ENDPOINTS.MOODTRIBES}/join`, {
      method: 'POST',
      body: JSON.stringify({ tribe_id: tribeId })
    });
  }

  async getShopItems() {
    return this.request(API_CONFIG.ENDPOINTS.SHOP);
  }

  async purchaseItem(itemId) {
    return this.request(`${API_CONFIG.ENDPOINTS.SHOP}/purchase`, {
      method: 'POST',
      body: JSON.stringify({ item_id: itemId })
    });
  }

  async getProfile() {
    return this.request(API_CONFIG.ENDPOINTS.PROFILE);
  }

  async getSettings() {
    return this.request(API_CONFIG.ENDPOINTS.SETTINGS);
  }

  async updateSettings(settings) {
    return this.request(API_CONFIG.ENDPOINTS.SETTINGS, {
      method: 'POST',
      body: JSON.stringify(settings)
    });
  }

  async markSafe() {
    return this.request(API_CONFIG.ENDPOINTS.CRISIS_SAFE, {
      method: 'POST'
    });
  }

  async requestHelp(helpType) {
    return this.request(API_CONFIG.ENDPOINTS.CRISIS_HELP, {
      method: 'POST',
      body: JSON.stringify({ type: helpType })
    });
  }
}

