    if (lastStreakUpdate === today) return;
    
    // Check if user has a second chance token
    const hasSecondChance = localStorage.getItem('secondChanceToken') === 'true';
    
    if (hasSecondChance) {
      // Use second chance token
      localStorage.removeItem('secondChanceToken');
      alert("You used a Second Chance Token to keep your streak! üî•");
    } else {
      // Update streak based on consecutive login
      const yesterday = new Date();
      yesterday.setDate(yesterday.getDate() - 1);
      
      if (lastStreakUpdate === yesterday.toDateString()) {
        // Consecutive login - increase streak
        const newStreak = (userObj.streak || 0) + 1;
        const updatedUser = {...userObj, streak: newStreak};
        setUser(updatedUser);
        localStorage.setItem('user', JSON.stringify(updatedUser));
        
        // Award coins based on streak length
        const coinsEarned = Math.min(5 + Math.floor(newStreak / 7), 20);
        const newCoins = (userObj.coins || 0) + coinsEarned;
        const updatedUserWithCoins = {...updatedUser, coins: newCoins};
        setUser(updatedUserWithCoins);
        localStorage.setItem('user', JSON.stringify(updatedUserWithCoins));
        
        // Check for achievement unlocks
        checkAchievements(newStreak);
      } else if (lastStreakUpdate && lastStreakUpdate !== today) {
        // Broken streak
        alert(`You broke your ${userObj.streak || 0}-day streak. Start a new one today!`);
        const updatedUser = {...userObj, streak: 1};
        setUser(updatedUser);
        localStorage.setItem('user', JSON.stringify(updatedUser));
      } else if (!lastStreakUpdate) {
        // First login - start streak
        const updatedUser = {...userObj, streak: 1};
        setUser(updatedUser);
        localStorage.setItem('user', JSON.stringify(updatedUser));
      }
    }
    
    // Update last streak update date
    setLastStreakUpdate(today);
    localStorage.setItem('lastStreakUpdate', today);
  };

  const checkAchievements = (streak) => {
    const newAchievements = [...achievements];
