      email: formData.get('email'),
      password: formData.get('password')
    };

    try {
      const data = await apiService.login(credentials);

      if (data.status === "success") {
        // Store both access and refresh tokens
        localStorage.setItem('accessToken', data.token);
        localStorage.setItem('refreshToken', data.refreshToken);
        
        // Map backend user properties to frontend expected structure
        const userData = {
          id: data.user.id,
          email: data.user.email,
          name: data.user.name,
          coins: data.user.coins,
          streak: data.user.streak,
          mentalFitness: data.user.mentalFitness || 75
        };
        
        localStorage.setItem('user', JSON.stringify(userData));
        localStorage.setItem('lastStreakUpdate', new Date().toDateString());
        setUser(userData);
        setIsLoggedIn(true);
        setShowLogin(false);
        loadInitialData();
      } else {
        setLoginError(data.error || "Login failed");
      }
    } catch (error) {
      console.error("Login error:", error);
      setLoginError(error.message || "Login failed. Please try again.");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setRegisterError("");
    const formData = new FormData(e.target);
    const userData = {
      name: formData.get('name'),
      email: formData.get('email'),
      password: formData.get('password'),
      confirmPassword: formData.get('confirmPassword')
    };

    try {
      const data = await apiService.register(userData);

      if (data.status === "success") {
        // Store both access and refresh tokens
        localStorage.setItem('accessToken', data.token);
        localStorage.setItem('refreshToken', data.refreshToken);
        
        // Map backend user properties to frontend expected structure
        const userObj = {
          id: data.user.id,
          email: data.user.email,
          name: data.user.name,
          coins: data.user.coins,
          streak: data.user.streak,
          mentalFitness: data.user.mentalFitness || 75
        };
        
        localStorage.setItem('user', JSON.stringify(userObj));
        localStorage.setItem('lastStreakUpdate', new Date().toDateString());
        setUser(userObj);
        setIsLoggedIn(true);
        setShowRegister(false);
        setShowLogin(false);
        loadInitialData();
      } else {
        setRegisterError(data.error || "Registration failed");
      }
    } catch (error) {
      console.error("Registration error:", error);
      setRegisterError(error.message || "Registration failed. Please try again.");
    }
  };

  const handleForgotPassword = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const email = formData.get('email');
    
    try {
      const data = await apiService.forgotPassword(email);

      if (data.status === "success") {
        alert(data.message);
        setShowForgotPassword(false);
        setShowLogin(true);
      } else {
        alert(data.error || "Failed to send reset instructions");
      }
    } catch (error) {
      console.error("Forgot password error:", error);
      alert(error.message || "Password reset feature is not available right now.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    localStorage.removeItem('refreshToken');
    localStorage.removeItem('user');
    localStorage.removeItem('lastStreakUpdate');
    setUser(null);
    setIsLoggedIn(false);
    setShowLogin(true);
    setMessages([]);
    setSidebarOpen(false);
  };

  const startNewChat = async () => {
    try {
      await apiService.newChat();
      setMessages([{
        text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
        sender: "bot"
      }]);
      setShowNewChatModal(false);
    } catch (error) {
      console.error("Error starting new chat:", error);
      // Fallback to local reset
      setMessages([{
        text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
        sender: "bot"
      }]);
      setShowNewChatModal(false);
    }
  };

  const sendMessage = async () => {
    if (!input.trim() || !isLoggedIn) return;

    const userMsg = { text: input, sender: "user" };
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);
    
    // Check for emergency keywords
    const emergencyKeywords = ['suicide', 'kill myself', 'end it all', 'want to die', 'self harm', 'hurt myself'];
    const hasEmergency = emergencyKeywords.some(keyword => 
      input.toLowerCase().includes(keyword)
    );
    
    if (hasEmergency) {
      setShowEmergencyModal(true);
      setIsTyping(false);
      return;
    }
    
    // Simulate typing animation
    simulateTyping();

    try {
      const data = await apiService.sendChatMessage(input);
      
      if (data.status === "crisis") {
        setShowEmergencyModal(true);
        setIsTyping(false);
        return;
      }
      
      handleChatResponse(data);
    } catch (error) {
      console.log("Chat error:", error);
      // Fallback to diverse AI responses based on mood detection
      const mood = detectMoodFromInput(input);
      const moodResponses = aiResponses[mood] || aiResponses.neutral;
      const randomResponse = moodResponses[Math.floor(Math.random() * moodResponses.length)];
      
      const botMsg = { 
        text: randomResponse, 
        sender: "bot",
        mood: mood,
        moodEmoji: getMoodEmoji(mood),
        phrase: getMoodPhrase(mood)
      };
      setMessages(prev => [...prev, botMsg]);
    }

    setIsTyping(false);
    setTypingIndicator("");
  };

  const handleChatResponse = (data) => {
    const botMsg = { 
      text: data.reply, 
      sender: "bot",
      mood: data.mood,
      moodEmoji: data.moodEmoji,
      phrase: data.phrase
    };
    setMessages(prev => [...prev, botMsg]);

    // Update user data if coins were earned
    if (data.coinsEarned > 0) {
      const newCoins = (user?.coins || 0) + data.coinsEarned;
      const updatedUser = {...user, coins: newCoins, streak: data.streak};
      setUser(updatedUser);
      localStorage.setItem('user', JSON.stringify(updatedUser));
    }
  };

  const detectMoodFromInput = (text) => {
    const textLower = text.toLowerCase();
    if (textLower.includes('happy') || textLower.includes('good') || textLower.includes('great') || textLower.includes('excited')) return 'happy';
    if (textLower.includes('sad') || textLower.includes('depressed') || textLower.includes('unhappy') || textLower.includes('cry')) return 'sad';
