import React from 'react';

function Profile({ user }) {
  return (
    <div className="tab-content profile-tab">
      <div className="profile-header">
        <div className="profile-avatar">
          <div className="avatar-image">{user.username.charAt(0).toUpperCase()}</div>
        </div>
        <div className="profile-info">
          <h2>{user.username}</h2>
          <p className="member-since">Joined on {new Date().toLocaleDateString()}</p>
        </div>
      </div>

      <div className="profile-stats-grid">
        <div className="stat-card">
          <h3>Streak</h3>
          <p>ðŸ”¥ {user.streak}</p>
        </div>
        <div className="stat-card">
          <h3>Coins</h3>
          <p>ðŸª™ {user.coins}</p>
        </div>
        <div className="stat-card">
          <h3>Premium</h3>
          <p>{user.is_premium ? 'ðŸŒŸ Active' : 'Basic'}</p>
        </div>
      </div>
      
      <div className="profile-section">
        <h3>My Items</h3>
        <div className="items-grid">
          {user.purchasedItems && user.purchasedItems.length > 0 ? (
            user.purchasedItems.map(itemId => {
                // In a real app, you'd have a full item list to map IDs to names
                return <div key={itemId} className="item-chip">Item #{itemId}</div>;
            })
          ) : (
            <p>You haven't purchased any items yet.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Profile;
