import React, { useState, useRef, useEffect } from 'react';
const API_BASE_URL = 'http://127.0.0.1:5000';

// Placed here for demonstration; in a real app, this would be fetched
const sampleTherapists = [
    {id: 1, name: "Dr. Arnav Singh", specialization: "Clinical Psychology", price: 799, rating: 5.0, experience: "8 years", avatar: "üë®‚Äç‚öïÔ∏è"},
    {id: 2, name: "Aaryan Kumar", specialization: "Counseling Psychology", price: 599, rating: 4.5, experience: "5 years", avatar: "üë®‚Äçüíº"},
    {id: 3, name: "Ankur Verma", specialization: "Psychiatry", price: 999, rating: 4.8, experience: "12 years", avatar: "üë®‚Äç‚öïÔ∏è"},
    {id: 4, name: "Aanchal", specialization: "Art Therapy", price: 499, rating: 4.6, experience: "4 years", avatar: "üë©‚Äçüé®"},
];
const sampleDoctorAvailability = {
    1: ["Mon 2-5PM", "Wed 10AM-1PM", "Fri 4-7PM"],
    2: ["Tue 9AM-12PM", "Thu 3-6PM", "Sat 11AM-2PM"],
    3: ["Mon 9AM-12PM", "Wed 2-5PM", "Fri 10AM-1PM"],
    4: ["Tue 1-4PM", "Thu 5-8PM", "Sat 3-6PM"],
};

function Therapy({ user, onUpdateUser }) {
    const [therapists, setTherapists] = useState(sampleTherapists);
    const [showTherapistForm, setShowTherapistForm] = useState(false);
    const [selectedTherapist, setSelectedTherapist] = useState(null);
    const [bookingForm, setBookingForm] = useState({ name: "", contact: "", problem: "", preferredTime: "" });
    const [showPaymentModal, setShowPaymentModal] = useState(false);
    const inputRef = useRef(null);

    useEffect(() => {
        if (showTherapistForm && inputRef.current) {
            inputRef.current.focus();
        }
    }, [showTherapistForm]);

    const handleFormChange = (e) => {
        const { name, value } = e.target;
        setBookingForm(prev => ({ ...prev, [name]: value }));
    };

    const bookTherapist = (therapist) => {
        if (user.coins < therapist.price) {
            alert("Not enough coins to book this therapist!");
            return;
        }
        setSelectedTherapist(therapist);
        setShowTherapistForm(true);
    };

    const confirmBooking = async () => {
        if (!selectedTherapist || !bookingForm.name || !bookingForm.contact || !bookingForm.problem || !bookingForm.preferredTime) {
            alert("Please fill all required fields.");
            return;
        }

        try {
            const response = await fetch(`${API_BASE_URL}/api/therapist/book`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                credentials: 'include',
                body: JSON.stringify({ therapist: selectedTherapist, booking_details: bookingForm })
            });
            const data = await response.json();

            if (response.ok) {
                onUpdateUser({ coins: data.new_balance }); // Update user coins in App.jsx
                setShowTherapistForm(false);
                setShowPaymentModal(true);
                setTimeout(() => {
                    setShowPaymentModal(false);
                    alert(`‚úÖ Booking successful with ${selectedTherapist.name}!`);
                    setBookingForm({ name: "", contact: "", problem: "", preferredTime: "" });
                }, 2000);
            } else {
                alert(`Booking failed: ${data.error}`);
            }
        } catch (error) {
            alert("An error occurred during booking.");
        }
    };

    return (
        <div className="tab-content therapy-tab">
            <h2>Find a Therapist</h2>
            <p>Connect with certified professionals for guidance and support.</p>
            <div className="therapists-grid">
                {therapists.map((therapist) => (
                    <div key={therapist.id} className="therapist-card">
                        <div className="therapist-avatar">{therapist.avatar}</div>
                        <h4>{therapist.name}</h4>
                        <p>{therapist.specialization}</p>
                        <p>‚≠ê {therapist.rating} ‚Ä¢ {therapist.experience}</p>
                        <p className="therapist-price">ü™ô {therapist.price} coins/session</p>
                        <button onClick={() => bookTherapist(therapist)} disabled={user.coins < therapist.price}>
                            {user.coins >= therapist.price ? "Book Session" : "Not Enough Coins"}
                        </button>
                    </div>
                ))}
            </div>

            {showTherapistForm && selectedTherapist && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <button onClick={() => setShowTherapistForm(false)} className="close-btn">√ó</button>
                        <h2>Book with {selectedTherapist.name}</h2>
                        <form className="therapist-form" onSubmit={(e) => { e.preventDefault(); confirmBooking(); }}>
                            <input name="name" value={bookingForm.name} onChange={handleFormChange} placeholder="Full Name *" required ref={inputRef} />
                            <input name="contact" value={bookingForm.contact} onChange={handleFormChange} placeholder="Contact (Email/Phone) *" required />
                            <textarea name="problem" value={bookingForm.problem} onChange={handleFormChange} placeholder="Briefly describe your concerns *" required />
                            <div className="form-group">
                                <label>Available Time Slots *</label>
                                <div className="time-slots">
                                    {sampleDoctorAvailability[selectedTherapist.id]?.map((slot, i) => (
                                        <button
                                            key={i} type="button"
                                            className={`time-slot-btn ${bookingForm.preferredTime === slot ? 'selected' : ''}`}
                                            onClick={() => handleFormChange({ target: { name: 'preferredTime', value: slot }})}
                                        >{slot}</button>
                                    )) || <p>No slots available.</p>}
                                </div>
                            </div>
                            <button type="submit">Confirm Booking (Cost: ü™ô {selectedTherapist.price})</button>
                        </form>
                    </div>
                </div>
            )}
            
            {showPaymentModal && (
                <div className="modal-overlay">
                    <div className="modal-content payment-modal">
                        <div className="payment-animation">
                            <div className="coin">ü™ô</div>
                            <div className="checkmark">‚úì</div>
                        </div>
                        <h2>Payment Successful!</h2>
                        <p>Your session is confirmed.</p>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Therapy;
