import React, { useState, useEffect, useRef } from "react";
import "./app.css";

// Base URL for API calls
const API_BASE_URL = 'http://127.0.0.1:5000';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLogin, setShowLogin] = useState(true);
  const [showRegister, setShowRegister] = useState(false);
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [activeTab, setActiveTab] = useState("chat");
  const [journalEntries, setJournalEntries] = useState([]);
  const [therapists, setTherapists] = useState([]);
  const [resources, setResources] = useState([]);
  const [moodWall, setMoodWall] = useState([]);
  const [moodTribes, setMoodTribes] = useState([]);
  const [shopItems, setShopItems] = useState([]);
  const [userTribes, setUserTribes] = useState([]);
  const [currentTheme, setCurrentTheme] = useState("default");
  const [typingIndicator, setTypingIndicator] = useState("");
  const [showEmergencyModal, setShowEmergencyModal] = useState(false);
  const [achievements, setAchievements] = useState([]);
  const [activeTribe, setActiveTribe] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [showNewChatModal, setShowNewChatModal] = useState(false);
  const [loginError, setLoginError] = useState("");
  const [registerError, setRegisterError] = useState("");
  const [aiProvider, setAiProvider] = useState("gemini");
  const [audioEnabled, setAudioEnabled] = useState(true);
  const [musicEnabled, setMusicEnabled] = useState(true);
  const [tribeChatMessages, setTribeChatMessages] = useState({});
  const [tribeInput, setTribeInput] = useState("");
  const [lastStreakUpdate, setLastStreakUpdate] = useState("");

  const chatEndRef = useRef(null);

  // Helper function to get authentication headers
  const getAuthHeaders = () => {
    const token = localStorage.getItem('token');
    return {
      'Content-Type': 'application/json',
      ...(token && { 'Authorization': `Bearer ${token}` })
    };
  };

  // Sample data
  const sampleTherapists = [
    {id: 1, name: "Dr. Priya Sharma", specialization: "Clinical Psychology", price: 799, rating: 4.7, experience: "8 years", avatar: "ðŸ‘©â€âš•ï¸"},
    {id: 2, name: "Rajiv Mehta", specialization: "Counseling Psychology", price: 599, rating: 4.5, experience: "5 years", avatar: "ðŸ‘¨â€âš•ï¸"},
    {id: 3, name: "Dr. Anjali Singh", specialization: "Psychiatry", price: 999, rating: 4.8, experience: "12 years", avatar: "ðŸ‘©â€âš•ï¸"},
    {id: 4, name: "Neha Gupta", specialization: "Art Therapy", price: 499, rating: 4.6, experience: "4 years", avatar: "ðŸŽ¨"}
  ];

  // ... rest of your sample data remains the same

  // Load saved session
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    const savedTheme = localStorage.getItem('theme') || 'default';
    const savedTribes = JSON.parse(localStorage.getItem('userTribes') || '[]');
    const savedAchievements = JSON.parse(localStorage.getItem('achievements') || '[]');
    const savedAiProvider = localStorage.getItem('aiProvider') || 'gemini';
    const savedAudio = localStorage.getItem('audioEnabled') !== 'false';
    const savedMusic = localStorage.getItem('musicEnabled') !== 'false';
    const savedLastStreakUpdate = localStorage.getItem('lastStreakUpdate') || '';
    
    if (token && userData) {
      const userObj = JSON.parse(userData);
      setUser(userObj);
      setIsLoggedIn(true);
      setShowLogin(false);
      setCurrentTheme(savedTheme);
      setUserTribes(savedTribes);
      setAchievements(savedAchievements);
      setAiProvider(savedAiProvider);
      setAudioEnabled(savedAudio);
      setMusicEnabled(savedMusic);
      setLastStreakUpdate(savedLastStreakUpdate);
      loadInitialData();
      
      // Update streak if it's a new day
      updateStreakAndCoins(userObj);
    }
  }, []);

  // Auto scroll
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Check backend connectivity on app load
  useEffect(() => {
    checkBackendConnectivity();
  }, []);

  const checkBackendConnectivity = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/health`);
      if (!response.ok) {
        console.warn("Backend server might be down");
      }
    } catch (error) {
      console.warn("Cannot connect to backend server", error);
    }
  };

  const handleApiError = (error) => {
    console.error("API Error:", error);
    if (error.message.includes("401") || error.message.includes("authentication")) {
      handleLogout();
      alert("Authentication failed. Please login again.");
    }
  };

  const loadInitialData = () => {
    setMessages([{
      id: Date.now(),
      text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
      sender: "bot",
      timestamp: new Date().toISOString()
    }]);
    
    // Use sample data since backend endpoints might not be available
    setTherapists(sampleTherapists);
    setResources(sampleResources);
    
    // Update tribes with user's membership status
    const userTribeIds = userTribes.map(t => t.id);
    const updatedTribes = sampleMoodTribes.map(tribe => ({
      ...tribe,
      isMember: userTribeIds.includes(tribe.id)
    }));
    setMoodTribes(updatedTribes);
    
    setShopItems(sampleShopItems);
    
    // Load sample mood wall
    const samplePosts = [
      {id: 1, text: "Today I finally managed to meditate for 10 minutes straight! Feeling proud of myself.", mood: "happy", likes: 12, timestamp: new Date(Date.now() - 2*60*60*1000).toISOString(), anonymous: true, userName: "Anonymous"},
      {id: 2, text: "Struggling with anxiety today. Anyone else feeling this way?", mood: "anxious", likes: 8, timestamp: new Date(Date.now() - 5*60*60*1000).toISOString(), anonymous: true, userName: "Anonymous"},
      {id: 3, text: "Grateful for this community. Reading your stories gives me hope.", mood: "grateful", likes: 25, timestamp: new Date(Date.now() - 8*60*60*1000).toISOString(), anonymous: false, userName: "HappyUser123"}
    ];
    setMoodWall(samplePosts);
    
    // Load sample journal entries
    const sampleEntries = [
      {id: 1, text: "Today was a good day. I felt productive and happy.", summary: "Positive day with productivity", mood: "happy", date: new Date().toISOString().split('T')[0], shared: false},
      {id: 2, text: "Feeling a bit anxious about the upcoming presentation. Need to practice more.", summary: "Anxious about presentation", mood: "anxious", date: "2023-10-24", shared: true},
      {id: 3, text: "Had a great conversation with my friend. It felt good to open up.", summary: "Positive social interaction", mood: "happy", date: "2023-10-23", shared: false}
    ];
    setJournalEntries(sampleEntries);
    
    // Initialize tribe chat messages
    const initialTribeChats = {};
    sampleMoodTribes.forEach(tribe => {
      initialTribeChats[tribe.id] = [
        {id: 1, message: "Hello everyone! How are you all feeling today?", user_name: "Anonymous", timestamp: new Date(Date.now() - 2*60*60*1000).toISOString()},
        {id: 2, message: "I've been practicing mindfulness and it's really helping with my anxiety ðŸ˜Š", user_name: "Anonymous", timestamp: new Date(Date.now() - 1*60*60*1000).toISOString()}
      ];
    });
    setTribeChatMessages(initialTribeChats);
  };

  // ... rest of your functions with API_BASE_URL updated

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoginError("");
    const formData = new FormData(e.target);
    const credentials = {
      email: formData.get('email'),
      password: formData.get('password')
    };

    try {
      const response = await fetch(`${API_BASE_URL}/api/login`, {
        method: 'POST',
        headers: getAuthHeaders(),
        body: JSON.stringify(credentials)
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('token', data.token);
        
        // Map backend user properties to frontend expected structure
        const userData = {
          id: data.user.id,
          email: data.user.email,
          name: data.user.name,
          coins: data.user.coins,
          streak: data.user.streak,
          mentalFitness: data.user.mentalFitness || 75
        };
        
        localStorage.setItem('user', JSON.stringify(userData));
        localStorage.setItem('lastStreakUpdate', new Date().toDateString());
        setUser(userData);
        setIsLoggedIn(true);
        setShowLogin(false);
        loadInitialData();
      } else {
        setLoginError(data.error || "Login failed");
      }
    } catch (error) {
      console.error("Login error:", error);
      // Fallback for demo - create a mock user
      const mockUser = {
        id: 1,
        email: credentials.email,
        name: "Demo User",
        coins: 100,
        streak: 1,
        mentalFitness: 85
      };
      localStorage.setItem('user', JSON.stringify(mockUser));
      localStorage.setItem('lastStreakUpdate', new Date().toDateString());
      setUser(mockUser);
      setIsLoggedIn(true);
      setShowLogin(false);
      loadInitialData();
    }
  };

  // Update all other API calls to use API_BASE_URL and getAuthHeaders()

  // ... rest of your component code

  return (
    <div className={`app-container ${currentTheme}-theme`}>
      {/* Your JSX remains the same */}
    </div>
  );
}

export default App;
