import React, { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";
import "./app.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLogin, setShowLogin] = useState(true);
  const [showRegister, setShowRegister] = useState(false);
  const [activeTab, setActiveTab] = useState("chat");
  const [therapists, setTherapists] = useState([]);
  const [resources, setResources] = useState([]);
  const [storeItems, setStoreItems] = useState([]);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [showCrisis, setShowCrisis] = useState(false);

  const chatEndRef = useRef(null);

  const API = "http://localhost:5000/api"; // CORS-safe

  useEffect(() => {
    const token = localStorage.getItem("token");
    const userData = localStorage.getItem("user");
    if (token && userData) {
      setUser(JSON.parse(userData));
      setIsLoggedIn(true);
      setShowLogin(false);
      loadInitialData();
    }
  }, []);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const loadInitialData = () => {
    setMessages([
      { text: "Hello! I'm MoodMate. How are you feeling today?", sender: "bot" },
    ]);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const credentials = { email: formData.get("email"), password: formData.get("password") };
    const res = await fetch(`${API}/login`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(credentials),
    });
    const data = await res.json();
    if (data.success) {
      localStorage.setItem("token", data.token);
      localStorage.setItem("user", JSON.stringify(data.user));
      setUser(data.user);
      setIsLoggedIn(true);
      setShowLogin(false);
      loadInitialData();
    } else alert(data.error);
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const userData = {
      name: formData.get("name"),
      email: formData.get("email"),
      password: formData.get("password"),
    };
    const res = await fetch(`${API}/register`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(userData),
    });
    const data = await res.json();
    if (data.success) {
      alert("Registered! Login now.");
      setShowRegister(false);
      setShowLogin(true);
    } else alert(data.error);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
    setIsLoggedIn(false);
    setShowLogin(true);
    setMessages([]);
  };

  const sendMessage = async () => {
    if (!input.trim() || !isLoggedIn) return;
    setMessages((prev) => [...prev, { text: input, sender: "user" }]);
    setInput("");
    setIsTyping(true);
    try {
      const res = await fetch(`${API}/chat`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: input }),
      });
      const data = await res.json();
      setMessages((prev) => [...prev, { text: data.response, sender: "bot" }]);
      if (data.mood === "crisis") setShowCrisis(true);
    } catch {
      setMessages((prev) => [...prev, { text: "Connection error.", sender: "bot" }]);
    }
    setIsTyping(false);
  };

  // Other functions (journal, store, therapy) remain the same
  // For brevity, you can keep your previous code

  return <div>{/* Your full JSX here â€“ same as your last App.jsx code */}</div>;
}

export default App;
