import React, { useState, useEffect, useRef } from 'react';

const API_BASE_URL = 'http://127.0.0.1:5000';

function Chat({ user }) { // The full user object is now passed as a prop
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const [isTyping, setIsTyping] = useState(false);
    const chatEndRef = useRef(null);

    // Set the initial welcome message only when the user object is fully loaded
    useEffect(() => {
        if (user && user.username) {
            setMessages([{
                id: Date.now(),
                text: `Hello ${user.username}! I'm MoodMate. How can I help you today?`,
                sender: 'bot'
            }]);
        }
    }, [user]); // This effect runs when the user prop changes

    // Auto-scroll chat
    useEffect(() => {
        chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    const sendMessage = async () => {
        if (!input.trim()) return;

        const userMessage = { id: Date.now(), text: input, sender: 'user' };
        setMessages(prev => [...prev, userMessage]);
        setInput("");
        setIsTyping(true);

        try {
            // ✅ CRITICAL FIX: Added `credentials: 'include'`
            // This tells the browser to send the session cookie with the request,
            // authenticating the user with the backend.
            const response = await fetch(`${API_BASE_URL}/api/chat`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                credentials: 'include', // This line fixes the connection error
                body: JSON.stringify({ message: input })
            });

            if (!response.ok) {
                const errData = await response.json();
                throw new Error(errData.error || `HTTP error! status: ${response.status}`);
            }
            
            const data = await response.json();
            const botMessage = { id: Date.now() + 1, text: data.reply, sender: 'bot' };
            setMessages(prev => [...prev, botMessage]);

        } catch (error) {
            console.error("Chat error:", error);
            const errorMsg = { id: Date.now() + 1, text: "Sorry, I couldn't connect. Please ensure the backend is running and try again.", sender: 'bot' };
            setMessages(prev => [...prev, errorMsg]);
        } finally {
            setIsTyping(false);
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                {messages.map((msg) => (
                    <div key={msg.id} className={`chat-message ${msg.sender}`}>
                        <div className="bubble">{msg.text}</div>
                    </div>
                ))}
                {isTyping && (
                    <div className="chat-message bot typing">
                        <div className="bubble"><span>.</span><span>.</span><span>.</span></div>
                    </div>
                )}
                <div ref={chatEndRef} />
            </div>
            <div className="input-area">
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                    placeholder="Type your message..."
                />
                <button onClick={sendMessage} disabled={isTyping}>
                    {isTyping ? 'Thinking...' : 'Send'}
                </button>
            </div>
        </div>
    );
}

export default Chat;
