import React, { useState } from "react";
import "./AppWrapper.css";

export default function AppWrapper({ onLoginSuccess }) {
  const [isLogin, setIsLogin] = useState(true);
  const [isForgot, setIsForgot] = useState(false);

  // Form state
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: "",
    loginId: "",
    otp: ""
  });

  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ✅ Signup request
  const handleSignup = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      setMessage("❌ Passwords do not match");
      return;
    }

    const res = await fetch("http://127.0.0.1:5000/signup", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    });

    const data = await res.json();
    setMessage(data.message);

    if (data.success) {
      setIsLogin(true);
    }
  };
  const API_URL = "http://127.0.0.1:5000";

  // ✅ Login request
  const handleLogin = async (e) => {
    e.preventDefault();
    const res = await fetch("http://127.0.0.1:5000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ loginId: formData.loginId, password: formData.password }),
    });

    const data = await res.json();
    setMessage(data.message);

    if (data.success) {
      onLoginSuccess(); // redirect to main App.jsx
    }
  };

  // ✅ Forgot Password → request OTP
  const handleForgot = async (e) => {
    e.preventDefault();
    const res = await fetch("http://127.0.0.1:5000/forgot", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ loginId: formData.loginId }),
    });

    const data = await res.json();
    setMessage(data.message);
  };

  // ✅ Reset with OTP
  const handleReset = async (e) => {
    e.preventDefault();
    const res = await fetch("http://127.0.0.1:5000/reset", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        loginId: formData.loginId,
        otp: formData.otp,
        newPassword: formData.password,
      }),
    });

    const data = await res.json();
    setMessage(data.message);

    if (data.success) {
      setIsForgot(false);
      setIsLogin(true);
    }
  };

  return (
    <div className="wrapper-container">
      <div className="auth-box">
        <h1>🌙 MoodMate</h1>
        <p>Your Mental Health Companion</p>

        {message && <div className="message">{message}</div>}

        {/* LOGIN */}
        {isLogin && !isForgot && (
          <form onSubmit={handleLogin} className="auth-form">
            <h2>Login</h2>
            <input
              type="text"
              name="loginId"
              placeholder="Email or Phone"
              value={formData.loginId}
              onChange={handleChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <button type="submit">Login</button>
            <p className="toggle" onClick={() => setIsForgot(true)}>
              Forgot Password?
            </p>
            <p className="toggle" onClick={() => setIsLogin(false)}>
              New here? Sign up
            </p>
          </form>
        )}

        {/* SIGNUP */}
        {!isLogin && (
          <form onSubmit={handleSignup} className="auth-form">
            <h2>Sign Up</h2>
            <input
              type="text"
              name="username"
              placeholder="Full Name"
              value={formData.username}
              onChange={handleChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <input
              type="tel"
              name="phone"
              placeholder="Phone Number"
              value={formData.phone}
              onChange={handleChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <input
              type="password"
              name="confirmPassword"
              placeholder="Confirm Password"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
            />
            <button type="submit">Sign Up</button>
            <p className="toggle" onClick={() => setIsLogin(true)}>
              Already have an account? Login
            </p>
          </form>
        )}

        {/* FORGOT PASSWORD */}
        {isForgot && (
          <form onSubmit={handleForgot} className="auth-form">
            <h2>Forgot Password</h2>
            <input
              type="text"
              name="loginId"
              placeholder="Enter Email or Phone"
              value={formData.loginId}
              onChange={handleChange}
              required
            />
            <button type="submit">Send OTP</button>
            <input
              type="text"
              name="otp"
              placeholder="Enter OTP"
              value={formData.otp}
              onChange={handleChange}
            />
            <input
              type="password"
              name="password"
              placeholder="New Password"
              value={formData.password}
              onChange={handleChange}
            />
            <button onClick={handleReset}>Reset Password</button>
            <p className="toggle" onClick={() => setIsForgot(false)}>
              Back to Login
            </p>
          </form>
        )}
      </div>
    </div>
  );
}
