// app.js
// MoodMate: React frontend for the mental wellness companion
// This file handles all UI, state management, and API interactions.
// It is a single-page application structure for a hackathon.

import React, { useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom/client';
import './app.css';

// Component for the main chat UI
const ChatMessage = ({ sender, text, mood, mood_suggestion, audio_url, isTyping }) => {
    const isUser = sender === 'user';
    const audioRef = useRef(null);

    // Auto-play audio when it's the AI's turn
    useEffect(() => {
        if (audioRef.current && audio_url) {
            audioRef.current.play().catch(e => console.error("Audio playback failed:", e));
        }
    }, [audio_url]);

    const renderText = () => {
        if (isTyping) {
            return (
                <div className="typing-animation-container">
                    <span className="dot dot1"></span>
                    <span className="dot dot2"></span>
                    <span className="dot dot3"></span>
                </div>
            );
        }
        return text;
    };

    return (
        <div className={`message-container ${isUser ? 'user-message-container' : 'ai-message-container'}`}>
            <div className={`chat-bubble ${isUser ? 'user-bubble' : 'ai-bubble'}`}>
                <p>{renderText()}</p>
                {mood_suggestion && (
                    <div className="mood-suggestion-box">
                        <p className="suggestion-title">‚ú® Mood Suggestion: {mood}</p>
                        <p className="suggestion-text">{mood_suggestion.text}</p>
                        {mood_suggestion.music && (
                            <a href={`https://www.youtube.com/results?search_query=${encodeURIComponent(mood_suggestion.music)}`} target="_blank" rel="noopener noreferrer" className="suggestion-link">
                                üé∂ Listen to Music
                            </a>
                        )}
                        {mood_suggestion.quote && (
                            <p className="suggestion-quote">‚Äú{mood_suggestion.quote}‚Äù</p>
                        )}
                    </div>
                )}
            </div>
            {audio_url && !isUser && <audio ref={audioRef} src={`http://localhost:5000/${audio_url}`} />}
        </div>
    );
};

// Component for the Journaling UI
const JournalingTool = ({ onSave, journalEntries, onClose }) => {
    const [entryText, setEntryText] = useState('');
    const [selectedMood, setSelectedMood] = useState('');
    const moods = ['happy', 'sad', 'anxious', 'stressed', 'motivated', 'calm'];

    const handleSave = () => {
        if (entryText.trim()) {
            onSave({ text: entryText, mood: selectedMood });
            setEntryText('');
            setSelectedMood('');
            onClose();
        }
    };

    return (
        <div className="journal-overlay">
            <div className="journal-modal glassmorphism-modal">
                <button className="close-button" onClick={onClose}>&times;</button>
                <h2>Your Digital Journal</h2>
                <p>Jot down your thoughts and feelings securely.</p>
                <textarea
                    className="journal-textarea"
                    value={entryText}
                    onChange={(e) => setEntryText(e.target.value)}
                    placeholder="What's on your mind today?"
                ></textarea>
                <div className="mood-selection">
                    <p>How are you feeling?</p>
                    <div className="mood-buttons">
                        {moods.map(mood => (
                            <button
                                key={mood}
                                className={`mood-button ${selectedMood === mood ? 'selected' : ''}`}
                                onClick={() => setSelectedMood(mood)}
                            >
                                {mood.charAt(0).toUpperCase() + mood.slice(1)}
                            </button>
                        ))}
                    </div>
                </div>
                <button className="journal-save-button" onClick={handleSave}>Save Entry</button>
                <div className="past-entries">
                    <h3>Past Entries</h3>
                    {journalEntries.length > 0 ? (
                        <div className="entries-list">
                            {journalEntries.slice(0).reverse().map((entry, index) => (
                                <div key={index} className="entry-item glass-card">
                                    <p className="entry-date">{new Date(entry.timestamp).toLocaleDateString()}</p>
                                    <p className="entry-text">{entry.text.substring(0, 100)}...</p>
                                    <span className="entry-mood-tag">{entry.mood}</span>
                                </div>
                            ))}
                        </div>
                    ) : (
                        <p className="no-entries">You haven't written any entries yet.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

// Component for the Community Wall
const CommunityWall = ({ posts, onPost, onClose }) => {
    const [postText, setPostText] = useState('');

    const handlePost = () => {
        if (postText.trim()) {
            onPost(postText);
            setPostText('');
        }
    };

    return (
        <div className="community-overlay">
            <div className="community-modal glassmorphism-modal">
                <button className="close-button" onClick={onClose}>&times;</button>
                <h2>Mood Tribes</h2>
                <p>Share your anonymous thoughts and feelings with the community.</p>
                <div className="post-input-container">
                    <textarea
                        value={postText}
                        onChange={(e) => setPostText(e.target.value)}
                        placeholder="What's on your mind? (Your post is anonymous)"
                        className="post-textarea"
                    ></textarea>
                    <button className="post-button" onClick={handlePost}>Post Anonymously</button>
                </div>
                <div className="community-posts-list">
                    {posts.length > 0 ? (
                        posts.slice(0).reverse().map((post, index) => (
                            <div key={index} className="community-post glass-card">
                                <p className="post-text">{post.text}</p>
                                <span className="post-timestamp">~ {new Date(post.timestamp).toLocaleString()}</span>
                            </div>
                        ))
                    ) : (
                        <p className="no-posts">Be the first to post!</p>
                    )}
                </div>
            </div>
        </div>
    );
};

// Component for a generic Modal
const Modal = ({ show, onClose, children }) => {
    if (!show) {
        return null;
    }
    return (
        <div className="modal-backdrop" onClick={onClose}>
            <div className="modal-content" onClick={e => e.stopPropagation()}>
                <button className="modal-close-button" onClick={onClose}>&times;</button>
                {children}
            </div>
        </div>
    );
};

// Component for the Reward Pop-up
const RewardPopup = ({ reward, onClose }) => {
    const renderRewardContent = () => {
        if (!reward) return null;
        switch (reward.type) {
            case 'streak_milestone':
                return (
                    <div className="reward-content">
                        <h3 className="reward-title">üéâ Streak Milestone! üéâ</h3>
                        <p>You've reached a {reward.value}-day streak!</p>
                        <p className="reward-info">You earned {reward.value * 10} Mood Coins!</p>
                    </div>
                );
            case 'badge_unlock':
                return (
                    <div className="reward-content">
                        <h3 className="reward-title">üèÖ New Badge Unlocked! üèÖ</h3>
                        <p>You earned the "{reward.badge}" badge!</p>
                    </div>
                );
            case 'challenge_complete':
                return (
                    <div className="reward-content">
                        <h3 className="reward-title">üöÄ Challenge Completed! üöÄ</h3>
                        <p>You completed the challenge and earned the "{reward.badge}" badge!</p>
                    </div>
                );
            default:
                return null;
        }
    };
    return (
        <Modal show={true} onClose={onClose}>
            <div className="reward-modal-body">
                {renderRewardContent()}
                <p className="reward-cta">Keep going! You're doing great!</p>
            </div>
        </Modal>
    );
};

// Main App Component
const App = () => {
    // State management for the entire application
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [isTyping, setIsTyping] = useState(false);
    const [moodCoins, setMoodCoins] = useState(0);
    const [streak, setStreak] = useState(0);
    const [badges, setBadges] = useState([]);
    const [chatId, setChatId] = useState(null);
    const [isJournalOpen, setIsJournalOpen] = useState(false);
    const [journalEntries, setJournalEntries] = useState([]);
    const [isCommunityOpen, setIsCommunityOpen] = useState(false);
    const [communityPosts, setCommunityPosts] = useState([]);
    const [isHistoryOpen, setIsHistoryOpen] = useState(false);
    const [chatHistorySummaries, setChatHistorySummaries] = useState([]);
    const [currentTheme, setCurrentTheme] = useState('default');
    const [showRewardPopup, setShowRewardPopup] = useState(false);
    const [rewardPayload, setRewardPayload] = useState(null);
    const messagesEndRef = useRef(null);

    // Fetch user state and start a new chat on initial load
    useEffect(() => {
        const fetchInitialData = async () => {
            try {
                // Fetch user state (streaks, coins, etc.)
                const stateResponse = await fetch('http://localhost:5000/api/user/state');
                const stateData = await stateResponse.json();
                if (stateData.user) {
                    setMoodCoins(stateData.user.mood_coins);
                    setStreak(stateData.user.streaks);
                    setBadges(stateData.user.badges);
                }
                if (stateData.reward_event) {
                    setRewardPayload(stateData.reward_event);
                    setShowRewardPopup(true);
                }

                // Create a new chat session
                const chatResponse = await fetch('http://localhost:5000/api/chat/new');
                const chatData = await chatResponse.json();
                if (chatData.chat_id) {
                    setChatId(chatData.chat_id);
                    // Add initial AI greeting message
                    setMessages([{
                        sender: 'ai',
                        text: "Hey there! I'm MoodMate. How are you feeling today? You can just type, or use the Journal and Community Wall to express yourself. ‚ú®",
                        audio_url: null,
                        mood: 'welcoming',
                        mood_suggestion: null
                    }]);
                }
            } catch (error) {
                console.error("Failed to fetch initial data:", error);
                setMessages([{ sender: 'ai', text: "Sorry, I'm having trouble connecting to my brain. Please check your network or try again.", mood: 'error' }]);
            }
        };
        fetchInitialData();
        fetchJournalEntries();
        fetchCommunityPosts();
    }, []);

    // Scroll to the latest message
    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    // Handles sending a new message to the backend
    const sendMessage = async (e) => {
        e.preventDefault();
        if (!input.trim() || isTyping) return;

        const userMessage = input.trim();
        const newMessage = { sender: 'user', text: userMessage };
        setMessages(prev => [...prev, newMessage]);
        setInput('');
        setIsTyping(true);

        try {
            const response = await fetch('http://localhost:5000/api/chat/message', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ message: userMessage, chat_id: chatId }),
            });
            const data = await response.json();

            // Handle errors
            if (data.error) {
                console.error("Backend error:", data.error);
                setMessages(prev => [...prev, { sender: 'ai', text: "Oops! Something went wrong. Let's try that again.", mood: 'error' }]);
            } else {
                // Realistic typing animation
                setTimeout(() => {
                    setMessages(prev => [...prev, {
                        sender: 'ai',
                        text: data.response,
                        mood: data.mood,
                        mood_suggestion: data.mood_suggestion,
                        audio_url: data.audio_url
                    }]);
                    setMoodCoins(data.mood_coins);
                    if (data.reward_event) {
                        setRewardPayload(data.reward_event);
                        setShowRewardPopup(true);
                    }
                }, Math.min(data.response.length * 50, 2000)); // Simulate typing speed
            }
        } catch (error) {
            console.error("Network or fetch error:", error);
            setMessages(prev => [...prev, { sender: 'ai', text: "Hmm, it seems I'm having trouble. Can you check your connection?", mood: 'error' }]);
        } finally {
            setIsTyping(false);
        }
    };

    // Fetches journal entries
    const fetchJournalEntries = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/journal/entries');
            const data = await response.json();
            if (data.entries) {
                setJournalEntries(data.entries);
            }
        } catch (error) {
            console.error("Failed to fetch journal entries:", error);
        }
    };

    // Saves a journal entry
    const saveJournalEntry = async (entry) => {
        try {
            const response = await fetch('http://localhost:5000/api/journal/entry', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(entry),
            });
            const data = await response.json();
            if (data.message) {
                console.log(data.message);
                setMoodCoins(data.mood_coins);
                await fetchJournalEntries();
                if (data.reward_event) {
                    setRewardPayload(data.reward_event);
                    setShowRewardPopup(true);
                }
            }
        } catch (error) {
            console.error("Failed to save journal entry:", error);
        }
    };

    // Fetches community wall posts
    const fetchCommunityPosts = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/community/posts');
            const data = await response.json();
            if (data.posts) {
                setCommunityPosts(data.posts);
            }
        } catch (error) {
            console.error("Failed to fetch community posts:", error);
        }
    };

    // Adds a community post
    const addCommunityPost = async (postText) => {
        try {
            const response = await fetch('http://localhost:5000/api/community/post', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ text: postText }),
            });
            const data = await response.json();
            if (data.message) {
                console.log(data.message);
                setMoodCoins(data.mood_coins);
                await fetchCommunityPosts();
                if (data.reward_event) {
                    setRewardPayload(data.reward_event);
                    setShowRewardPopup(true);
                }
            }
        } catch (error) {
            console.error("Failed to add community post:", error);
        }
    };

    // Fetches chat history summaries
    const fetchChatHistorySummaries = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/chat/history');
            const data = await response.json();
            setChatHistorySummaries(data.history || []);
        } catch (error) {
            console.error("Failed to fetch chat history:", error);
        }
    };

    // Loads a specific chat history
    const loadChatHistory = async (id) => {
        try {
            const response = await fetch(`http://localhost:5000/api/chat/history/${id}`);
            const data = await response.json();
            if (data.messages) {
                setMessages(data.messages);
                setChatId(id);
                setIsHistoryOpen(false);
            }
        } catch (error) {
            console.error("Failed to load chat history:", error);
        }
    };

    const toggleJournal = () => {
        setIsJournalOpen(prev => !prev);
        fetchJournalEntries();
    };

    const toggleCommunity = () => {
        setIsCommunityOpen(prev => !prev);
        fetchCommunityPosts();
    };

    const toggleHistory = () => {
        setIsHistoryOpen(prev => !prev);
        fetchChatHistorySummaries();
    };

    // Main App UI structure
    return (
        <div className={`app-container ${currentTheme}`}>
            {showRewardPopup && <RewardPopup reward={rewardPayload} onClose={() => setShowRewardPopup(false)} />}
            
            {/* Top Navbar */}
            <nav className="top-nav glassmorphism-nav">
                <div className="nav-left">
                    <span className="logo">MoodMate ‚ú®</span>
                </div>
                <div className="nav-center">
                    <span className="nav-item-icon" onClick={toggleJournal}><i className="fas fa-book"></i> Journal</span>
                    <span className="nav-item-icon" onClick={toggleCommunity}><i className="fas fa-users"></i> Mood Tribes</span>
                    <span className="nav-item-icon" onClick={toggleHistory}><i className="fas fa-history"></i> History</span>
                    <div className="streak-display">
                        <i className="fas fa-fire streak-icon"></i>
                        <span className="streak-count">{streak} Day Streak</span>
                    </div>
                </div>
                <div className="nav-right">
                    <div className="coins-display">
                        <i className="fas fa-coins coin-icon"></i>
                        <span className="coin-count">{moodCoins}</span>
                    </div>
                </div>
            </nav>

            {/* Main Chat Area */}
            <main className="chat-main-area">
                <div className="chat-container">
                    {messages.map((msg, index) => (
                        <ChatMessage key={index} {...msg} isTyping={isTyping && index === messages.length - 1 && msg.sender === 'ai'} />
                    ))}
                    <div ref={messagesEndRef} />
                </div>
            </main>

            {/* Fixed Chat Input Bar */}
            <div className="chat-input-bar">
                <form onSubmit={sendMessage} className="chat-form">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        placeholder="Say hello to your MoodMate..."
                        className="chat-input-field"
                        disabled={isTyping}
                    />
                    <button type="submit" className="send-button" disabled={isTyping}>
                        <i className="fas fa-paper-plane"></i>
                    </button>
                </form>
            </div>
            
            {/* Modals and Overlays */}
            {isJournalOpen && <JournalingTool onSave={saveJournalEntry} journalEntries={journalEntries} onClose={toggleJournal} />}
            {isCommunityOpen && <CommunityWall posts={communityPosts} onPost={addCommunityPost} onClose={toggleCommunity} />}
            {isHistoryOpen && (
                <div className="history-overlay">
                    <div className="history-modal glassmorphism-modal">
                        <button className="close-button" onClick={toggleHistory}>&times;</button>
                        <h2>Chat History</h2>
                        <div className="history-list">
                            {chatHistorySummaries.length > 0 ? (
                                chatHistorySummaries.map(chat => (
                                    <div key={chat.id} className="history-item glass-card" onClick={() => loadChatHistory(chat.id)}>
                                        <p className="history-summary">{chat.summary}</p>
                                        <p className="history-timestamp">{new Date(chat.timestamp).toLocaleString()}</p>
                                    </div>
                                ))
                            ) : (
                                <p className="no-history">No past conversations found.</p>
                            )}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

