import React, { useState, useEffect, useRef } from "react";
import "./app.css";

// Base URL for API calls
const API_BASE = "http://localhost:5000";

 

 
const AppContext = createContext();

const useAppContext = () => {
    const context = useContext(AppContext);
    if (!context) {
        throw new Error('useAppContext must be used within AppProvider');
    }
    return context;
};

// ================================================================
// CUSTOM HOOKS
// ================================================================

const useAPI = () => {
    const makeRequest = useCallback(async (endpoint, options = {}) => {
        try {
            const response = await fetch(`/api${endpoint}`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include',
                ...options
            });
            
            const data = await response.json();
            
            if (!response.ok) {
                throw new Error(data.error || `HTTP ${response.status}`);
            }
            
            return data;
        } catch (error) {
            console.error('API Error:', error);
            throw error;
        }
    }, []);
    
    return { makeRequest };
};

const useLocalStorage = (key, defaultValue) => {
    const [value, setValue] = useState(() => {
        try {
            const item = window.localStorage.getItem(key);
            return item ? JSON.parse(item) : defaultValue;
        } catch (error) {
            return defaultValue;
        }
    });
    
    const setStoredValue = useCallback((newValue) => {
        try {
            setValue(newValue);
            window.localStorage.setItem(key, JSON.stringify(newValue));
        } catch (error) {
            console.error('LocalStorage error:', error);
        }
    }, [key]);
    
    return [value, setStoredValue];
};

const useTypingEffect = (text, speed = 50) => {
    const [displayedText, setDisplayedText] = useState('');
    const [isTyping, setIsTyping] = useState(false);
    
    useEffect(() => {
        if (!text) {
            setDisplayedText('');
            return;
        }
        
        setIsTyping(true);
        setDisplayedText('');
        
        let index = 0;
        const timer = setInterval(() => {
            if (index < text.length) {
                setDisplayedText(text.slice(0, index + 1));
                index++;
            } else {
                setIsTyping(false);
                clearInterval(timer);
            }
        }, speed);
        
        return () => {
            clearInterval(timer);
            setIsTyping(false);
        };
    }, [text, speed]);
    
    return { displayedText, isTyping };
};

const useAutoScroll = (dependency) => {
    const scrollRef = useRef(null);
    
    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
        }
    }, [dependency]);
    
    return scrollRef;
};

// ================================================================
// NOTIFICATION SYSTEM
// ================================================================

const NotificationSystem = ({ notifications, onRemove }) => {
    if (!notifications.length) return null;
    
    return (
        <div className="notification-container">
            {notifications.map(notification => (
                <div 
                    key={notification.id} 
                    className={`notification ${notification.type}`}
                    onClick={() => onRemove(notification.id)}
                >
                    <div className="notification-content">
                        {notification.icon && <span className="notification-icon">{notification.icon}</span>}
                        <div>
                            <div className="notification-title">{notification.title}</div>
                            <div className="notification-message">{notification.message}</div>
                        </div>
                    </div>
                    <button className="notification-close">Ã—</button>
                </div>
            ))}
        </div>
    );
};

// ================================================================
// AUTHENTICATION COMPONENTS
// ================================================================

const LoginForm = ({ onLogin, onSwitchToRegister }) => {
    const [formData, setFormData] = useState({ username: '', password: '' });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const { makeRequest } = useAPI();
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        
        try {
            const response = await makeRequest('/auth/login', {
                method: 'POST',
                body: JSON.stringify(formData)
            });
            
            onLogin(response.user);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };
    
    return (
        <div className="auth-form">
            <div className="auth-header">
                <h2>Welcome Back to MoodMate</h2>
                <p>Sign in to continue your mental wellness journey</p>
            </div>
            
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Username or Email</label>
                    <input
                        type="text"
                        value={formData.username}
                        onChange={(e) => setFormData({...formData, username: e.target.value})}
                        placeholder="Enter your username or email"
                        required
                    />
                </div>
                
                <div className="form-group">
                    <label>Password</label>
                    <input
                        type="password"
                        value={formData.password}
                        onChange={(e) => setFormData({...formData, password: e.target.value})}
                        placeholder="Enter your password"
                        required
                    />
                </div>
                
                {error && <div className="error-message">{error}</div>}
                
                <button type="submit" className="btn btn-primary" disabled={loading}>
                    {loading ? (
                        <span className="loading-spinner">
                            <span className="spinner"></span>
                            Signing in...
                        </span>
                    ) : (
                        'Sign In'
                    )}
                </button>
                
                <div className="auth-switch">
                    <p>Don't have an account? 
                        <button type="button" onClick={onSwitchToRegister} className="link-button">
                            Create one here
                        </button>
                    </p>
                </div>
            </form>
        </div>
    );
};

const RegisterForm = ({ onRegister, onSwitchToLogin }) => {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        confirmPassword: '',
        display_name: '',
        email: ''
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const { makeRequest } = useAPI();
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        
        if (formData.password !== formData.confirmPassword) {
            setError('Passwords do not match');
            setLoading(false);
            return;
        }
        
        try {
            const response = await makeRequest('/auth/register', {
                method: 'POST',
                body: JSON.stringify({
                    username: formData.username,
                    password: formData.password,
                    display_name: formData.display_name,
                    email: formData.email || undefined
                })
            });
            
            onRegister(response.user);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };
    
    return (
        <div className="auth-form">
            <div className="auth-header">
                <h2>Join MoodMate</h2>
                <p>Start your journey to better mental wellness</p>
            </div>
            
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Display Name</label>
                    <input
                        type="text"
                        value={formData.display_name}
                        onChange={(e) => setFormData({...formData, display_name: e.target.value})}
                        placeholder="Your display name"
                        required
                    />
                </div>
                
                <div className="form-group">
                    <label>Username</label>
                    <input
                        type="text"
                        value={formData.username}
                        onChange={(e) => setFormData({...formData, username: e.target.value})}
                        placeholder="Choose a username"
                        required
                    />
                </div>
                
                <div className="form-group">
                    <label>Email (Optional)</label>
                    <input
                        type="email"
                        value={formData.email}
                        onChange={(e) => setFormData({...formData, email: e.target.value})}
                        placeholder="your.email@example.com"
                    />
                </div>
                
                <div className="form-group">
                    <label>Password</label>
                    <input
                        type="password"
                        value={formData.password}
