        !isUser ? message.content : '', 
        30
    );
    
    const content = isUser ? message.content : displayedText;
    
    return (
        <div className={`chat-message ${isUser ? 'user' : 'assistant'}`}>
            <div className="message-avatar">
                {isUser ? (
                    <div className="user-avatar">ðŸ‘¤</div>
                ) : (
                    <div className="ai-avatar">ðŸ¤–</div>
                )}
            </div>
            
            <div className="message-content">
                <div className="message-bubble">
                    <div className="message-text">
                        {content}
                        {!isUser && isTyping && <span className="typing-cursor">|</span>}
                    </div>
                    
                    {message.detected_mood && (
                        <div className="mood-indicator">
                            <span className="mood-emoji">
                                {message.detected_mood === 'happy' ? 'ðŸ˜Š' :
                                 message.detected_mood === 'sad' ? 'ðŸ˜¢' :
                                 message.detected_mood === 'anxious' ? 'ðŸ˜°' :
                                 message.detected_mood === 'angry' ? 'ðŸ˜ ' :
                                 message.detected_mood === 'calm' ? 'ðŸ˜Œ' :
                                 message.detected_mood === 'excited' ? 'ðŸ¤©' :
                                 message.detected_mood === 'lonely' ? 'ðŸ˜”' : 'ðŸ˜'}
                            </span>
                            <span className="mood-text">Detected: {message.detected_mood}</span>
